INSERT INTO `news` (`news_id`, `news_name`, `news_des`, `news_content`, `news_img`, `news_views`, `newscat_id`, `news_author`, `news_created_date`, `news_update_date`, `state`, `news_sub_info1`, `news_sub_info2`, `news_sub_info3`, `news_sub_info4`, `news_sub_info5`, `title_seo`, `des_seo`, `friendly_url`, `keyword`, `created_id`) VALUES
(81, 'Dịch vụ rà soát sổ sách chất lượng và uy tín nhất hiện nay', 'Có phải bạn đang tìm một công ty uy tín để thuê dịch vụ rà soát sổ sách chất lượng?  Chúng tôi sẽ cug cấp cho các bạn tại bài viết này.', '<p dir=\"ltr\" style=\"text-align:justify\"><a href=\"http://baocaothueonline.vn/dich-vu-ra-soat-so-sach-chat-luong-va-uy-tin-nhat-hien-nay\"><strong>Dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch chất lượng</strong></a> hiện nay cũng rất phổ biến. Nhưng t&igrave;m đưuọc một nơi uy t&iacute;n để trao đi sự tin tưởng của doanh nghiệp th&igrave; rất hiếm. Do vậy, b&agrave;i viết n&agrave;y ch&uacute;ng t&ocirc;i sẽ đưa ra những th&ocirc;ng tin về dịch vụ n&agrave;y tại một địa chỉ tin cậy.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch chất lượng hiện nay tr&ecirc;n thị trường</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"dịch vụ rà soát sổ sách chất lượng\" height=\"400\" src=\"/image/images/dich-vu-ra-soat-so-sach-chat-luong.jpg\" width=\"600\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">Dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch chất lượng</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Dịch vụ n&agrave;y thường l&agrave; nhu cầu của c&aacute;c doanh nghiệp vừa v&agrave; nhỏ. Tại sao lại như vậy?Bởi v&igrave; một số c&ocirc;ng ty, doanh nghiệp kh&ocirc;ng đủ khả năng chi trả cho nh&acirc;n vi&ecirc;n kế to&aacute;n l&agrave;m việc h&agrave;ng th&aacute;ng. Trong khi khối lượng c&ocirc;ng việc kh&ocirc;ng nhiều ( đối với khối doanh nghiệp vừa v&agrave; nhỏ), chỉ tập trung c&ocirc;ng việ nhiều nhất ở những ng&agrave;y cuối th&aacute;ng cuối năm. Do vậy, việc thu&ecirc; dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch chất lượng l&agrave; một việc l&agrave;m v&ocirc; c&ugrave;ng tiện lợi v&agrave; mang lại hiệu quả tối ưu nhất cho doanh nghiệp. N&oacute; kh&ocirc;ng tốn qu&aacute; nhiều chi ph&iacute;, đồng thời, hiệu suất c&ocirc;ng việc cao. Tuy nhi&ecirc;n, điều đ&oacute; xảy ra như thế n&agrave;o th&igrave; c&ograve;n phụ thuộc v&agrave;o c&ocirc;ng ty dịch vụ m&agrave; doanh nghiệp thu&ecirc;. N&oacute; c&oacute; thật sự chất lượng v&agrave; uy t&iacute;n hay kh&ocirc;ng?</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>C&ocirc;ng ty Nguy&ecirc;n Anh</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"dịch vụ rà soát sổ sách chất lượng\" height=\"327\" src=\"/image/images/dich-vu-ra-soat-so-sach-chat-luong-tai-cong-ty-nguyen-anh.jpg\" width=\"600\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">Dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch chất lượng tại c&ocirc;ng ty Nguy&ecirc;n Anh</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">C&oacute; nhiều điểm m&agrave; c&aacute;c bạn cần phải tin tưởng khi giao cho <a href=\"http://baocaothueonline.vn/ra-soat-so-sach\"><strong>dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch chất lượng</strong></a> tại c&ocirc;ng ty Nguy&ecirc;n Anh.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Thứ nhất, đ&acirc;y l&agrave; một c&ocirc;ng ty chuy&ecirc;n hoạt động b&ecirc;n lĩnh vực kế to&aacute;n- thuế. Bao giờ chuy&ecirc;n m&ocirc;n của họ th&igrave; họ l&agrave;m tốt hơn những ng&agrave;nh chỉ li&ecirc;n quan. C&ugrave;ng với đ&oacute;, c&ocirc;ng ty đ&agrave;o tạo một đội ngũ nh&acirc;n vi&ecirc;n nhiều kinh nghiệm với kiến thức chuy&ecirc;n m&ocirc;n cao v&agrave; đầy l&ograve;ng nhiệt huyết với nghề. Do vậy, những c&ocirc;ng việc m&agrave; doanh nghiệp y&ecirc;u cầu chắc chắn sẽ kh&ocirc;ng l&agrave;m kh&oacute; dễ c&aacute;c nh&acirc;n vi&ecirc;n của c&ocirc;ng ty.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Thứ hai, c&ocirc;ng ty lu&ocirc;n hướng đến lợi &iacute;ch cao nhất của kh&aacute;ch h&agrave;ng. Xem lợi &iacute;ch của&nbsp;kh&aacute;ch h&agrave;ng l&agrave; mục ti&ecirc;u của hoạt động v&agrave; ph&aacute;t triển.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Trong những ng&agrave;y cuối th&aacute;ng hay cuối năm nếu cần dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch chất lượng th&igrave; h&atilde;y truy cập ngay v&agrave;o <a href=\"http://baocaothueonline.vn\"><strong>http://baocaothueonline.vn</strong></a> để được tư vấn v&agrave; gi&uacute;p đỡ.</p>\r\n', 'dich-vu-ra-soat-so-sach-chat-luong-tai-cong-ty-nguyen-anh.jpg', 0, 67, '', '2018-01-31 16:01:08', '2018-03-27 15:03:57', 1, '', '', '', '', '', 'Dịch vụ rà soát sổ sách chất lượng và uy tín nhất hiện nay', 'Có phải bạn đang tìm một công ty uy tín để thuê dịch vụ rà soát sổ sách chất lượng?  Chúng tôi sẽ cug cấp cho các bạn tại bài viết này.', 'dich-vu-ra-soat-so-sach-chat-luong-va-uy-tin-nhat-hien-nay', 'Dịch vụ rà soát sổ sách chất lượng', 1),
(82, 'Địa chỉ cung cấp dịch vụ rà soát sổ sách uy tín nhất hiện nay', 'Những ngày cuối tháng hay cuối năm thì những công việc liên quan đến sổ sách, thu chi của các công ty, doanh nghiệp đang trở nên cực kỳ căng thẳng. Cho nên tìm ra một địa chỉ cung cấp dịch vụ rà soát sổ sách uy tín là cực kỳ khó khăn.', '<p dir=\"ltr\" style=\"text-align:justify\"><a href=\"http://baocaothueonline.vn/dia-chi-cung-cap-dich-vu-ra-soat-so-sach-uy-tin-nhat-hien-nay\"><strong>Cung cấp dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch</strong></a> hiện nay l&agrave; kh&ocirc;ng hiếm. Nhiều c&ocirc;ng ty kế to&aacute;n- kiểm to&aacute;n mở ra nhằm mục đ&iacute;ch đ&aacute;p ứng nhu cầu của thị trường hiện nay. Nhưng t&igrave;m một c&ocirc;ng ty uy t&iacute;n th&igrave; rất kh&oacute;. Ch&uacute;ng t&ocirc;i sẽ đem đến cho c&aacute;c bạn một địa chỉ chất lượng với uy t&iacute;n cao.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Những th&ocirc;ng tin về c&ocirc;ng ty Nguy&ecirc;n Anh</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Cung cấp dịch vụ rà soát sổ sách\" height=\"418\" src=\"/image/images/dich-vu-ra-soat-so-sach%20(1)(1).jpg\" width=\"600\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">C&ocirc;ng ty Nguy&ecirc;nAnh với chất lượng dịch vụ tốt nhất</p>\r\n\r\n<h3 dir=\"ltr\" style=\"text-align:justify\"><em><strong>Lĩnh vực hoạt động</strong></em></h3>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Lĩnh vực của c&ocirc;ng ty Nguy&ecirc;n Anh ch&iacute;nh l&agrave; tư vấn kế to&aacute;n- thuế. B&ecirc;n c&ocirc;ng ty chuy&ecirc;n b&ecirc;n lĩnh vực n&agrave;y n&ecirc;n đội ngũ nh&acirc;n vi&ecirc;n c&oacute; tr&igrave;nh độ chuy&ecirc;n m&ocirc;n rất cao cũng như c&oacute; bề d&agrave;y kinh nghiệm hơn hẳn những nơi kh&aacute;c. Những nh&acirc;n vi&ecirc;n n&agrave;y t&acirc;m huyết với nghề n&ecirc;n chắc chắn sẽ đem đến sự h&agrave;i l&ograve;ng tuyệt đối cho c&aacute;c doanh nghiệp đối t&aacute;c. Đồng thời cũng sẽ mang đến những giải ph&aacute;p tối ưu về thuế, kiểm so&aacute;t rủi ro thuế cho c&aacute;c doanh nghiệp vừa v&agrave; nhỏ bằng kiến thức, kinh nghiệm v&agrave; l&ograve;ng nhiệt huyết của m&igrave;nh. Chắc chắn sẽ l&agrave;m cho c&aacute;c doanh nghiệp thực sự h&agrave;i l&ograve;ng.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\"><em><strong>Sứ mệnh c&ocirc;ng ty</strong></em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Cung cấp dịch vụ rà soát sổ sách\" height=\"400\" src=\"/image/images/dich-vu-ra-soat-so-sach%20(2)(1).jpg\" width=\"600\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">Cung cấp dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Sứ mệnh cao cả của c&ocirc;ng ty Nguy&ecirc;n Anh l&agrave; mang đến cho c&aacute;c doanh nghiệp đối t&aacute;c sự tối ưu h&oacute;a số thuế phải nộp. &nbsp;B&ecirc;n cạnh đ&oacute; c&ograve;n giẩm thiểu những rủi ro đến mức thấp nhất c&oacute; thể. Tất cả đều hướng đến lợi &iacute;ch của c&aacute;c doanh nghiệp kh&aacute;ch h&agrave;ng khi đến với c&ocirc;ng ty để t&igrave;m dịch vụ <a href=\"http://baocaothueonline.vn/ra-soat-so-sach\"><strong>cung cấp dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch</strong></a>.</p>\r\n\r\n<h3 dir=\"ltr\" style=\"text-align:justify\"><em><strong>Tầm nh&igrave;n hoạt động</strong></em></h3>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">C&oacute; hai mục ti&ecirc;u m&agrave; c&ocirc;ng ty Nguy&ecirc;n Anh muốn hướng đến v&agrave; l&agrave; hai chiến lược m&agrave; c&ocirc;ng ty lấy l&agrave;m kim chỉ nam cho mọi hoạt động.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Thứ nhất, x&acirc;y dựng hệ thống kế to&aacute;n thuế vững mạnh.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Thứ hai, trở th&agrave;nh mắt x&iacute;ch gi&uacute;p doanh nghiệp kinh doanh hiệu quả.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Đến với c&ocirc;ng ty Nguy&ecirc;n Anh th&igrave; việc cung cấp dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch sẽ trở n&ecirc;n đơn giản hơn bao giờ hết. Nhanh ch&oacute;ng truy cập v&agrave;o&nbsp;<a href=\"http://baocaothueonline.vn\"><strong>http://baocaothueonline.vn</strong></a> để c&ugrave;ng t&igrave;m hiểu n&agrave;o c&aacute;c bạn.</p>\r\n', 'dich-vu-ra-soat-so-sach (1).jpg', 0, 60, '', '2018-01-31 17:01:51', '2018-01-31 17:01:14', 1, '', '', '', '', '', 'Địa chỉ cung cấp dịch vụ rà soát sổ sách uy tín nhất hiện nay', 'Những ngày cuối tháng hay cuối năm thì những công việc liên quan đến sổ sách, thu chi của các công ty, doanh nghiệp đang trở nên cực kỳ căng thẳng. Cho nên tìm ra một địa chỉ cung cấp dịch vụ rà soát sổ sách uy tín là cực kỳ khó khăn.', 'dia-chi-cung-cap-dich-vu-ra-soat-so-sach-uy-tin-nhat-hien-nay', 'Cung cấp dịch vụ rà soát sổ sách', 1),
(83, 'Sự cần thiết của dịch vụ rà soát sổ sách kế toán', 'Để có được sổ sách kế toán rõ ràng thì sự cần thiết của dịch vụ rà soát sổ sách kế toán là vô cùng quan trọng', '<p dir=\"ltr\" style=\"text-align:justify\">Sổ s&aacute;ch kế to&aacute;n cuối năm l&agrave; một việc v&ocirc; c&ugrave;ng quan trọng v&agrave; phức tạp. Tuy nhi&ecirc;n việc r&agrave; so&aacute;t, sắp xếp sổ s&aacute;ch kế to&aacute;n cần phải c&oacute; được những kiến thức, kĩ năng ch&iacute;nh x&aacute;c để cho ra kết quả đ&uacute;ng nhất. Trong b&agrave;i viết ng&agrave;y h&ocirc;m nay, Nguy&ecirc;n Anh Tax sẽ gi&uacute;p bạn thấu hiểu được sự cần thiết của dịch vụ <a href=\"http://baocaothueonline.vn/su-can-thiet-cua-dich-vu-ra-soat-so-sach-ke-toan\"><strong>r&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n</strong></a>.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Thực trạng r&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n cuối năm tại c&aacute;c c&ocirc;ng ty:</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"rà soát sổ sách kế toán\" height=\"301\" src=\"/image/images/dich-vu-ra-soat-so-sach-ke-toan.jpg\" width=\"600\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Theo nghi&ecirc;n cứu thực tế, ch&uacute;ng ta c&oacute; thể thấy rằng c&aacute;c c&ocirc;ng ty, doanh nghiệp hiện nay đang gặp phải kh&oacute; khăn lớn đặc biệt dẫn đến c&aacute;c sai phạm kh&ocirc;ng đ&aacute;ng c&oacute; khi r&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n. Cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Hệ thống sổ s&aacute;ch chứng từ mất m&aacute;t, sắp xếp một c&aacute;ch lộn xộn thiếu khoa học</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">H&oacute;a đơn sổ s&aacute;ch kh&ocirc;ng hợp lệ</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">C&aacute;c số liệu sổ s&aacute;ch kh&ocirc;ng minh bạch, kh&ocirc;ng tr&ugrave;ng khớp khiến doanh nghiệp bị truy thu số tiền lớn, g&acirc;y hậu quả nặng nề cho doanh nghiệp.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Kinh nghiệm, kĩ năng kế to&aacute;n non nớt của nh&acirc;n vi&ecirc;n kế to&aacute;n dẫn đến rất nhiều sai s&oacute;t, c&aacute;c khoản phạt kh&ocirc;ng đ&aacute;ng c&oacute;.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Ch&iacute;nh v&igrave; những l&yacute; do n&agrave;y, dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch, tổng hợp v&agrave; ho&agrave;n thiện sổ s&aacute;ch kế to&aacute;n cuối năm l&agrave; v&ocirc; c&ugrave;ng cần thiết, quan trọng đối với mọi doanh nghiệp, c&ocirc;ng ty.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Lợi &iacute;ch, ưu điểm dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n cuối năm:</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Kh&ocirc;ng phải ngẫu nhi&ecirc;n m&agrave; dịch vụ <a href=\"http://baocaothueonline.vn/ra-soat-so-sach\"><strong>r&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n</strong></a> lại được y&ecirc;u th&iacute;ch v&agrave; nhu cầu sử dụng lớn đến vậy. Đ&oacute; ch&iacute;nh l&agrave; nhờ những lợi &iacute;ch v&agrave; ưu điểm m&agrave; dịch vụ mang lại:</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Ph&aacute;t hiện lỗi kịp thời v&agrave; nhanh ch&oacute;ng trước khi cơ quan thuế của nh&agrave; nước kiểm tra doanh nghiệp của bạn</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Hệ thống sổ s&aacute;ch được sắp xếp một c&aacute;ch khoa học, hợp l&yacute; để b&aacute;o c&aacute;o cũng như thuận lợi cho việc lưu trữ, t&igrave;m kiếm sau n&agrave;y.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Được thực hiện bởi đội ngũ kế to&aacute;n gi&agrave;u kinh nghiệm cũng như khả năng t&iacute;nh to&aacute;n, sắp xếp khoa học v&agrave; nhanh ch&oacute;ng</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Được đảm bảo an to&agrave;n, hiệu quả một c&aacute;ch trọn g&oacute;i.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><img alt=\"rà soát sổ sách kế toán\" height=\"308\" src=\"/image/images/dich-vu-ra-soat-so-sach-ke-toan.png\" width=\"600\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Nếu bạn đang t&igrave;m kiếm một đơn vị cung cấp dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n uy t&iacute;n v&agrave; chất lượng th&igrave; h&atilde;y đến ngay với C&ocirc;ng Ty TNHH Quản Trị Rủi Ro Thuế Nguy&ecirc;n Anh -<a href=\"http://baocaothueonline.vn/\"><strong>&nbsp;http://baocaothueonline.vn/</strong></a></p>\r\n', 'dich-vu-ra-soat-so-sach-ke-toan.jpg', 0, 71, '', '2018-01-31 17:01:49', '2018-03-27 17:03:41', 1, '', '', '', '', '', 'Sự cần thiết của dịch vụ rà soát sổ sách kế toán', 'Để có được sổ sách kế toán rõ ràng thì sự cần thiết của dịch vụ rà soát sổ sách kế toán là vô cùng quan trọng', 'su-can-thiet-cua-dich-vu-ra-soat-so-sach-ke-toan', 'rà soát sổ sách kế toán', 1),
(84, 'Những lưu ý khi rà soát sổ sách kế toán doanh nghiệp thành phố Hồ Chí Minh', 'Để có được sổ sách kế toán chính xác và khoa học nhất nên lưu ý khi rà soát sổ sách kế toán doanh nghiệp tphcm', '<p dir=\"ltr\" style=\"text-align:justify\"><strong><a href=\"http://baocaothueonline.vn/nhung-luu-y-khi-ra-soat-so-sach-ke-toan-doanh-nghiep-thanh-pho-ho-chi-minh\">R&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n</a></strong><a href=\"http://baocaothueonline.vn/nhung-luu-y-khi-ra-soat-so-sach-ke-toan-doanh-nghiep-thanh-pho-ho-chi-minh\"><strong> doanh nghiệp tphcm</strong></a> l&agrave; một kh&acirc;u quan trọng đối với bất cứ doanh nghiệp n&agrave;o. Tuy nhi&ecirc;n, c&ocirc;ng việc n&agrave;y cũng kh&aacute; phức tạp đ&ograve;i hỏi người l&agrave;m cần đặc biệt ch&uacute; &yacute;. Trong b&agrave;i viết ngay h&ocirc;m nay h&atilde;y để Nguy&ecirc;n Anh Tax mang đến bạn những lưu &yacute; khi thực hiện c&ocirc;ng việc n&agrave;y.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Những lưu &yacute; khi r&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n doanh nghiệp tphcm:</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Trong qu&aacute; tr&igrave;nh <strong><a href=\"http://baocaothueonline.vn/ra-soat-so-sach\">r&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n</a></strong><a href=\"http://baocaothueonline.vn/ra-soat-so-sach\"><strong> doanh nghiệp tphcm</strong></a> v&agrave; lập b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh, doanh nghiệp sẽ cần k&ecirc; khai, kiểm tra những danh mục quan trọng sau:</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"rà soát sổ sách kế toán\" height=\"368\" src=\"/image/images/ra-soat-so-sach-ke-toan%20(1).jpg\" width=\"600\" /></p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Kiểm tra nguồn tiền mặt của doanh nghiệp</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Kiểm đầy đủ th&ocirc;ng tin t&agrave;i khoản ng&acirc;n h&agrave;ng, kiểm tra số dư</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Kiểm tra thuế gi&aacute; trị gia tăng khấu tr&ugrave;</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">C&ocirc;ng nợ của doanh nghiệp cần phải thu &ndash; trả trong một năm qua</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Kiểm tra v&agrave; đối chiếu tiền tạm ứng</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Kiểm tra lượng h&agrave;ng tồn kho</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Kiểm tra, k&ecirc; khai về t&agrave;i sản cố định của doanh nghiệp đ&atilde; đầy đủ hay chưa</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Kiểm tra c&aacute;c ph&acirc;n bố chi ph&iacute; trả trước những khoản hợp l&yacute; v&agrave; kh&ocirc;ng hợp l&yacute;</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Lập danh s&aacute;ch c&aacute;c loại thuế cần phải nộp</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Hoạch to&aacute;n về lương, bảo hiểm x&atilde; hội, bảo hiểm y tế...</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Kiểm tra c&aacute;c khoản tiền vay, mượn</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Kiểm tra, r&agrave; so&aacute;t doanh thu của doanh nghiệp</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">C&oacute; thể thấy rằng, c&ocirc;ng việc n&agrave;y rất phức tạp đ&ograve;i hỏi người k&ecirc; khai, r&agrave; so&aacute;t cần nắm chắc c&aacute;c th&ocirc;ng tin v&agrave; c&oacute; kỹ năng xử l&yacute; nhanh ch&oacute;ng, hiệu quả.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Nguy&ecirc;n Anh Tax &ndash; địa chỉ r&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n doanh nghiệp tphcm:</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"rà soát sổ sách kế toán\" height=\"233\" src=\"/image/images/ra-soat-so-sach-ke-toan%20(2)(1).jpg\" width=\"600\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Để ho&agrave;n th&agrave;nh nhanh ch&oacute;ng, hiệu quả v&agrave; ch&iacute;nh x&aacute;c c&ocirc;ng việc n&agrave;y c&aacute;c doanh nghiệp cần đến ngay với C&ocirc;ng Ty TNHH Quản Trị Rủi Ro Thuế Nguy&ecirc;n Anh &ndash; địa chỉ cung cấp dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n doanh nghiệp tphcm h&agrave;ng đầu hiện nay.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Đến với Nguy&ecirc;n Anh Tax bạn sẽ c&oacute; được:</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Dịch vụ nhanh ch&oacute;ng, hiệu quả</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Đảm bảo độ bảo mật 100%</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Được thực hiện bởi đội ngũ chuy&ecirc;n gia kế to&aacute;n chuy&ecirc;n nghiệp, chu đ&aacute;o c&oacute; kinh nghiệm nhiều năm trong lĩnh vực n&agrave;y.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Gi&aacute; th&agrave;nh phải chăng</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"text-align:justify\">Giảm thiểu rủi ro xuống mức thấp nhất.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Mong rằng những chia sẻ tr&ecirc;n đ&acirc;y sẽ gi&uacute;p bạn c&oacute; được th&ocirc;ng tin hữu &iacute;ch nhất về dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch kế to&aacute;n doanh nghiệp tphcm cũng như những lưu &yacute; quan trọng khi thực hiện. C&ograve;n chần chừ g&igrave; m&agrave; kh&ocirc;ng truy cập ngay <a href=\"http://baocaothueonline.vn\"><strong>http://baocaothueonline.vn</strong></a>&nbsp;để c&oacute; được những dịch vụ chất lượng nhất.</p>\r\n', 'ra-soat-so-sach-ke-toan (2).jpg', 0, 0, '', '2018-01-31 18:01:02', '2018-01-31 18:01:28', 1, '', '', '', '', '', 'Những lưu ý khi rà soát sổ sách kế toán doanh nghiệp thành phố Hồ Chí Minh', '', 'nhung-luu-y-khi-ra-soat-so-sach-ke-toan-doanh-nghiep-thanh-pho-ho-chi-minh', '', 1),
(85, 'Gia Bảo 9 Tuổi', '', '', '14636333654508_gia-bao-2.jpg', 0, 74, '', '2018-01-31 19:01:04', '2018-03-30 14:03:11', 1, '', '', '', '', '', 'Gia Bảo 9 Tuổi', 'Nguyên Anh Tax chính là địa chỉ cung cấp dịch vụ rà soát sổ sách kế toán cho doanh nghiệp uy tín, chất lượng nhất trên thị trường\r\n', 'gia-bao-9-tuoi', 'gia bảo, gia bao, gia bao 9 tuoi, gia bảo 9 tuổi', 1),
(86, 'Dịch vụ quyết toán thuế nhanh nhất', 'Trong những ngày cuối năm như thế này, các công ty đang phải khẩn trương hoàn thiện các hồ sơ sổ sách quyết toán thuế. Lượng công việc quá tải khiến chúng ta cảm thấy vất vả. Hãy tham khảo ngay dịch vụ quyết toán thuế. Nhanh và hiệu quả\r\n', '<p dir=\"ltr\"><strong><a href=\"http://baocaothueonline.vn/index.php\">Dịch vụ quyết to&aacute;n thuế</a></strong> ng&agrave;y c&agrave;ng được nhiều c&ocirc;ng ty lựa chọn. Giảm số lượng c&ocirc;ng việc để tr&aacute;nh nhầm lẫn.</p>\r\n\r\n<h2 dir=\"ltr\">Dịch vụ quyết to&aacute;n thuế, dịch vụ hỗ trợ doanh nghiệp</h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Đ&acirc;y l&agrave; một loại dịch vụ rất tiện lợi cho c&aacute;c doanh nghiệp từ nhỏ đến vừa. Thay v&igrave; việc c&oacute; qu&aacute; nhiều c&ocirc;ng việc cần giải quyết, c&aacute;c nh&acirc;n vi&ecirc;n thuế kh&ocirc;ng l&agrave;m hết được. Ch&uacute;ng ta rất cần đến dịch vụ với mong muốn thực hiện c&ocirc;ng việc ch&iacute;nh x&aacute;c hơn.</p>\r\n\r\n<p dir=\"ltr\">Hơn thế nữa, c&oacute; nhiều điều luật li&ecirc;n quan đến thuế nh&acirc;n vi&ecirc;n chưa thể nắm bắt được hết. M&agrave; c&aacute;c c&ocirc;ng ty c&ocirc;ng cấp dịch vụ quyết to&aacute;n thuế hiện nay lại l&agrave;m kh&aacute; tốt. Gi&uacute;p cải thiện t&igrave;nh trạng rủi ro khi quyết to&aacute;n.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"328\" src=\"https://lh5.googleusercontent.com/ERyRnkeS-BjhqtalgO8-oUi4C7tKv06o6h6KRCDn8XBS6l-k4Ni7B51SxTf4aER7ADKiHsU9o9rEEH1Rc7fx8ht9L9SRfAga0SMGYVpxPhcDy0FS4HatuXiJiP6mgEXqc4HqT_WyjdFjPBhUgw\" width=\"624\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Dịch vụ quyết to&aacute;n thuế lợi &iacute;ch cho c&aacute; nh&acirc;n v&agrave; cả doanh nghiệp</p>\r\n\r\n<h2 dir=\"ltr\">Dịch vụ quyết to&aacute;n thuế nhanh nhất.</h2>\r\n\r\n<p dir=\"ltr\">C&aacute;c c&ocirc;ng ty <a href=\"http://baocaothueonline.vn/quyet-toan-thue\"><strong>dịch vụ quyết to&aacute;n thuế</strong></a>, họ kh&ocirc;ng chỉ chuy&ecirc;n về một mảng thuế nhất định n&agrave;o đ&oacute;.M&agrave; mỗi một người sẽ chịu tr&aacute;ch nghiệm về một mảng chuy&ecirc;n s&acirc;u.</p>\r\n\r\n<p dir=\"ltr\">Lập l&ecirc;n bảng quyết to&aacute;n ho&agrave;n hảo nhất để nộp v&agrave;o cơ quan thuế nh&agrave; nước. Đơn vị cao nhất ph&ecirc; duyệt.</p>\r\n\r\n<p dir=\"ltr\">C&ocirc;ng việc cuối năm cần c&aacute;c doanh nghiệp phải ho&agrave;n thiện nhanh, do đ&oacute; một c&ocirc;ng ty chuy&ecirc;n cung cấp dịch vụ quyết to&aacute;n thuế thực sự cần thiết.</p>\r\n\r\n<p dir=\"ltr\">C&ocirc;ng ty TNHH quản trị rủi ro thuế Nguy&ecirc;n Anh nhanh ch&oacute;ng, chất lượng. C&ocirc;ng ty mang đến cho c&aacute;c doanh nghiệp một <a href=\"http://baocaothueonline.vn/dich-vu-quyet-toan-thue-nhanh-nhat\"><strong>dịch vụ quyết to&aacute;n thuế</strong></a> v&ocirc; c&ugrave;ng tuyệt vời. Ưu việt hơn hẳn so với c&aacute;c địa chỉ kh&aacute;c. Đặc biệt gi&aacute; cả rất phải chăng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"399\" src=\"https://lh6.googleusercontent.com/RH29hl77dN6MCEbxfgNk1DcIcp-XtQ6_fy0uK_c62fGrgiFHwAp7eZszThKHjYy-yeCFMvOhhRyzsTjwwCN4Ida-WTB_rZ7f4LerJfaGwiKGcXhFs_WWicdS7U9amlmdtFFCcT4GYCOguucpoA\" width=\"586\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Quyết to&aacute;n thuế nhanh nhất, ch&iacute;nh x&aacute;c nhất hiện nay</p>\r\n\r\n<p dir=\"ltr\">Hy vọng với những th&ocirc;ng tin n&agrave;y, kh&aacute;ch h&agrave;ng đ&atilde; c&oacute; th&ecirc;m kinh nghiệm v&agrave; địa chỉ c&ocirc;ng ty cung cấp dịch vụ quyết to&aacute;n thuế. Kết th&uacute;c một năm l&agrave;m việc trọn vẹn, để chuẩn bị cho những dự định trong năm tiếp theo. Li&ecirc;n hệ c&agrave;ng sớm gi&aacute; c&agrave;ng hợp l&yacute;.</p>\r\n', 'image2.jpg', 0, 60, '', '2018-01-31 19:01:32', '2018-01-31 19:01:24', 1, '', '', '', '', '', 'Dịch vụ quyết toán thuế nhanh nhất', 'Trong những ngày cuối năm như thế này, các công ty đang phải khẩn trương hoàn thiện các hồ sơ sổ sách quyết toán thuế. Lượng công việc quá tải khiến chúng ta cảm thấy vất vả. Hãy tham khảo ngay dịch vụ quyết toán thuế. Nhanh và hiệu quả\r\n', 'dich-vu-quyet-toan-thue-nhanh-nhat', 'dịch vụ quyết toán thuế', 1),
(87, 'Dịch vụ quyết toán thuế tại tphcm', 'Một thị trường công nghiệp như thành phố Hồ Chí Minh thì không thể nào thiếu được\r\ncác loại hình dịch vụ quyết toán thuế. Dịch vụ quyết toán thuế tại tphcm có giá như thế\r\nnào?', '<p dir=\"ltr\">C&acirc;u hỏi được nhiều c&aacute; nh&acirc;n, c&ocirc;ng ty quan t&acirc;m. V&agrave; đặc biệt địa chỉ n&agrave;o uy t&iacute;n nhất cung cấp dịch vụ n&agrave;y hiện nay tại tphcm.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Mục đ&iacute;ch sử dụng <a href=\"http://baocaothueonline.vn/quyet-toan-thue\">dịch vụ quyết to&aacute;n thuế tại tphcm</a></strong></h2>\r\n\r\n<p dir=\"ltr\">Thuế l&agrave; một trong những kh&acirc;u v&ocirc; c&ugrave;ng quan trọng đối với c&aacute;c doanh nghiệp. Tất cả c&aacute;c thủ tục li&ecirc;n quan đến hoạt động của c&ocirc;ng ty cần được giải tr&igrave;nh chi tiết đối chứng với nh&agrave; nước. Ch&iacute;nh v&igrave; thể trong qu&aacute; tr&igrave;nh thực hiện đ&atilde; c&oacute; kh&ocirc;ng &iacute;t c&aacute;c c&ocirc;ng ty gặp phải rủi ro, g&acirc;y hậu quả nghi&ecirc;m trọng.</p>\r\n\r\n<p dir=\"ltr\">Dịch vụ quyết to&aacute;n thuế được ra đời nhằm hạn chế sự rủi ro, đồng thời gi&uacute;p c&aacute;c doanh nghiệp quyết to&aacute;n nhanh nhất. Kh&ocirc;ng chỉ c&aacute;c th&ocirc;ng tin số liệu phải ch&iacute;nh x&aacute;c m&agrave; đi k&egrave;m v&agrave;o đ&oacute; l&agrave; c&aacute;c bi&ecirc;n bản, ho&aacute; đơn chuẩn ph&aacute;p l&yacute;. Để l&agrave;m được điều n&agrave;y, c&aacute;c c&ocirc;ng ty thường chọn những người c&oacute; kinh nghiệm, hiểu luật mới c&oacute; thể l&agrave;m được.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"388\" src=\"https://lh6.googleusercontent.com/Pt1MNU_zgVZ6owCHA6d0ajIvg1zLsrMgi99m7bhp0hfoxMo_JnN76-FZnUAjhFEGhwn0cwRRTdePw1HZcifx4DReibWW3DwR-2OaXkXP2JLkpTQ-GSVwtgFKurLjdiT24AjmHGmngAg8JAarLQ\" width=\"624\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">Quyết to&aacute;n thuế c&ocirc;ng việc quan trọng đối với c&aacute;c doanh nghiệp. Bạn đ&atilde; hiểu g&igrave; về c&ocirc;ng việc n&agrave;y chưa?</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Địa chỉ cung cấp <a href=\"http://baocaothueonline.vn/index.php\">dịch vụ quyết to&aacute;n thuế tại tphcm</a></strong></h2>\r\n\r\n<p dir=\"ltr\">Rất nhiều c&aacute;c c&ocirc;ng ty cung cấp dịch vụ n&agrave;y đang hoạt động tr&ecirc;n địa b&agrave;n th&agrave;nh phố hồ ch&iacute; minh. Song việc lựa chọn lại kh&ocirc;ng hề đơn giản với c&aacute;c doanh nghiệp.</p>\r\n\r\n<p dir=\"ltr\">Địa chỉ n&agrave;o uy t&iacute;n, chất lượng?</p>\r\n\r\n<p dir=\"ltr\">Với thời gian hoạt động l&acirc;u năm, c&ugrave;ng với đội ngũ c&aacute;c chuy&ecirc;n gia về thuế, đ&atilde; c&oacute; nhiều kinh nghiệm. C&ocirc;ng ty TNHH quản trị rủi ro thuế Nguy&ecirc;n Anh tự tin mang đến dịch vụ quyết to&aacute;n thuế đảm bảo nhất cho tất cả c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; nhu cầu.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"328\" src=\"https://lh4.googleusercontent.com/n6PpHBJjUYGmQ5YBq2ShP-0OFKS9-FfOIr9YCxyGO4vlIqhN1nqFeUL9cRwz20NxCWLEqcYHxRLjko-sQfWVu38QaKghyZ1zNslqLFLTAWN3cvYTwwCeQvu9W7W9Ri9hCg9UhvD5vRon4ODaqw\" width=\"624\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">Tầm quan trọng kh&ocirc;ng thể phủ nhận của <a href=\"http://baocaothueonline.vn/dich-vu-quyet-toan-thue-tai-tphcm\"><strong>dịch vụ quyết to&aacute;n thuế tại tphcm</strong></a> hiện nay ở c&aacute;c doanh nghiệp</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Nếu bạn đang gặp kh&oacute; khăn trong việc xử l&yacute; thuế. H&atilde;y li&ecirc;n hệ ngay tới địa chỉ n&agrave;y để c&oacute; th&ecirc;m tư vấn ph&ugrave; hợp.V&agrave; c&ugrave;ng trải nghiệm dịch vụ tiện &iacute;ch cho cuộc sống hiện đại như b&acirc;y giờ.</p>\r\n', 'Dịch vụ quyết toán thuế tại tphcm.01.jpg', 0, 68, '', '2018-01-31 19:01:52', '2018-03-27 15:03:11', 1, '', '', '', '', '', 'Dịch vụ quyết toán thuế tại tphcm', 'Một thị trường công nghiệp như thành phố Hồ Chí Minh thì không thể nào thiếu được các loại hình dịch vụ quyết toán thuế. Dịch vụ quyết toán thuế tại tphcm có giá như thế nào?\r\n', 'dich-vu-quyet-toan-thue-tai-tphcm', 'Dịch vụ quyết toán thuế tại tphcm', 1),
(88, 'Dịch vụ rà soát sổ sách uy tín, chuyên nghiệp giá tốt', 'Lựa chọn dịch vụ rà soát sổ sách uy tín, chuyên nghiệp giúp các doanh nghiệp hoàn toàn yên tâm về sổ sách, thuế, tránh được nhiều rùi ro.\r\n', '<p dir=\"ltr\">Để hỗ trợ c&aacute;c doanh nghiệp trong c&ocirc;ng t&aacute;c xử l&yacute; sổ s&aacute;ch, chứng từ ch&iacute;nh x&aacute;c, nhanh ch&oacute;ng, h&atilde;y tham khảo <a href=\"http://baocaothueonline.vn/ra-soat-so-sach\"><strong>dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch uy t&iacute;n</strong></a> dưới đ&acirc;y.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Lựa chọn dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch uy t&iacute;n để ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m, tr&aacute;nh rủi ro kh&ocirc;ng mong muốn</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"300\" src=\"https://lh6.googleusercontent.com/HebFH26f0cPJLRsp6trbZnnlW8XdKbidoenfofVEesCNg9FfnA16ikPa3cFOar-irQ4Qr5t80ReWVJrVyaaN9THMWFXyPHzOys9kCMP5f_1MNU6FrOhPD7c41RpPEcWADK1qnXJx7S3rwkFRiQ\" width=\"480\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">Lựa chọn <a href=\"http://baocaothueonline.vn/index.php\"><strong>dịch vụ ra so&aacute;t sổ s&aacute;ch uy t&iacute;n</strong></a></p>\r\n\r\n<p dir=\"ltr\">C&aacute;c doanh &nbsp;nghiệp thường vẫn c&oacute; bộ phận kế to&aacute;n, xử l&yacute; sổ s&aacute;ch ri&ecirc;ng, tuy nhi&ecirc;n do nhiều l&yacute; do m&agrave; trong qu&aacute; tr&igrave;nh l&agrave;m việc kh&ocirc;ng tr&aacute;nh khỏi những sai s&oacute;t, nhầm lẫn như c&ocirc;ng việc qu&aacute; bận rộn, nh&acirc;n vi&ecirc;n chưa c&oacute; nhiều kinh nghiệm chuy&ecirc;n m&ocirc;n,... Những sai s&oacute;t đ&oacute; v&ocirc; t&igrave;nh sẽ g&acirc;y ra nhiều sự rủi ro khiến doanh nghiệp kh&aacute; lo lắng, đau đầu.</p>\r\n\r\n<p dir=\"ltr\">Giải ph&aacute;p hữu &iacute;ch l&agrave; c&aacute;c doanh nghiệp n&ecirc;n nhờ đến <a href=\"http://baocaothueonline.vn/dich-vu-ra-soat-so-sach-uy-tin-chuyen-nghiep-gia-tot\"><strong>dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch uy t&iacute;n</strong></a> để đảm bảo an to&agrave;n, đem lại tiện lợi cho doanh nghiệp.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Nhiệm vụ của đơn vị cung cấp dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch uy t&iacute;n&nbsp;</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"401\" src=\"https://lh5.googleusercontent.com/caed2DmlYXdhUt5_yglxsRO27cHrvEmvEhyiyN7N39wojq-uVS3Vf3zJGTZ8V2fkougvWHKHPdi7Ftc_6-lei3j5EDEoOXcBya37Y4MzZeoYSD7mq3HgDzRdwEXcbIjgCW4XjWlWZquJUc4qeA\" width=\"602\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">Nhiệm vụ của đơn vị cung cấp dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch uy t&iacute;n</p>\r\n\r\n<p dir=\"ltr\">Đơn vị cung cấp dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch uy t&iacute;n sẽ hỗ trợ bạn:</p>\r\n\r\n<p dir=\"ltr\">Xem x&eacute;t, r&agrave; so&aacute;t to&agrave;n bộ sổ s&aacute;ch li&ecirc;n quan nhằm ph&aacute;t hiện những nhầm lẫn, sai s&oacute;t để kịp thời tư vấn cho doanh nghiệp xử l&yacute;.</p>\r\n\r\n<p dir=\"ltr\">Hỗ trợ v&agrave; tư vấn để giải quyết c&aacute;c vấn đề rắc rối gặp phải sau khi r&agrave; so&aacute;t sổ s&aacute;ch chi tiết, khoa học với chi ph&iacute; tiết kiệm tối đa.</p>\r\n\r\n<p dir=\"ltr\">Đảm bảo số liệu lu&ocirc;n ch&iacute;nh x&aacute;c tuyệt đối để c&aacute;c doanh nghiệp ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m kh&ocirc;ng phải nộp phạt hoặc gặp bất k&igrave; rủi ro n&agrave;o.</p>\r\n\r\n<p dir=\"ltr\">Tư vấn để doanh nghiệp lường trước những rủi ro v&agrave; c&oacute; c&aacute;ch xử l&yacute; kịp thời nếu c&oacute;.</p>\r\n\r\n<p dir=\"ltr\">Xử l&yacute; c&aacute;c chứng từ tồn đọng, lộn xộn, số liệu kế to&aacute;n chưa r&otilde; r&agrave;ng, chưa ổn.</p>\r\n\r\n<p dir=\"ltr\">Để được tư vấn dịch vụ r&agrave; so&aacute;t sổ s&aacute;ch uy t&iacute;n nhanh ch&oacute;ng, qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ C&ocirc;ng Ty TNHH Quản Trị Rủi Ro Thuế Nguy&ecirc;n Anh hoặc tham khảo website: Nguyenanhtax.vn</p>\r\n', 'dich-vu-ra-soat-so-sach-chat-luong-01.jpg', 0, 60, '', '2018-01-31 19:01:38', '2018-01-31 19:01:40', 1, '', '', '', '', '', 'Dịch vụ rà soát sổ sách uy tín, chuyên nghiệp giá tốt', 'Lựa chọn dịch vụ rà soát sổ sách uy tín, chuyên nghiệp giúp các doanh nghiệp hoàn toàn yên tâm về sổ sách, thuế, tránh được nhiều rùi ro.\r\n', 'dich-vu-ra-soat-so-sach-uy-tin-chuyen-nghiep-gia-tot', 'dịch vụ rà soát sổ sách uy tín', 1),
(89, 'Tìm hiểu về dịch vụ thành lập doanh nghiệp chất lượng', 'Công ty TNHH Quản Trị Rủi Ro Thuế Nguyên Anh (Nguyên Anh Tax) chuyên đáp ứng mọi yêu cầu của khách hàng với các dịch vụ thành lập doanh nghiệp chất lượng nhiều tiện ích, giá ưu đãi.\r\n', '<p dir=\"ltr\">Bạn đang c&oacute; dự định th&agrave;nh lập doanh nghiệp, bạn băn khoăn trong qui tr&igrave;nh, thủ tục c&aacute;ch thức đăng k&yacute;? Vậy th&igrave; h&atilde;y đến ngay với <a href=\"http://baocaothueonline.vn/\"><strong>dịch vụ th&agrave;nh lập doanh nghiệp chất lượng</strong></a> C&ocirc;ng Ty TNHH Quản Trị Rủi Ro Thuế Nguy&ecirc;n Anh v&agrave; tham khảo b&agrave;i viết dưới đ&acirc;y.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Dịch vụ th&agrave;nh lập doanh nghiệp chất lượng l&agrave; g&igrave;?</strong></h2>\r\n\r\n<p dir=\"ltr\"><a href=\"http://baocaothueonline.vn/thanh-lap-doanh-nghiep\"><strong>Dịch vụ th&agrave;nh lập doanh nghiệp chất lượng</strong></a> l&agrave; dịch vụ tư vấn, tiến h&agrave;nh thủ tục th&agrave;nh lập doanh nghiệp những bước đầu ti&ecirc;n để doanh nghiệp đi v&agrave;o hoạt động một c&aacute;ch hợp ph&aacute;p.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Kết quả hình ảnh cho thành lập doanh nghiệp\" height=\"395\" src=\"https://lh6.googleusercontent.com/osUasPK--t86PURPXAHpz9TZoXXBoXUT9xvm4YeBxFhoYx5OcbN61v9ua2qtEsrGxz2NomKnLfL39anDvIXia-SZxfggu3jc7ZEdvr7ubv8-C5FcHaON0TWUlZtFDw9rYxqJ2dDiTAcBiKtnvA\" width=\"601\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">C&aacute;c loại h&igrave;nh doanh nghiệp tại Việt Nam hiện nay:</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">C&ocirc;ng ty TNHH: một th&agrave;nh vi&ecirc;n, hai th&agrave;nh vi&ecirc;n</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">C&ocirc;ng ty cổ phần</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">C&ocirc;ng ty hợp danh</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Doanh nghiệp tư nh&acirc;n</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\">C&aacute;c loại hồ sơ cần chuẩn bị trước khi th&agrave;nh lập doanh nghiệp:</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Bản sao chứng minh nh&acirc;n d&acirc;n/hộ chiếu</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Bản sao giấy chứng nhận đăng k&yacute; kinh doanh c&ocirc;ng ty</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Giấy đề nghị đăng k&iacute; th&agrave;nh lập c&ocirc;ng ty</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Danh s&aacute;ch th&agrave;nh vi&ecirc;n hoặc cổ đ&ocirc;ng doanh nghiệp</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Th&ocirc;ng tin th&agrave;nh lập doanh nghiệp</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Điều lệ c&ocirc;ng ty, doanh nghiệp</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">C&aacute;c t&agrave;i liệu kh&aacute;c...</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2 dir=\"ltr\"><strong>Quy tr&igrave;nh dịch vụ th&agrave;nh lập doanh nghiệp chất lượng:</strong></h2>\r\n\r\n<p dir=\"ltr\">Đối với quy tr&igrave;nh <a href=\"http://baocaothueonline.vn/dich-vu-ra-soat-so-sach-uy-tin-chuyen-nghiep-gia-tot\"><strong>dịch vụ th&agrave;nh lập doanh nghiệp chất lượng</strong></a> th&igrave; tư vấn ch&iacute;nh l&agrave; bước đầu ti&ecirc;n v&ocirc; c&ugrave;ng quan trọng để bạn c&oacute; thể chuẩn bị v&agrave; th&agrave;nh lập doanh nghiệp.</p>\r\n\r\n<p dir=\"ltr\">Trong giai đoạn tư vấn, bạn sẽ được cung cấp những th&ocirc;ng tin sau:</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Tư vấn quy tr&igrave;nh thực hiện th&agrave;nh lập doanh nghiệp</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Hướng dẫn chuẩn bị c&aacute;c loại hồ sơ, th&ocirc;ng tin, giấy tờ cần chuẩn bị</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Tư vấn về vốn đầu tư của doanh nghiệp</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Tư vấn về quy định về đ&oacute;ng thuế</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Tư vấn điều lệ c&ocirc;ng ty</p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"873\" src=\"https://lh5.googleusercontent.com/uXpx_pYzX99G6jypqaOzVCBl8hCMatgJYSZwMFPCW2eY3-5ipiTCkBwDUcU91BXOKEmRcOLPQm5rsVlERgDNQ-aUR502lVEadkZDp3R1zb_CfGCEk4RclMBdpYXpSXWsMWJy3RfV\" width=\"602\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">Giấy chứng nhận đăng k&yacute; &nbsp;kinh doanh</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p dir=\"ltr\">Khi đ&atilde; ho&agrave;n th&agrave;nh qu&aacute; tr&igrave;nh tư vấn, c&ocirc;ng ty Nguy&ecirc;n Anh sẽ gi&uacute;p bạn l&agrave;m những c&ocirc;ng việc sau để ho&agrave;n th&agrave;nh th&agrave;nh lập doanh nghiệp:</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Soạn hồ sơ đăng k&yacute;</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Nộp hồ sơ đăng k&yacute; v&agrave; nhận kết quả</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">B&agrave;n giao cho kh&aacute;ch h&agrave;ng giấy chứng nhận v&agrave; dấu ph&aacute;p nh&acirc;n của c&ocirc;ng ty</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Lập c&aacute;c loại giấy tờ k&ecirc; khai thuế của doanh nghiệp sau khi được th&agrave;nh lập</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\">Đến với dịch vụ th&agrave;nh lập doanh nghiệp chất lượng đặc biệt tại Nguy&ecirc;n Anh, bạn sẽ c&oacute; thể ho&agrave;n th&agrave;nh c&aacute;ch thủ tục một c&aacute;ch nhanh ch&oacute;ng v&agrave; ch&iacute;nh x&aacute;c nhất.</p>\r\n\r\n<p dir=\"ltr\">C&ograve;n chần chừ g&igrave; m&agrave; kh&ocirc;ng sử dụng ng&agrave;y dịch vụ th&agrave;nh lập doanh nghiệp chất lượng tại Nguy&ecirc;n Anh tax để tiết kiệm thời gian, c&ocirc;ng sức trong quy tr&igrave;nh th&agrave;nh lập doanh nghiệp. Mọi &yacute; kiến thắc mắc vui l&ograve;ng truy cập web: http://nguyenanhtax.vn</p>\r\n', 'dv-2.png', 0, 66, '', '2018-02-01 21:02:50', '2018-03-27 15:03:13', 1, '', '', '', '', '', 'Tìm hiểu về dịch vụ thành lập doanh nghiệp chất lượng', 'Công ty TNHH Quản Trị Rủi Ro Thuế Nguyên Anh (Nguyên Anh Tax) chuyên đáp ứng mọi yêu cầu của khách hàng với các dịch vụ thành lập doanh nghiệp chất lượng nhiều tiện ích, giá ưu đãi.\r\n', 'tim-hieu-ve-dich-vu-thanh-lap-doanh-nghiep-chat-luong', 'dịch vụ thành lập doanh nghiệp chất lượng', 1),
(90, 'Địa chỉ cung cấp dịch vụ thành lập doanh nghiệp uy tín', 'Nguyên Anh Tax chính là địa chỉ cung cấp dịch vụ thành lập doanh nghiệp uy tín, chất lượng nhất.\r\n', '<p dir=\"ltr\">Dịch vụ th&agrave;nh lập doanh nghiệp l&agrave; một trong những dịch vụ được nhiều người t&igrave;m kiếm nhất hiện nay. Tuy nhi&ecirc;n, để c&oacute; được dịch vụ nhanh ch&oacute;ng, chất lượng nhất đ&ograve;i hỏi phải c&oacute; một địa chỉ uy t&iacute;n. Nguy&ecirc;n Anh Tax ch&iacute;nh l&agrave; Địa chỉ <strong><a href=\"http://baocaothueonline.vn/index.php\">cung cấp dịch vụ th&agrave;nh lập doanh nghiệp</a> </strong>uy t&iacute;n m&agrave; bạn đang t&igrave;m kiếm.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Tại sao n&ecirc;n sử dụng cung cấp dịch vụ th&agrave;nh lập doanh nghiệp:</strong></h2>\r\n\r\n<p dir=\"ltr\">Trước khi đến với địa chỉ <a href=\"http://baocaothueonline.vn/thanh-lap-doanh-nghiep\"><strong>cung cấp dịch vụ th&agrave;nh lập doanh nghiệp</strong></a> th&igrave; bạn cần biết được tại sao n&ecirc;n sử dụng dịch vụ n&agrave;y. L&yacute; do quan trọng nhất ch&iacute;nh l&agrave; do thủ tục đăng k&yacute; th&agrave;nh lập doanh nghiệp v&ocirc; c&ugrave;ng phức tạp v&agrave; cần sự cẩn trọng, tỉ mỉ. Nếu kh&ocirc;ng c&oacute; hiểu biết v&agrave; kinh nghiệm trong vấn đề n&agrave;y sẽ khiến bạn tốn thời gian, c&ocirc;ng sức v&agrave; thậm ch&iacute; l&agrave; ảnh hưởng trực tiếp đến hoạt động của c&ocirc;ng ty sau n&agrave;y.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Kết quả hình ảnh cho thành lập doanh nghiệp\" height=\"199\" src=\"https://lh4.googleusercontent.com/SgVoO3ylEqaTcwZ4r_p-o2Ohxrtq1QS-mMrxdUcRJG9O0yD2MNjRys7e2Ffj6F4O2xIfhd-tvOT5G2uRXlm_2saZlFwkdIxX-_dpF0txvMDj9h39p-BGWsplQ8RJHBexTlmgNGgiL4KvRQxmsw\" width=\"607\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">Dịch vụ th&agrave;nh lấp doanh nghiệp</p>\r\n\r\n<p dir=\"ltr\">Với dịch vụ th&agrave;nh lập n&agrave;y sẽ hỗ trợ bạn trong việc giấy tờ, quy tr&igrave;nh, tư vấn th&agrave;nh lập đ&uacute;ng c&aacute;ch, khoa học v&agrave; hợp ph&aacute;p.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Nguy&ecirc;n Anh Tax &ndash; địa chỉ cung cấp dịch vụ th&agrave;nh lập doanh nghiệp:</strong></h2>\r\n\r\n<p dir=\"ltr\">Nếu bạn đang t&igrave;m kiếm một địa chỉ <a href=\"http://baocaothueonline.vn/dia-chi-cung-cap-dich-vu-thanh-lap-doanh-nghiep-uy-tin\"><strong>cung cấp dịch vụ th&agrave;nh lập doanh nghiệp</strong></a> uy t&iacute;n, chất lượng nhất h&atilde;y đến ngay với C&ocirc;ng Ty TNHH Quản Trị Rủi Ro Thuế Nguy&ecirc;n Anh.</p>\r\n\r\n<p dir=\"ltr\">Đến với Nguy&ecirc;n Anh Tax, bạn sẽ nhận được những dịch vụ doanh nghiệp v&ocirc; c&ugrave;ng chất lượng. Lợi &iacute;ch của kh&aacute;ch h&agrave;ng khi đến với Nguy&ecirc;n Anh Tax trong dịch vụ th&agrave;nh lập doanh nghiệp như:</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Sự hỗ trợ, tư vấn chuy&ecirc;n nghiệp, chu đ&aacute;o của đội ngũ luật sư gi&agrave;u kinh nghiệm, c&oacute; chuy&ecirc;n m&ocirc;n cao trong lĩnh vực luật doanh nghiệp, luật kinh tế.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">L&agrave; đại diện gi&uacute;p kh&aacute;ch h&agrave;ng giải quyết tất cả c&aacute;c vấn đề li&ecirc;n quan đến tiến h&agrave;nh th&agrave;nh lập doanh nghiệp một c&aacute;ch ch&iacute;nh x&aacute;c v&agrave; bảo mật nhất.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Hỗ trợ giải quyết c&aacute;c tranh chấp nếu xảy ra sau khi th&agrave;nh lập, tư vấn giải quyết miễn ph&iacute;.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Kết quả hình ảnh cho thành lập doanh nghiệp\" height=\"229\" src=\"https://lh3.googleusercontent.com/WnuAv2vCLwQw_BcupNWxj0zSBJ1B36aoLOMPIUwfGcq6sYT0fG-ryU1-xm4yC4MZbPHhtElEn6yL5X3VfsyRCiEJx6Nw71Zkm1pcNLA0lkMW19xk7Rs-q7nL2EnKGurm7EXREFgVWFCzDlDFQg\" width=\"595\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">Bạn sẽ nhận được những dịch vụ doanh nghiệp v&ocirc; c&ugrave;ng chất lượng</p>\r\n\r\n<p dir=\"ltr\">Đến với Nguy&ecirc;n Anh Tax ch&iacute;nh l&agrave; đến với c&aacute;c dịch vụ chất lượng nhất, uy t&iacute;n nhất. Tuy nhi&ecirc;n, ch&uacute;ng t&ocirc;i cũng lu&ocirc;n ch&uacute; &yacute; đến gi&aacute; th&agrave;nh phải chăng để đặt lợi &iacute;ch của kh&aacute;ch h&agrave;ng l&ecirc;n h&agrave;ng đầu.</p>\r\n\r\n<p dir=\"ltr\">Chắc hẳn với những th&ocirc;ng tin tr&ecirc;n đ&acirc;y bạn đ&atilde; c&oacute; được cho m&igrave;nh địa chỉ cung cấp dịch vụ th&agrave;nh lập doanh nghiệp uy t&iacute;n. H&atilde;y đến ngay với Nguy&ecirc;n Anh Tax để c&oacute; được những dịch vụ li&ecirc;n quan đến doanh nghiệp chất lượng nhất. Mọi li&ecirc;n hệ vui l&ograve;ng truy cập v&agrave;o web: <a href=\"http://nguyenanhtax.vn\">http://nguyenanhtax.vn</a> .</p>\r\n', 'địa-chỉ-2.png', 0, 66, '', '2018-02-01 21:02:47', '2018-03-27 15:03:20', 1, '', '', '', '', '', 'Địa chỉ cung cấp dịch vụ thành lập doanh nghiệp uy tín', 'Nguyên Anh Tax chính là địa chỉ cung cấp dịch vụ thành lập doanh nghiệp uy tín, chất lượng nhất.\r\n', 'dia-chi-cung-cap-dich-vu-thanh-lap-doanh-nghiep-uy-tin', 'cung cấp dịch vụ thành lập doanh nghiệp', 1);
INSERT INTO `news` (`news_id`, `news_name`, `news_des`, `news_content`, `news_img`, `news_views`, `newscat_id`, `news_author`, `news_created_date`, `news_update_date`, `state`, `news_sub_info1`, `news_sub_info2`, `news_sub_info3`, `news_sub_info4`, `news_sub_info5`, `title_seo`, `des_seo`, `friendly_url`, `keyword`, `created_id`) VALUES
(91, 'Giới thiệu về Smart Skills', 'Math Skills được xem là phương pháp phát triển tư duy Toán học tiên tiến của Mỹ dành cho học sinh Mầm non và Tiểu học', '<h2>KỸ NĂNG TƯ DUY TO&Aacute;N HỌC &ndash; GI&Uacute;P TRẺ EM PH&Aacute;T TRIỂN TƯ DUY V&Agrave; HỌC GIỎI TO&Aacute;N</h2>\r\n\r\n<p><br />\r\n<br />\r\nMath Skills được xem l&agrave;&nbsp;<strong>phương ph&aacute;p ph&aacute;t triển tư duy To&aacute;n học ti&ecirc;n tiến của Mỹ d&agrave;nh cho học sinh Mầm non v&agrave; Tiểu học.</strong>&nbsp;Math Skills hướng đến việc ph&aacute;t triển kỹ năng tư duy To&aacute;n học cho trẻ em ngay từ nhỏ với phương ph&aacute;p c&aacute; thể h&oacute;a nhằm tối đa ho&aacute; hiệu quả v&agrave; ph&aacute;t huy được điểm mạnh đồng thời khắc phục điểm yếu của từng học sinh.</p>\r\n\r\n<p><br />\r\nNghi&ecirc;n cứu của Trường Đại Học Harvard- Mỹ năm 2006 cho thấy, trẻ em được tiếp cận với kỹ năng tư duy To&aacute;n ngay từ tuổi Mầm non sẽ c&oacute; khả năng tiếp cận với tất cả c&aacute;c m&ocirc;n học tại trường Tiểu học tốt hơn c&aacute;c bạn c&ugrave;ng trang lứa chưa được tiếp cận với kỹ năng n&agrave;y. Nghi&ecirc;n cứu cũng chỉ ra rằng, trẻ em được tiếp cận với kỹ năng tư duy To&aacute;n trước 6 tuổi sẽ c&oacute; khả năng tập trung, khả năng quan s&aacute;t, ph&acirc;n t&iacute;ch vấn đề v&agrave; tư duy logic tốt hơn so với c&aacute;c trẻ em kh&aacute;c.</p>\r\n\r\n<p><img alt=\"\" height=\"218\" src=\"http://americanskills.vn/contents_americanskills/uploads/images/0033-01-1024x447.jpg\" width=\"500\" /></p>\r\n\r\n<p><br />\r\nTheo kết quả khảo s&aacute;t của&nbsp;<strong>Bộ Gi&aacute;o dục Mỹ</strong>&nbsp;cũng cho thấy:</p>\r\n\r\n<p><br />\r\n- Học sinh học giỏi To&aacute;n cũng c&oacute; khả năng học giỏi c&aacute;c m&ocirc;n học kh&aacute;c. Tỷ lệ n&agrave;y l&agrave; 38%.<br />\r\n- Học sinh c&oacute; khả năng tư duy To&aacute;n tốt c&oacute; khả năng đỗ v&agrave;o c&aacute;c Trường Đại học cao hơn c&aacute;c học sinh kh&aacute;c đồng thời những sinh vi&ecirc;n n&agrave;y ra trường c&oacute; tỷ lệ t&igrave;m được c&ocirc;ng việc tốt với mức lương cao hơn c&aacute;c sinh vi&ecirc;n kh&aacute;c kh&ocirc;ng giỏi To&aacute;n. Tỷ lệ n&agrave;y l&agrave; 42%.</p>\r\n\r\n<p><br />\r\nThực tế cho thấy trẻ em Việt Nam c&oacute; tố chất học giỏi m&ocirc;n To&aacute;n. Tuy nhi&ecirc;n, phương ph&aacute;p gi&aacute;o dục hiện tại mới chỉ dừng lại ở việc học nhớ, học vẹt v&agrave; l&agrave;m nhiều th&agrave;nh quen m&agrave; kh&ocirc;ng vận dụng kỹ năng tư duy logic, quan s&aacute;t, ph&acirc;n t&iacute;ch vấn đề. American Skills x&acirc;y dựng chương tr&igrave;nh Math Skills với mong muốn mang đến cho trẻ em Việt Nam kiến thức nền tảng về To&aacute;n học &ndash; th&ocirc;ng qua việc hiểu bản chất vấn đề, đồng thời luyện tập khả năng tư duy logic v&agrave; ph&acirc;n t&iacute;ch vấn đề m&agrave; kh&ocirc;ng cần học nhớ, học vẹt.</p>\r\n\r\n<h2>PHƯƠNG PH&Aacute;P Đ&Agrave;O TẠO</h2>\r\n\r\n<ul>\r\n	<li>Đ&agrave;o tạo theo&nbsp;<strong>phương ph&aacute;p c&aacute; thể ho&aacute;</strong>&nbsp;&ndash; mỗi học sinh sẽ được học theo một kế hoạch học tập ri&ecirc;ng.</li>\r\n	<li>Vận dụng c&aacute;c&nbsp;<strong>kỹ thuật giảng dạy đa chiều</strong>&nbsp;gồm: quan s&aacute;t h&igrave;nh ảnh gi&aacute;o cụ trực quan -&gt; diễn đạt bằng ng&ocirc;n từ -&gt; suy luận tư duy logic-&gt; to&aacute;n viết -&gt; ứng dụng.Gi&uacute;p học sinh hiểu nhanh bản chất của vấn đề, ph&aacute;t triển khả năng quan s&aacute;t, ph&aacute;n đo&aacute;n, tư duy đồng thời biết ứng dụng to&aacute;n v&agrave;o thực tế.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Vận dụng c&aacute;ch tiếp cận vấn đề đi từ bản chất, từ c&aacute;i cơ bản đến tổng thể v&agrave; n&acirc;ng cao với 3 bộ b&agrave;i tập: r&egrave;n luyện kỹ năng t&iacute;nh to&aacute;n, To&aacute;n c&oacute; lời giải, To&aacute;n tư duy tổng hợp. Gi&uacute;p trẻ thấu hiểu to&aacute;n, y&ecirc;u th&iacute;ch học to&aacute;n v&agrave; ph&aacute;t triển tư duy.</li>\r\n	<li>Qu&aacute; tr&igrave;nh học tập với quy tr&igrave;nh kh&eacute;p k&iacute;n:&nbsp;<strong>Kiểm tra đầu v&agrave;o- Đ&agrave;o tạo- Kiểm tra cuối kho&aacute;</strong>. Gi&uacute;p đ&aacute;nh gi&aacute; sự tiến bộ của học sinh trong từng kho&aacute; học.</li>\r\n	<li><strong>Gi&aacute;o tr&igrave;nh</strong>&nbsp;do c&aacute;c chuy&ecirc;n gia To&aacute;n học của American Skills x&acirc;y dựng c&oacute; sự điều chỉnh cho&nbsp;<em>ph&ugrave; hợp với chương tr&igrave;nh To&aacute;n của Việt Nam</em>.</li>\r\n	<li>Phương ph&aacute;p giảng dạy trực quan, sinh động lấy học sinh l&agrave;m chủ thể.</li>\r\n	<li>M&ocirc;i trường học tập th&acirc;n thiện, chuy&ecirc;n nghiệp.</li>\r\n</ul>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2>LƠI &Iacute;CH SAU KH&Oacute;A HỌC</h2>\r\n\r\n<ul>\r\n	<li>&nbsp; &nbsp; &nbsp;&nbsp;Gi&uacute;p học sinh hiểu bản chất vấn đề v&agrave; biết ứng dụng To&aacute;n v&agrave;o thực tế.</li>\r\n	<li>&nbsp;&nbsp;&nbsp; Gi&uacute;p trẻ em y&ecirc;u th&iacute;ch học to&aacute;n, c&oacute; &yacute; thức tự gi&aacute;c trong học tập.</li>\r\n	<li>&nbsp;&nbsp;&nbsp; Gi&uacute;p c&aacute;c em học tốt M&ocirc;n To&aacute;n v&agrave; c&aacute;c m&ocirc;n học kh&aacute;c tr&ecirc;n trường.</li>\r\n	<li>&nbsp;&nbsp;&nbsp; Gi&uacute;p trẻ em ph&aacute;t triển tư duy to&agrave;n diện.</li>\r\n	<li>&nbsp;&nbsp;&nbsp; Gi&uacute;p c&aacute;c em ho&agrave;n thiện bản th&acirc;n v&agrave; l&agrave;m chủ cuộc sống.</li>\r\n</ul>\r\n', 'fastrackids-3-460.png', 0, 62, '', '2018-02-01 21:02:38', '2018-03-27 14:03:02', 1, '', '', '', '', '', 'Giới thiệu về Smart Skills', 'Với dịch vụ kê khai thuế online sẽ giúp bạn tiết kiệm thời gian, công sức một cách hiệu quả nhất.\r\n', 'gioi-thieu-ve-smart-skills', 'dịch vụ kê khai thuế online', 1),
(92, 'Giới thiệu về Smart Reading Skills', 'Chương trình Smart Reading Skills được phát triển trên nền tảng phương pháp học đọc  đa giác quan kết hợp đa cấp độ của Mỹ', '<h2>PHƯƠNG PH&Aacute;P ĐỌC TH&Ocirc;NG MINH GI&Uacute;P B&Eacute; Y&Ecirc;U PH&Aacute;T TRIỂN TR&Iacute; N&Atilde;O V&Agrave; KHẢ NĂNG NG&Ocirc;N NGỮ</h2>\r\n\r\n<p><br />\r\nChương tr&igrave;nh&nbsp;Smart Reading Skills&nbsp;được ph&aacute;t triển tr&ecirc;n nền tảng phương ph&aacute;p học đọc&nbsp; đa gi&aacute;c quan kết hợp đa cấp độ của Mỹ&nbsp; đ&atilde; được &aacute;p dụng tại c&aacute;c trường học tr&ecirc;n khắp nước&nbsp; Mỹ&nbsp; cũng như tại nhiều quốc gia tr&ecirc;n thế giới. Phương ph&aacute;p n&agrave;y được phụ huynh ưa chuộng v&agrave; lựa chọn l&agrave; phương ph&aacute;p học đọc s&aacute;ng tạo&nbsp; v&agrave; hiệu quả nhất hiện nay.&nbsp;<em><strong>Smart Reading Skills được c&ocirc;ng nhận gi&uacute;p trẻ ph&aacute;t triển tr&iacute; n&atilde;o v&agrave; khả năng ng&ocirc;n ngữ.</strong></em></p>\r\n\r\n<p>Theo nghi&ecirc;n cứu của Trường Đại Học Harvard- Mỹ th&igrave; c&oacute; đến 70% trẻ em dưới 6 tuổi được cha mẹ c&ugrave;ng đọc s&aacute;ch trước khi đi ngủ. C&aacute;c chuy&ecirc;n gia cho biết việc tiếp tục hướng dẫn trẻ đọc s&aacute;ch trong giai đoạn tiểu học l&agrave; rất quan trọng đối với sự ph&aacute;t triển tr&iacute; n&atilde;o v&agrave; ng&ocirc;n ngữ của c&aacute;c em. Tuy nhi&ecirc;n, hầu hết c&aacute;c trẻ em Việt Nam từ 7 tuổi trở l&ecirc;n kh&ocirc;ng được r&egrave;n luyện kỹ năng n&agrave;y ở trường cũng như ở nh&agrave; m&agrave; hầu hết c&aacute;c em đều tự m&igrave;nh đọc một c&aacute;ch tự ph&aacute;t. V&igrave; vậy, n&ecirc;n sau 7 tuổi c&aacute;c em bắt đầu bộc lộ nhiều điểm hạn chế về khả năng đọc, ph&acirc;n t&iacute;ch, diễn đạt bằng ng&ocirc;n ngữ cũng như khả năng tư duy độc lập. Thực tế cho thấy rằng những trẻ em th&iacute;ch đọc s&aacute;ch c&oacute; nhiều khả năng đạt th&agrave;nh t&iacute;ch học tập tốt ở trường v&agrave; th&agrave;nh c&ocirc;ng hơn ở nơi l&agrave;m việc sau n&agrave;y.</p>\r\n\r\n<p>B&iacute; quyết gi&uacute;p c&aacute;c em quan t&acirc;m đến việc đọc l&agrave; giới thiệu với c&aacute;c em nhiều loại s&aacute;ch ph&ugrave; hợp với từng độ tuổi v&agrave; cho con tiếp x&uacute;c với việc học đọc c&agrave;ng sớm c&agrave;ng tốt. Với mỗi chủ đề n&ecirc;n cho trẻ tiếp cận v&agrave; hiểu được từ mới, nội dung cuốn s&aacute;ch v&agrave; c&ugrave;ng thảo luận về c&aacute;c nội dung đ&atilde; đọc để&nbsp; gi&uacute;p trẻ bộc lộ hiểu biết, khả nằn suy luận của m&igrave;nh v&agrave; ph&aacute;t triển tư duy.</p>\r\n\r\n<p>Chương tr&igrave;nh&nbsp;<strong>Smart Reading Skills</strong>&nbsp;đ&atilde; được&nbsp; American Skills triển khai tại Việt Nam nhằm mang đến cho c&aacute;c em những lợi &iacute;ch sau:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&uacute;p c&aacute;c b&eacute; luyện ph&aacute;t &acirc;m chuẩn.</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&uacute;p b&eacute; từ việc biết đ&aacute;nh vần đến biết đọc trơn.</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&uacute;p c&aacute;c em th&iacute;ch đọc s&aacute;ch v&agrave; biết c&aacute;ch tự đọc đ&uacute;ng c&aacute;ch.</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&uacute;p c&aacute;c em h&igrave;nh th&agrave;nh c&aacute;c kỹ năng tư duy như: khả năng t&oacute;m tắt v&agrave; ghi nhớ th&ocirc;ng tin, khả năng ph&acirc;n t&iacute;ch v&agrave; kh&aacute;i qu&aacute;t một vấn đề, khả năng suy luận logic, khả năng tưởng tượng, li&ecirc;n tưởng v&agrave; s&aacute;ng tạo,&hellip;</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&uacute;p n&atilde;o trẻ hoạt động v&agrave; gi&uacute;p suy nghĩ của trẻ ph&aacute;t triển đa chiều, đồng thời c&ograve;n l&agrave;m tăng sự tập trung của n&atilde;o bộ trong mọi ho&agrave;n cảnh.</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&uacute;p tăng cường khả năng giao tiếp hiệu quả v&agrave; gi&uacute;p r&egrave;n luyện năng lực ng&ocirc;n ngữ: đọc s&aacute;ch gi&uacute;p l&agrave;m gi&agrave;u vốn từ, tăng khả năng sử dụng ng&ocirc; ngữ để diễn đạt v&agrave; giao tiếp.</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&uacute;p c&aacute;c b&eacute; h&igrave;nh th&agrave;nh nh&acirc;n c&aacute;ch, vốn sống v&agrave; kiến thức x&atilde; hội th&ocirc;ng qua c&aacute;c chủ đề b&agrave;i học phong ph&uacute;, đa dạng.</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&uacute;p trẻ cảm thấy phấn chấn hơn, gi&uacute;p b&eacute; giảm bớt những th&oacute;i quen ảnh hưởng đến khả năng tư duy v&agrave; tập trung của b&eacute; như xem tivi, chơi game,&hellip;</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&uacute;p c&aacute;c em giảm bớt &aacute;p lực của cuộc sống v&agrave; lấy lại sự c&acirc;n bằng cần thiết, gi&uacute;p c&aacute;c em sống khoẻ v&agrave; sống tốt hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>PHƯƠNG PH&Aacute;P CỦA CHƯƠNG TR&Igrave;NH:</h2>\r\n\r\n<p><strong>Phương ph&aacute;p học đọc đa cấp độ</strong>: Mỗi buổi học b&eacute; được học một nội dung/ chủ đề hấp dẫn v&agrave; ph&ugrave; hợp với độ tuổi v&agrave; tr&igrave;nh độ của b&eacute;. Đ&acirc;y l&agrave; nội dung được c&aacute;c chuy&ecirc;n gia nước ngo&agrave;i bi&ecirc;n soạn d&agrave;nh ri&ecirc;ng cho chương tr&igrave;nh&nbsp;<strong>Smart Reading Skills</strong>&nbsp;với c&aacute;c chủ đề rất đa dạng, với nhiều cấp độ kh&aacute;c nhau như: thế giới quanh em, kh&aacute;m ph&aacute; bản th&acirc;n, gi&aacute;o dục t&igrave;nh cảm gia đ&igrave;nh, bạn b&egrave;, gi&aacute;o dục gi&aacute; trị sống, c&aacute;c hiện tượng thi&ecirc;n nhi&ecirc;n v&agrave; c&aacute;c t&aacute;c phẩm văn học nổi tiếng cho mọi lứa tuổi,&hellip;</p>\r\n\r\n<p><strong>Phương ph&aacute;p học đọc đa gi&aacute;c quan</strong>: Với mỗi chủ đề b&eacute; đều được tiếp cận th&ocirc;ng qua c&aacute;c gi&aacute;c quan như: nghe, nh&igrave;n, sờ, tưởng tượng, đọc, nhập vai,&hellip;với c&aacute;c hoạt động phong ph&uacute; như: đố vui, đ&oacute;ng kịch, kể chuyện, xem phim, b&igrave;nh luận, thảo luận nh&oacute;m, chơi tr&ograve; chơi tương t&aacute;c, vận động cơ thể theo ng&ocirc;n ngữ, diễn đạt ng&ocirc;n ngữ bằng c&aacute;c h&agrave;nh động phi ng&ocirc;n ngữ.</p>\r\n\r\n<p>Ph&aacute;t triển Kỹ năng tư duy: Mỗi buổi b&eacute; được giới thiệu v&agrave; r&egrave;n luyện một hoặc một số kỹ năng tư duy nhất định. Chẳng hạn như:</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; T&oacute;m tắt th&ocirc;ng tin &ndash; dưới dạng sơ đồ tư duy</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ghi nhớ v&agrave; diễn đạt</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kh&aacute;i qu&aacute;t v&agrave; tổng kết</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kỹ năng suy luận v&agrave; tư duy</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kỹ năng ph&aacute;n đo&aacute;n</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kỹ năng ph&acirc;n t&iacute;ch nh&acirc;n vật v&agrave; ph&acirc;n t&iacute;ch bối cảnh</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đưa ra giải ph&aacute;p cho một vấn đề</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Li&ecirc;n tưởng v&agrave; &aacute;p dụng</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tưởng tượng v&agrave; s&aacute;ng tạo</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ vựng: Mỗi buổi sẽ cung cấp cho b&eacute; một lượng từ vựng mới nhất định tuỳ thuộc v&agrave;o từng cấp độ, gi&uacute;p b&eacute; bổ sung vốn từ th&ecirc;m phong ph&uacute;.</p>\r\n\r\n<p>B&aacute;o c&aacute;o h&agrave;ng ng&agrave;y: Sau từng buổi học, phụ huynh đều nhận được th&ocirc;ng tin&nbsp; về những g&igrave; m&agrave; b&eacute; được học trong buổi học đ&oacute;, c&ugrave;ng với giới &yacute; về b&agrave;i tập gi&uacute;p b&eacute; r&egrave;n luyện tại nh&agrave;.</p>\r\n', 'Banner-fix-01-1024x538.jpg', 0, 63, '', '2018-03-27 14:03:56', '2018-03-27 14:03:19', 1, '', '', '', '', '', 'Giới thiệu về Smart Reading Skills', '', 'gioi-thieu-ve-smart-reading-skills', '', 1),
(93, 'Khóa học của Social Skills', '', '', '', 0, 64, '', '2018-03-27 14:03:54', NULL, 1, '', '', '', '', '', 'Khóa học của Social Skills', '', 'khoa-hoc-cua-social-skills', '', 1),
(94, ' Khóa học của tiếng anh', '', '', '', 0, 65, '', '2018-03-27 14:03:07', NULL, 1, '', '', '', '', '', ' Khóa học của tiếng anh', '', 'khoa-hoc-cua-tieng-anh', '', 1),
(95, 'Giới thiệu tuan', '', '<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Sứ mệnh</strong>&nbsp;của ch&uacute;ng t&ocirc;i l&agrave; tạo ra một m&ocirc;i trường học c&oacute; thể đem lại ảnh hưởng t&iacute;ch cực, niềm vui, t&igrave;nh y&ecirc;u v&agrave; sự ph&aacute;t triển to&agrave;n diện cho học sinh. B&ecirc;n cạnh c&aacute;c kiến thức chuy&ecirc;n m&ocirc;n,&nbsp;học sinh tại&nbsp;<strong>American Skills&nbsp;</strong>được bồi dưỡng nh&acirc;n c&aacute;ch, ph&aacute;t triển sự tự tin, kỹ năng sống l&agrave; bước đệm quan trọng, cần thiết cho sự th&agrave;nh c&ocirc;ng của c&aacute;c em trong tương lai.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"color:#2980b9\"><strong>TRIẾT L&Yacute; GI&Aacute;O DỤC : PHƯƠNG PH&Aacute;P TO&Agrave;N DIỆN</strong></span></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"color:#2980b9\"><strong>ĐỂ GI&Uacute;P TRẺ TẠO DỰNG TƯƠNG LAI</strong></span></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"color:#2980b9\"><strong>HELPING CHILDREN WRITE THE FUTURE</strong></span></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Qua c&aacute;c nghi&ecirc;n cứu c&oacute; thể thấy con bạn hay bất kỳ đứa trẻ n&agrave;o cũng tiềm t&agrave;ng những khả năng tr&iacute; tuệ, điều quan trọng l&agrave; cần những phương ph&aacute;p ph&ugrave; hợp để mở kh&oacute;a tiềm năng đ&oacute;. Những chương tr&igrave;nh n&agrave;y sẽ gi&uacute;p ph&aacute;t huy t&iacute;nh s&aacute;ng tạo, sự linh hoạt của n&atilde;o bộ, khả năng tư duy, khả năng ng&ocirc;n ngữ, giải quyết vấn đề, từ đ&oacute; mang đến cho trẻ tr&iacute; th&ocirc;ng minh to&agrave;n diện.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>AMERICAN SKILLS (AVITS)</strong> -<strong> Hệ thống Trung T&acirc;m Đ&agrave;o tạo Kỹ Năng TO&Agrave;N DIỆN</strong>: To&aacute;n tư duy Mỹ IXL; Ph&aacute;t triển Ng&ocirc;n ngữ (tiếng Việt); Kỹ năng x&atilde; hội; Ng&ocirc;n ngữ Tiếng Anh&nbsp;d&agrave;nh cho trẻ ở lứa tuổi Mầm non, Tiểu học V&Agrave; THCS,&nbsp;giảng dạy t&iacute;ch hợp c&aacute;c m&ocirc;n học&nbsp;hướng tới sự ph&aacute;t triển to&agrave;n diện của trẻ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Trong tất cả c&aacute;c kh&oacute;a đ&agrave;o tạo, American Skills&nbsp;lu&ocirc;n cam kết hướng tới c&aacute;c mục ti&ecirc;u sau :</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&uacute;p b&eacute; ph&aacute;t triển tr&iacute; tuệ, khả năng tư duy, linh hoạt ng&ocirc;n ngữ</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&uacute;p b&eacute; h&igrave;nh th&agrave;nh v&agrave;&nbsp;r&egrave;n luyện c&aacute;c kỹ năng sống thiết yếu như l&agrave;m chủ bản th&acirc;n, l&agrave;m chủ việc học, kỹ năng giao tiếp, h&ograve;a nhập x&atilde; hội&hellip;</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nu&ocirc;i dưỡng t&acirc;m hồn, t&igrave;nh cảm của b&eacute;, dạy b&eacute; biết y&ecirc;u thương để được y&ecirc;u thương.</span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'gioi-thieu-americanskills.jpg', 0, 69, '', '2018-03-27 16:03:34', '2018-06-21 17:06:17', 1, '', '', '', '', '', 'Giới thiệu tuan', 'Học sinh tại American Skills được bồi dưỡng nhân cách, phát triển sự tự tin, kỹ năng sống là bước đệm quan trọng, cần thiết cho sự thành công của các em trong tương lai.', 'gioi-thieu-tuan', 'Toán tư duy Mỹ IXL; Phát triển Ngôn ngữ (tiếng Việt); Kỹ năng xã hội; Ngôn ngữ Tiếng Anh dành cho trẻ ở lứa tuổi Mầm non, Tiểu học VÀ THCS', 1),
(96, 'Ngọc Trang _TOEIC 820', '', '<p>Ngọc Trang lựa chọn kh&oacute;a TOEIC cho mục đ&iacute;ch ri&ecirc;ng của bản th&acirc;n, d&ugrave; rằng chỉ đặt mục ti&ecirc;u kh&aacute; khi&ecirc;m tốn với điểm TOEIC 350 cho 1 kh&oacute;a học tại AMES Ngọc Trang đ&atilde; bứt ph&aacute; ngoạn mục v&agrave; đạt được con số 820 thật sự ấn tượng.</p>\r\n', 'Le Ngoc Trang_TOEIC 820.jpg', 0, 70, '', '2018-03-27 16:03:54', '2018-04-21 16:04:48', 1, '', '', '', '', '', 'Ngọc Trang _TOEIC 820', '', 'ngoc-trang-toeic-820', '', 1),
(97, 'Avits Math<br>Tư duy toán Mỹ (IXL): 4-14 tuổi', 'Math Skills được xem là phương pháp phát triển tư duy Toán học tiên tiến của Mỹ dành cho học sinh Mầm non và Tiểu học. Math Skills hướng đến việc phát triển kỹ năng tư duy Toán học cho trẻ em ngay từ nhỏ với phương pháp cá thể hóa nhằm tối đa hoá hiệu quả và phát huy được điểm mạnh đồng thời khắc phục điểm yếu của từng học sinh.', '', '113806_math_512x512.png', 0, 72, '', '2018-03-27 22:03:21', '2018-04-16 10:04:50', 1, '', '', '', '', '', 'Avits Math<br>Tư duy toán Mỹ (IXL): 4-14 tuổi', '', 'avits-mathbrtu-duy-toan-my-ixl-4-14-tuoi', '', 1),
(98, 'Avits Smart Reading<br>Phát triển ngôn ngữ (Tiếng Việt): 4-10 tuổi', 'Chương trình Smart Reading Skills được phát triển trên nền tảng phương pháp học đọc  đa giác quan kết hợp đa cấp độ của Mỹ  đã được áp dụng tại các trường học trên khắp nước  Mỹ  cũng như tại nhiều quốc gia trên thế giới. Phương pháp này được phụ huynh ưa chuộng và lựa chọn là phương pháp học đọc sáng tạo  và hiệu quả nhất hiện nay. Smart Reading Skills được công nhận giúp trẻ phát triển trí não và khả năng ngôn ngữ.', '', 'coaching-icon.png', 0, 72, '', '2018-03-27 22:03:32', '2018-04-16 10:04:18', 1, '', '', '', '', '', 'Avits Smart Reading<br>Phát triển ngôn ngữ (Tiếng Việt): 4-10 tuổi', '', 'avits-smart-readingbrphat-trien-ngon-ngu-tieng-viet-4-10-tuoi', '', 1),
(99, 'Phương pháp đào tạo', '', '<h2 style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"phương pháp americanskills\" height=\"417\" src=\"/image/images/phuong-phap-americanskills.jpg\" width=\"556\" /></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>NHỮNG ĐIỂM MẠNH CỦA PHƯƠNG PH&Aacute;P</strong></span></span></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#333333\"><strong>Đ&agrave;o tạo theo phương ph&aacute;p c&aacute; thể ho&aacute;: </strong>Phương ph&aacute;p c&aacute; thể h&oacute;a được &aacute;p dụng từ bước khảo s&aacute;t, đ&aacute;nh gi&aacute; năng lực từng học sinh để x&aacute;c định điểm mạnh, đặc điểm tiếp thu, năng lực hiện tại cũng như những điểm yếu để c&oacute; cơ sở x&aacute;c lập kế hoạch học tập v&agrave; phương ph&aacute;p ph&ugrave; hợp nhất cho trẻ. Phương ph&aacute;p c&aacute; thể h&oacute;a được ứng dụng t&iacute;ch hợp xuy&ecirc;n suốt trong qu&aacute; học, thực hiện những điều chỉnh cần thiết để đảm bảo hiệu quả v&agrave; sự tiến bộ của trẻ. Mỗi học sinh sẽ được học theo một kế hoạch học tập ri&ecirc;ng.</span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#333333\"><strong>Vận dụng c&aacute;c kỹ thuật giảng dạy đa chiều gồm:</strong> quan s&aacute;t h&igrave;nh ảnh gi&aacute;o cụ trực quan, diễn đạt bằng ng&ocirc;n từ, suy luận tư duy logic, thực h&agrave;nh, ứng dụng. Gi&uacute;p học sinh hiểu nhanh bản chất của vấn đề, ph&aacute;t triển khả năng quan s&aacute;t, ph&aacute;n đo&aacute;n, tư duy đồng thời biết ứng dụng kiến thức đ&atilde; học v&agrave;o thực tế</span></span>.&nbsp;<span style=\"background-color:white\"><span style=\"color:#333333\">Vận dụng c&aacute;ch tiếp cận vấn đề từ bản chất, từ cơ bản đến tổng thể v&agrave; n&acirc;ng cao. Gi&uacute;p trẻ k&iacute;ch th&iacute;ch tự gi&aacute;c học v&agrave; ph&aacute;t triển tư duy.</span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#333333\"><strong>Qu&aacute; tr&igrave;nh học tập với quy tr&igrave;nh kh&eacute;p k&iacute;n: Kiểm tra đầu v&agrave;o &ndash; Đ&agrave;o tạo &ndash; Đ&aacute;nh gi&aacute;.</strong> Gi&uacute;p đ&aacute;nh gi&aacute; sự tiến bộ của học sinh trong từng kho&aacute; học. Ngo&agrave;i ra ch&uacute;ng t&ocirc;i c&ograve;n duy tr&igrave; li&ecirc;n hệ chặt chẽ với từng học sinh sau kho&aacute; học để kiểm chứng sự tiến bộ của học sinh.</span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><strong>Gi&aacute;o tr&igrave;nh do c&aacute;c chuy&ecirc;n gia đ&agrave;o tạo kỹ năng h&agrave;ng đầu của Mỹ x&acirc;y dựng:&nbsp;</strong><span style=\"background-color:white\"><span style=\"color:#333333\">Gi&aacute;o tr&igrave;nh kết hợp giữa phương ph&aacute;p ti&ecirc;n tiến của quốc tế v&agrave; điều chỉnh ph&ugrave; hợp với chương tr&igrave;nh, đặc điểm của học sinh Việt Nam.</span></span></span></span></span>\r\n	<ul>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#333333\"><span style=\"background-color:white\">Phương ph&aacute;p giảng dạy trực quan, sinh động lấy học sinh l&agrave;m chủ thể. Kết hợp với phương ph&aacute;p ABA: Applied Behavior Analysis -&nbsp;Ph&acirc;n t&iacute;ch h&agrave;nh vi ứng dụng mang đến hiệu quả học tập cho mọi học sinh.</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#333333\"><span style=\"background-color:white\">Gi&aacute;o vi&ecirc;n được đ&agrave;o tạo chuy&ecirc;n s&acirc;u</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#333333\"><span style=\"background-color:white\">M&ocirc;i trường học tập th&acirc;n thiện, chuy&ecirc;n nghiệp.</span></span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#333333\">Phương ph&aacute;p đ&agrave;o tạo của American Skills (<strong><em>Avits) </em></strong>được t&oacute;m tắt th&agrave;nh 4 chữ c&aacute;i viết tắt <strong>&ldquo;SAFE&rdquo;</strong> với &yacute; nghĩa:</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><strong><span style=\"color:#333333\">- Sequenced</span></strong><span style=\"color:#333333\">&nbsp;&ndash; Mỗi một vấn đề x&atilde; hội m&agrave; trẻ phải đối mặt sẽ c&oacute; một kỹ năng tương ứng. American Skills sẽ giải quyết&nbsp; tất cả c&aacute;c vấn đề x&atilde; hội m&agrave; trẻ em đ&atilde;, đang v&agrave; sẽ gặp phải, cung cấp cho trẻ những kỹ năng cần thiết khi tham gia v&agrave;o hoạt động x&atilde; hội. C&aacute;c kỹ năng n&agrave;y được sắp xếp theo một tr&igrave;nh tự nhất định từ đơn giản đến phức tạp, từ thấp đến cao theo h&igrave;nh th&aacute;p.<br />\r\n<strong>- Active</strong>&nbsp;&ndash; mỗi kỹ năng được giới thiệu sẽ gắn chặt với thực h&agrave;nh, học đi đ&ocirc;i với h&agrave;nh. Mục đ&iacute;ch m&agrave; American Skills muốn hướng tới l&agrave; gi&uacute;p cho c&aacute;c em biết ứng dụng những kiến thức đ&atilde; học v&agrave;o thực tế. American Skills sử dụng hệ thống mẫu biểu đ&aacute;nh gi&aacute; li&ecirc;n tục từ khi học sinh đăng k&yacute;, trong suốt qu&aacute; tr&igrave;nh học tập v&agrave; đến cả c&aacute;c giai đoạn tiếp sau n&agrave;y của học sinh.<br />\r\n<strong>- Focused</strong>&nbsp;&ndash; Mỗi một chương tr&igrave;nh sẽ chuy&ecirc;n s&acirc;u v&agrave;o một nh&oacute;m kỹ năng với mục ti&ecirc;u nhất định; Social Skills tập trung v&agrave;o việc ph&aacute;t triển kỹ năng v&agrave; tư duy x&atilde; hội của học sinh; Smart Reading Skills tập trung v&agrave;o việc ph&aacute;t triển ng&ocirc;n ngữ v&agrave; vận dụng sử dụng v&agrave;o cuộc sống; Math Skills tập trung v&agrave;o việc ph&aacute;t triển khả năng tư duy To&aacute;n học gi&uacute;p học sinh học giỏi To&aacute;n v&agrave; ph&aacute;t&nbsp; triển tư duy.<br />\r\n<strong>- Explicit</strong>&nbsp;&ndash; Kế hoạch học tập r&otilde; r&agrave;ng gởi về cho Phụ huynh ngay từ buổi học đầu ti&ecirc;n của học sinh. Việc trao đổi giữa gia đ&igrave;nh v&agrave; nh&agrave; trường được thực hiện h&agrave;ng tuần gi&uacute;p Phụ huynh nắm được c&aacute;c nội dung học tập của học sinh để c&oacute; những hỗ trợ kịp thời v&agrave; mang lại hiệu quả học tập tốt nhất. Kết hợp phương ph&aacute;p giảng dạy sử dụng gi&aacute;o cụ trực quan gi&uacute;p học sinh tiếp thu kiến thức một c&aacute;ch nhanh v&agrave; hiểu bản chất.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#333333\">Đ&acirc;y cũng ch&iacute;nh l&agrave; bốn mục ti&ecirc;u lớn m&agrave; American Skills muốn hướng tới&nbsp;</span><strong>&ldquo;SAFE&rdquo;</strong><strong>.</strong> <span style=\"color:#333333\">Với bốn mục ti&ecirc;u n&agrave;y sẽ gi&uacute;p trẻ vững tin bước tới tương lai <strong>&ldquo;Helping children write the future&rdquo;</strong></span></span></span></span></p>\r\n', 'phuong-phap-dao-tao-americanskills.jpg', 0, 69, '', '2018-03-28 08:03:44', '2018-04-19 15:04:50', 1, '', '', '', '', '', 'Phương pháp đào tạo', 'American Skills đã xây dựng một chiến lược “mục tiêu – chương trình“ có tính khoa học, hệ thống theo từng lứa tuổi, cá nhân hóa cho trẻ từ mầm non đến hết phổ thông trung học cơ sở nhằm tối ưu hóa khả năng tiếp cận với các kỹ năng cần thiết của học sinh.', 'phuong-phap-dao-tao', 'Phương pháp đào tạo', 1),
(100, 'American Skill', '', '<p><iframe frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/4J4YFw5EjF8\" width=\"560\"></iframe></p>\r\n', 'image-3-770x550.jpg', 0, 73, '', '2018-03-28 08:03:37', '2018-07-19 16:07:19', 1, '', '', '', '', '', 'American Skill', '', 'american-skill', '', 1),
(101, 'Avits Social Skills<br>Kỹ năng xã hội: 4-14 tuổi', 'Là những bậc làm cha làm mẹ, ai cũng mong mỏi con cái mình sẽ được phát triển thật cân bằng, hài hòa cả về kiến thức văn hoá lẫn xã hội. Những tưởng khi kinh tế phát triển hơn, xã hội dư thừa vật chất hơn thì không khó để đạt được điều đó; nhưng thực tế lại như đang chứng minh điều ngược lại.', '', 'icon_internships.png', 0, 72, '', '2018-03-28 14:03:24', '2018-04-16 10:04:40', 1, '', '', '', '', '', 'Avits Social Skills<br>Kỹ năng xã hội: 4-14 tuổi', '', 'avits-social-skillsbrky-nang-xa-hoi-4-14-tuoi', '', 1);
INSERT INTO `news` (`news_id`, `news_name`, `news_des`, `news_content`, `news_img`, `news_views`, `newscat_id`, `news_author`, `news_created_date`, `news_update_date`, `state`, `news_sub_info1`, `news_sub_info2`, `news_sub_info3`, `news_sub_info4`, `news_sub_info5`, `title_seo`, `des_seo`, `friendly_url`, `keyword`, `created_id`) VALUES
(102, 'Avits Math (IXL)', 'TOÁN TƯ DUY MỸ (IXL) – GIÚP TRẺ EM PHÁT TRIỂN TƯ DUY VÀ HỌC GIỎI TOÁN', '<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:#1579bd\">Avits Math (IXL) </span></span></strong><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">được xem l&agrave;&nbsp;</span></span><strong><span style=\"font-size:11.0pt\"><span style=\"color:blue\">phương ph&aacute;p ph&aacute;t triển tư duy To&aacute;n học ti&ecirc;n tiến của Mỹ d&agrave;nh cho học sinh Mầm non, Tiểu học v&agrave; THCS.</span></span></strong>&nbsp;<strong><span style=\"font-size:11.0pt\"><span style=\"color:#1579bd\">Avits Math (IXL) </span></span></strong><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">hướng đến việc ph&aacute;t triển kỹ năng tư duy To&aacute;n học cho trẻ em ngay từ nhỏ với phương ph&aacute;p c&aacute; thể h&oacute;a nhằm tối đa ho&aacute; hiệu quả v&agrave; ph&aacute;t huy được điểm mạnh đồng thời khắc phục điểm yếu của từng học sinh.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><br />\r\n<span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Nghi&ecirc;n cứu của Trường Đại Học Harvard- Mỹ năm 2006 cho thấy, trẻ em được tiếp cận với kỹ năng tư duy To&aacute;n ngay từ tuổi Mầm non sẽ c&oacute; khả năng tiếp cận với tất cả c&aacute;c m&ocirc;n học tại trường Tiểu học tốt hơn c&aacute;c bạn c&ugrave;ng trang lứa chưa được tiếp cận với kỹ năng n&agrave;y. Nghi&ecirc;n cứu cũng chỉ ra rằng, trẻ em được tiếp cận với kỹ năng tư duy To&aacute;n trước 6 tuổi sẽ c&oacute; khả năng tập trung, khả năng quan s&aacute;t, ph&acirc;n t&iacute;ch vấn đề v&agrave; tư duy logic tốt hơn so với c&aacute;c trẻ em kh&aacute;c.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><br />\r\n<span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Theo kết quả khảo s&aacute;t của&nbsp;</span></span><strong><span style=\"font-size:11.0pt\"><span style=\"color:blue\">Bộ Gi&aacute;o dục Mỹ</span></span></strong><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">&nbsp;cũng cho thấy:</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><br />\r\n<span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">- Học sinh học giỏi To&aacute;n cũng c&oacute; khả năng học giỏi c&aacute;c m&ocirc;n học kh&aacute;c. Tỷ lệ n&agrave;y l&agrave; 38%.<br />\r\n- Học sinh c&oacute; khả năng tư duy To&aacute;n tốt c&oacute; khả năng đỗ v&agrave;o c&aacute;c Trường Đại học cao hơn c&aacute;c học sinh kh&aacute;c đồng thời những sinh vi&ecirc;n n&agrave;y ra trường c&oacute; tỷ lệ t&igrave;m được c&ocirc;ng việc tốt với mức lương cao hơn c&aacute;c sinh vi&ecirc;n kh&aacute;c kh&ocirc;ng giỏi To&aacute;n. Tỷ lệ n&agrave;y l&agrave; 42%.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><br />\r\n<span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Thực tế cho thấy trẻ em Việt Nam c&oacute; tố chất học giỏi m&ocirc;n To&aacute;n. Tuy nhi&ecirc;n, phương ph&aacute;p gi&aacute;o dục hiện tại mới chỉ dừng lại ở việc học nhớ, học vẹt v&agrave; l&agrave;m nhiều th&agrave;nh quen m&agrave; kh&ocirc;ng vận dụng kỹ năng tư duy logic, quan s&aacute;t, ph&acirc;n t&iacute;ch vấn đề. American Skills x&acirc;y dựng chương tr&igrave;nh </span></span><strong><span style=\"font-size:11.0pt\"><span style=\"color:#1579bd\">Avits Math (IXL) </span></span></strong><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">với mong muốn mang đến cho trẻ em Việt Nam kiến thức nền tảng về To&aacute;n học &ndash; th&ocirc;ng qua việc hiểu bản chất vấn đề, đồng thời luyện tập khả năng tư duy logic v&agrave; ph&acirc;n t&iacute;ch vấn đề m&agrave; kh&ocirc;ng cần học nhớ, học vẹt.</span></span></span></span></span></p>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:blue\">PHƯƠNG PH&Aacute;P Đ&Agrave;O TẠO</span></span></strong></span></span></span></h2>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">Đ&agrave;o tạo theo&nbsp;</span></span><strong><span style=\"color:blue\">phương ph&aacute;p c&aacute; thể ho&aacute;</span></strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">&nbsp;&ndash; mỗi học sinh sẽ được học theo một kế hoạch học tập ri&ecirc;ng.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">Vận dụng c&aacute;c&nbsp;</span></span><strong><span style=\"color:blue\">kỹ thuật giảng dạy đa chiều</span></strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">&nbsp;gồm: quan s&aacute;t h&igrave;nh ảnh gi&aacute;o cụ trực quan -&gt; diễn đạt bằng ng&ocirc;n từ -&gt; suy luận tư duy logic-&gt; to&aacute;n viết -&gt; ứng dụng.Gi&uacute;p học sinh hiểu nhanh bản chất của vấn đề, ph&aacute;t triển khả năng quan s&aacute;t, ph&aacute;n đo&aacute;n, tư duy đồng thời biết ứng dụng to&aacute;n v&agrave;o thực tế.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">Vận dụng c&aacute;ch tiếp cận vấn đề đi từ bản chất, từ c&aacute;i cơ bản đến tổng thể v&agrave; n&acirc;ng cao với 3 bộ b&agrave;i tập: r&egrave;n luyện kỹ năng t&iacute;nh to&aacute;n, To&aacute;n c&oacute; lời giải, To&aacute;n tư duy tổng hợp. Gi&uacute;p trẻ thấu hiểu to&aacute;n, y&ecirc;u th&iacute;ch học to&aacute;n v&agrave; ph&aacute;t triển tư duy.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">Qu&aacute; tr&igrave;nh học tập với quy tr&igrave;nh kh&eacute;p k&iacute;n:&nbsp;</span></span><strong><span style=\"color:blue\">Kiểm tra đầu v&agrave;o- Đ&agrave;o tạo- Kiểm tra cuối kho&aacute;</span></strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">. Gi&uacute;p đ&aacute;nh gi&aacute; sự tiến bộ của học sinh trong từng kho&aacute; học.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:blue\">Gi&aacute;o tr&igrave;nh</span></strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">&nbsp;do c&aacute;c chuy&ecirc;n gia To&aacute;n học của American Skills x&acirc;y dựng c&oacute; sự điều chỉnh cho&nbsp;</span></span><em>ph&ugrave; hợp với chương tr&igrave;nh To&aacute;n của Việt Nam</em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:blue\">.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">Phương ph&aacute;p giảng dạy trực quan, sinh động lấy học sinh l&agrave;m chủ thể.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">LƠI &Iacute;CH SAU KH&Oacute;A HỌC</span></span></strong></span></span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">&nbsp; &nbsp; Gi&uacute;p học sinh hiểu bản chất vấn đề v&agrave; biết ứng dụng To&aacute;n v&agrave;o thực tế.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">&nbsp;&nbsp;&nbsp; Gi&uacute;p trẻ em y&ecirc;u th&iacute;ch học to&aacute;n, c&oacute; &yacute; thức tự gi&aacute;c trong học tập.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">&nbsp;&nbsp;&nbsp; Gi&uacute;p c&aacute;c em học tốt M&ocirc;n To&aacute;n v&agrave; c&aacute;c m&ocirc;n học kh&aacute;c tr&ecirc;n trường.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">&nbsp;&nbsp;&nbsp; Gi&uacute;p trẻ em ph&aacute;t triển tư duy to&agrave;n diện.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">&nbsp;&nbsp;&nbsp; Gi&uacute;p c&aacute;c em ho&agrave;n thiện bản th&acirc;n v&agrave; l&agrave;m chủ cuộc sống.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</h2>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">CHƯƠNG TR&Igrave;NH TIỀN TIỂU HỌC : (D&agrave;nh cho trẻ từ 4-6 tuổi)</span></span></strong></span></span></span></h2>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">avits MATH FOR PRE- SCHOOL: </span></span><span style=\"font-size:11.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">trang bị cho trẻ những kiến thức nền tảng của tư duy to&aacute;n học, tiếp cận v&agrave; ph&aacute;t triển tư duy số, khơi dậy niềm hứng th&uacute; với m&ocirc;n To&aacute;n tr&ecirc;n cơ sở thấu hiểu bản chất của c&aacute;c kh&aacute;i niệm to&aacute;n v&agrave; sự ứng dụng của to&aacute;n để giải quyết vấn đề thế giới thực.</span></span></span><br />\r\n<span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Với 3 cấp độ: Starter =&gt; K =&gt; SC</span></span></strong></span></span></span></h2>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">GỒM C&Aacute;C CHỦ ĐỀ:</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Color - M&agrave;u sắc</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Ngo&agrave;i &nbsp;việc &nbsp;nhận &nbsp;biết, &nbsp;ph&acirc;n &nbsp;biệt &nbsp;v&agrave; &nbsp;đọc &nbsp;t&ecirc;n &nbsp;m&agrave;u sắc c&aacute;c em c&ograve;n biết th&ocirc;ng qua m&agrave;u sắc ph&acirc;n biệt sự giống nhau v&agrave; kh&aacute;c nhau của sự vật, hiện tượng. T&igrave;m ra quy luật của sự vận động m&agrave;u sắc trong tự nhi&ecirc;n.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Material - Chất liệu</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Chất liệu l&agrave; một thuộc t&iacute;nh quan trọng gi&uacute;p trẻ cảm nhận được c&aacute;c đồ vật, ph&acirc;n biệt đặc t&iacute;nh của c&aacute;c đồ &nbsp;vật. &nbsp;R&egrave;n &nbsp;luyện &nbsp;kỹ &nbsp;năng tư &nbsp;duy &nbsp;của &nbsp;trẻ &nbsp;về &nbsp;sự giống v&agrave; kh&aacute;c nhau của c&aacute;c đồ vật th&ocirc;ng qua chất liệu. Qua đ&oacute; gi&uacute;p c&aacute;c em thấy được sự đa dạng của cuộc sống.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Size - K&iacute;ch thước</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Trẻ bước đầu được l&agrave;m quen với k&iacute;ch cỡ to/ nhỏ, ngắn/d&agrave;i. &nbsp;Biết &nbsp;so &nbsp;s&aacute;nh &nbsp;hơn/nhất. &nbsp;Ban &nbsp;đầu &nbsp;h&igrave;nh th&agrave;nh tư duy ước lượng v&agrave; tỷ lệ.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Shape - H&igrave;nh dạng</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">H&igrave;nh &nbsp;dạng &nbsp;l&agrave; &nbsp;kh&aacute;i &nbsp;niệm &nbsp;ban &nbsp;đầu &nbsp;của &nbsp;tư &nbsp;duy &nbsp;về h&igrave;nh học. Qua chủ để n&agrave;y c&aacute;c em kh&ocirc;ng chỉ dừng lại ở việc nhận biết h&igrave;nh dạng m&agrave; c&ograve;n biết được đặc điểm của c&aacute;c dạng h&igrave;nh, so s&aacute;nh sự giống v&agrave; kh&aacute;c nhau của h&igrave;nh. Gi&uacute;p c&aacute;c em ph&aacute;t triển khả năng quan s&aacute;t, ph&acirc;n t&iacute;ch v&agrave; tư duy về h&igrave;nh.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Classify - Ph&acirc;n loại</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Ph&acirc;n loại l&agrave; chủ đề tư duy tổng hợp của c&aacute;c chủ đề tr&ecirc;n. Qua chủ đề n&agrave;y học sinh được ứng dụng c&aacute;c kiến thức đ&atilde; học v&agrave;o c&aacute;c dạng b&agrave;i tập ph&acirc;n t&iacute;ch v&agrave; tư duy tổng hợp. Gi&uacute;p học sinh r&egrave;n luyện khả năng tư duy về h&igrave;nh v&agrave; ph&aacute;t triển khả năng qua s&aacute;t nhạy b&eacute;n.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Positions - Vị tr&iacute;</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Qua chủ đề n&agrave;y học sinh c&oacute; được những kh&aacute;i niệm ban &nbsp;đầu &nbsp;về &nbsp;kh&ocirc;ng &nbsp;gian &nbsp;v&agrave; &nbsp;l&agrave; &nbsp;tiền &nbsp;đề &nbsp;cho &nbsp;h&igrave;nh &nbsp;học kh&ocirc;ng gian sau n&agrave;y. Tư duy về vị tr&iacute; trong kh&ocirc;ng gian gi&uacute;p &nbsp;trẻ &nbsp;ph&aacute;t &nbsp;triển &nbsp;khả &nbsp;năng &nbsp;tưởng &nbsp;tượng &nbsp;v&agrave; &nbsp;định hướng trong kh&ocirc;ng gian tốt.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Patterns - Quy luật</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">L&agrave; chủ đề điển h&igrave;nh của kỹ năng tư duy của bộ n&atilde;o. Chủ đề n&agrave;y gi&uacute;p r&egrave;n luyện chỉ số IQ của trẻ, gi&uacute;p trẻ ph&aacute;t triển khả năng quan s&aacute;t, ph&acirc;n t&iacute;ch, ph&aacute;n đo&aacute;n, suy luận logic.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Numbers and counting - Tư duy số</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Tư duy số l&agrave; chủ đề kh&ocirc;ng thể thiếu gi&uacute;p trẻ học giỏi To&aacute;n. Ngo&agrave;i việc gi&uacute;p trẻ nhận diện v&agrave; viết số, th&ocirc;ng qua &nbsp;c&aacute;c &nbsp;b&agrave;i &nbsp;tập &nbsp;ứng dụng &nbsp;sẽ &nbsp;gi&uacute;p &nbsp;c&aacute;c &nbsp;em &nbsp;hiểu &nbsp;bản chất của số, vị tr&iacute; của số tr&ecirc;n trục số, khoảng c&aacute;ch của c&aacute;c số. Học đếm sẽ gi&uacute;p trẻ tăng khả năng tập trung, ph&aacute;t triển tr&iacute; n&atilde;o v&agrave; h&igrave;nh th&agrave;nh nếp nhăn của n&atilde;o bộ.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Sorting, ordering, and classifying - Sắp xếp, thứ tự, ph&acirc;n nh&oacute;m</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Khi đ&atilde; hiểu bản chất của c&aacute;c số c&aacute;c em sẽ được r&egrave;n luyện sắp xếp thứ tự c&aacute;c số theo y&ecirc;u cầu. L&agrave;m quen với việc ph&acirc;n nh&oacute;m theo số lượng v&agrave; hiểu được bản chất của nh&oacute;m. Tư duy về nh&oacute;m l&agrave; một dạng tư duy ho&agrave;n to&agrave;n mới m&agrave; từ trước đến nay c&aacute;c học sinh &iacute;t được tiếp cận. Tư duy nh&oacute;m gi&uacute;p c&aacute;c em giải quyết được rất nhiều dạng b&agrave;i tập sau n&agrave;y v&agrave; thậm ch&iacute; cả c&aacute;c b&agrave;i to&aacute;n k&iacute;nh tế, x&atilde; hội cũng như tiếp cận c&aacute;c m&ocirc;n học kh&aacute;c dễ d&agrave;ng hơn.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Fractions - Ph&acirc;n số</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">L&agrave; một chủ đề ho&agrave;n to&agrave;n mới với c&aacute;c em học sinh ở tuổi Mầm mon. Nhưng với c&aacute;ch tiếp cận thực tế v&agrave; trực quan th&ocirc;ng qua việc chia đồ vật, chia h&igrave;nh chương tr&igrave;nh gi&uacute;p c&aacute;c em hiểu bản chất của ph&acirc;n số v&agrave; t&igrave;m được gi&aacute; trị của ph&acirc;n số một c&aacute;ch dễ d&agrave;ng.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Measurement - Đo lường</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Gi&uacute;p c&aacute;c em c&oacute; kh&aacute;i niệm ban đầu về k&iacute;ch thước bằng c&aacute;ch so s&aacute;nh với c&aacute;c vật kh&aacute;c. Gi&uacute;p c&aacute;c em ph&aacute;t triển tư duy về tỷ lệ v&agrave; khả năng ước lượng. Ngo&agrave;i ra, c&aacute;c em c&ograve;n biết so s&aacute;nh c&aacute;c nh&oacute;m vật với nhau như: d&agrave;i hơn/ d&agrave;i nhất, ngắn hơn/ ngắn nhất, thấp hơn/ thấp nhất,&hellip;</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Time - Tư duy thời gian</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Thời gian l&agrave; nh&acirc;n tố v&ocirc; c&ugrave;ng quan trọng với cuộc sống của mỗi người. Biết tư duy về thời gian sẽ gi&uacute;p trẻ biết sắp xếp kế hoạch học tập v&agrave; vui chơi ph&ugrave; hợp. Hiểu về sự vận động của thời gian sẽ gi&uacute;p c&aacute;c em c&oacute; &yacute; thức v&agrave; biết c&aacute;ch sử dụng thời gian tốt hơn v&agrave; hiệu quả hơn.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Comparing - So s&aacute;nh</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Qua chủ đề n&agrave;y gi&uacute;p c&aacute;c em hiểu được mối tương quan giữa c&aacute;c sự vật v&agrave; hiện tượng qua đ&oacute; c&aacute;c em thấy được quy luật của To&aacute;n học. Đ&acirc;y l&agrave; một chủ đề tư duy tổng hợp của phần tư duy số. Để giải quyết được c&aacute;c dạng b&agrave;i tập trong chủ đề n&agrave;y c&aacute;c em phải c&oacute; tư duy tốt về lượng, nh&oacute;m, về tương quan tỷ lệ.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Addition - Ph&eacute;p cộng</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Ph&eacute;p cộng l&agrave; chủ đề kh&ocirc;ng thể thiếu trong To&aacute;n học. Math Skills kh&ocirc;ng ch&uacute; trọng đến kết quả của ph&eacute;p t&iacute;nh m&agrave; quan t&acirc;m đến phương ph&aacute;p để đi đến kết quả đ&oacute;. Với phương ph&aacute;p tư duy số kh&ocirc;ng chỉ gi&uacute;p c&aacute;c em t&iacute;nh nhẩm nhanh, nhẩm đ&uacute;ng m&agrave; c&ograve;n gi&uacute;p ph&aacute;t triển tư duy v&agrave; tr&iacute; n&atilde;o.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Subtraction - Ph&eacute;p trừ</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Ph&eacute;p trừ l&agrave; dạng ngược lại của ph&eacute;p cộng. &nbsp;Tư duy ngược đ&ocirc;i khi kh&oacute; hơn tư duy xu&ocirc;i. Với phương ph&aacute;p tư duy Math Skills gi&uacute;p tr&iacute; n&atilde;o của c&aacute;c em hoạt động đa chiều v&agrave; linh hoạt.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Proportional thinking - Tư duy tỷ lệ</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Đ&acirc;y l&agrave; đỉnh cao của tư duy số. B&agrave;i tập tư duy tỷ lệ gi&uacute;p r&egrave;n luyện khả năng tư duy tổng hợp, khả năng định lượng v&agrave; tư duy nh&oacute;m.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">1. Cấp độ Starter : </span></span></strong><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">sẽ bắt đầu được tiếp cận dần với c&aacute;c nội dung To&aacute;n căn bản. Bắt đầu với c&aacute;c nội dung nền tảng: ph&acirc;n biệt h&igrave;nh dạng, m&agrave;u sắc, chất liệu, con số để dần dần y&ecirc;u th&iacute;ch việc tương t&aacute;c với con số. Từ đ&oacute; c&aacute;c b&eacute; sẽ y&ecirc;u th&iacute;ch dần m&ocirc;n To&aacute;n học tưởng chừng như rất kh&ocirc; khan.</span></span></span><br />\r\n<span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\"><span style=\"background-color:white\">Khi đ&atilde; y&ecirc;u th&iacute;ch m&ocirc;n học, trẻ sẽ tiếp tục được kh&aacute;m ph&aacute; c&aacute;c kiến thức To&aacute;n n&acirc;ng cao hơn: Đếm &ndash; viết, sắp xếp thứ tự c&aacute;c con số. Dần dần từ đ&oacute; mới tiếp cận đến c&aacute;c ph&eacute;p t&iacute;nh cộng v&agrave; trừ. &nbsp;Đ&acirc;y cũng ch&iacute;nh l&agrave; nền tảng để ph&aacute;t triển tư duy v&agrave;o lứa tuổi n&agrave;y cho trẻ.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">2. Cấp độ Kindergarten&nbsp;(K) : </span></span></strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">được x&acirc;y dựng đặc th&ugrave; d&agrave;nh cho trẻ từ 5 đến 6 tuổi.<br />\r\n&nbsp;<br />\r\nTrẻ được l&agrave;m quen, kh&aacute;m ph&aacute; c&aacute;c con số th&ocirc;ng qua c&aacute;c nội dung: số liền trước - số liền sau, đếm xu&ocirc;i - đếm ngược, đơn vị&hellip; để hiểu được thứ tự v&agrave; bản chất c&aacute;c con số. Trẻ được t&igrave;m hiểu th&ecirc;m về c&aacute;c h&igrave;nh học: h&igrave;nh oval, h&igrave;nh thoi, h&igrave;nh 5 cạnh đồng thời biết chia h&igrave;nh, gh&eacute;p h&igrave;nh, s&aacute;ng tạo h&igrave;nh để tạo ra c&aacute;c h&igrave;nh mới. Thay v&igrave; trẻ học c&aacute;ch cộng, trừ &ldquo;một c&aacute;ch m&aacute;y m&oacute;c&rdquo; bằng việc nhớ ph&eacute;p t&iacute;nh, trẻ tại IXL được học bản chất của c&aacute;c ph&eacute;p t&iacute;nh qua nhiều c&aacute;ch tiếp cận kh&aacute;c nhau. VD: C&oacute; 2 nh&oacute;m số lượng kh&aacute;c nhau, cần th&ecirc;m v&agrave;o bao nhi&ecirc;u h&igrave;nh để 2 nh&oacute;m bằng nhau. Đ&acirc;y l&agrave; nền tảng gi&uacute;p trẻ biết c&aacute;ch tư duy đ&uacute;ng về To&aacute;n học. V&agrave; khi đ&atilde; hiểu điều nay, trẻ sẽ rất th&iacute;ch học m&ocirc;n To&aacute;n.<br />\r\n&nbsp;<br />\r\n<strong>3. Cấp độ Starter School (SC) : </strong>được x&acirc;y dựng đặc th&ugrave; d&agrave;nh cho trẻ&nbsp;<strong>đang chuẩn bị v&agrave;o lớp 1.</strong><br />\r\nThời điểm n&agrave;y rất quan trọng đối với trẻ v&igrave; đ&acirc;y l&agrave; giai đoạn chuyển tiếp m&ocirc;i trường học tập từ mầm non l&ecirc;n tiểu học.<br />\r\n&nbsp;<br />\r\nTrẻ sẽ được tiếp cận với c&aacute;c kiến thức to&aacute;n một c&aacute;ch logic hơn. Thay v&igrave; học ngay c&aacute;c ph&eacute;p cộng, ph&eacute;p trừ, trẻ vẫn sẽ được tiếp cận c&aacute;c nội dung nền tảng từ quy luật, h&igrave;nh học... V&agrave; dần dần l&agrave;m quen với c&aacute;c kh&aacute;i niệm to&aacute;n học: trục số, đếm c&aacute;ch, t&igrave;m khoảng c&aacute;ch giữa c&aacute;c số.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">&nbsp;<br />\r\n<em>Với chương tr&igrave;nh Tư duy To&aacute;n&nbsp;Mỹ - Avits Math (IXL) - </em></span></span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">FOR PRE- SCHOOL</span></span><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">, trẻ kh&ocirc;ng chỉ thực hiện đ&uacute;ng theo y&ecirc;u cầu, trẻ cần phải giải th&iacute;ch được qu&aacute; tr&igrave;nh đi đến kết quả quả, v&igrave; sao lại l&agrave;m được như vậy để thực h&agrave;nh c&aacute;ch diễn đạt suy nghĩ của m&igrave;nh. C&aacute;c hoạt động thực h&agrave;nh v&agrave; l&agrave;m quen kiến thức được đan xen với nhau trong qu&aacute; tr&igrave;nh học gi&uacute;p trẻ hứng th&uacute; với m&ocirc;n học, tự tin hơn với những b&agrave;i To&aacute;n ở trường. Quan trọng hơn, trẻ được cung cấp c&aacute;c kiến thức rất nền tảng một c&aacute;ch hệ thống v&agrave; hiểu s&acirc;u về bản chất, gi&uacute;p trẻ tư duy v&agrave; ứng dụng v&agrave;o những kiến thức học ở cấp cao hơn.</span></span></em><br />\r\n<span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">&nbsp;<br />\r\n<em>Điều đặc biệt của Chương tr&igrave;nh Avits Math (IXL)</em></span></span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\"> FOR PRE- SCHOOL</span></span><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\"> l&agrave; lu&ocirc;n c&oacute; hoạt động s&aacute;ng tạo mang t&iacute;nh ứng dụng của nội dung vừa học v&agrave;o thực tế. Khi trẻ biết c&aacute;ch vận dụng những điều m&igrave;nh vừa học v&agrave;o cuộc sống, trẻ hiểu bản chất kiến thức v&agrave; sẽ nhớ rất l&acirc;u nội dung học đ&oacute;. Từ đ&oacute; l&agrave;m nền tảng để ph&aacute;t triển tư duy, tr&iacute; th&ocirc;ng minh v&agrave; t&iacute;nh s&aacute;ng tạo của trẻ lứa tuổi n&agrave;y.</span></span></em></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">CHƯƠNG TR&Igrave;NH TIỂU HỌC </span></span></strong></span></span></span></h2>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">avits math (IXL) - FOR PRIMARY SCHOOL :</span></span></strong></span></span></span></h2>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">D&agrave;nh cho học sinh tiểu học (6-10 tuổi)&nbsp;được thiết kế nội dung song song với kiến thức to&aacute;n tr&ecirc;n trường của học sinh gi&uacute;p bổ trợ c&aacute;c kiến thức cần thiết cho học sinh. Ngo&agrave;i ra Avits Math c&ograve;n bổ sung một số dạng to&aacute;n quốc tế mang t&iacute;nh ứng dụng cao gi&uacute;p c&aacute;c em ph&aacute;t triển khả năng tư duy, biết ứng dụng to&aacute;n v&agrave;o thực tế.</span></span><br />\r\n<br />\r\n<span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Với phương ph&aacute;p tiệp cận vấn đề To&aacute;n ho&agrave;n to&agrave;n mới v&agrave; khoa học, đi từ dễ đến kh&oacute;, đi từ cơ bản đến phức tạp. Avits Math </span></span><span style=\"font-size:11.0pt\"><span style=\"color:black\">(IXL) </span></span><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">gi&uacute;p học sinh hiểu bản chất vấn đề, c&oacute; kiến thức To&aacute;n vững chắc v&agrave; kh&ocirc;ng c&ograve;n cảm thấy &aacute;p lực với m&ocirc;n To&aacute;n.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><br />\r\n<span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Nội dung Chương tr&igrave;nh được thiết kế với 5 cấp độ:</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Level 1 &ndash; Level 2 &ndash; Level 3 &ndash; Level 4 &ndash; Level 5</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">1. Tư duy số v&agrave; c&aacute;c ph&eacute;p to&aacute;n (<em>Number sense &amp; operations</em>)</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">Hiểu r&otilde; c&aacute;c kh&aacute;i niệm, bản chất về số tự nhi&ecirc;n, cấu tạo số l&agrave;m tiền đề để r&egrave;n luyện kỹ năng t&iacute;nh to&aacute;n v&agrave; ph&aacute;t triển khả năng t&iacute;nh nhẩm, t&iacute;nh nhanh v&agrave; tư duy logic cho trẻ. Hiểu r&otilde; bản chất của c&aacute;c ph&eacute;p to&aacute;n cộng, trừ, nh&acirc;n, chia để đưa ra phương ph&aacute;p t&iacute;nh&nbsp; to&aacute;n ph&ugrave; hợp v&agrave; cho kết quả nhanh, ch&iacute;nh x&aacute;c nhất.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">2.Ph&acirc;n số (<em>Fractions</em>)</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">Giới thiệu về ph&acirc;n số cho học sinh hiểu r&otilde; kh&aacute;i niệm &ldquo;ph&acirc;n số l&agrave; g&igrave;?&rdquo; từ những h&igrave;nh ảnh trực quan. Khi nắm chắc được kh&aacute;i niệm, hiểu được bản chất của ph&acirc;n số học sinh sẽ thực hiện ph&eacute;p t&iacute;nh li&ecirc;n quan đến ph&acirc;n số một c&aacute;ch dễ d&agrave;ng v&agrave; ứng dụng th&agrave;nh thạo trong c&aacute;c b&agrave;i to&aacute;n đố li&ecirc;n quan.<br />\r\n&nbsp;</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">3. Đo lường &ndash; Thời gian (<em>Measurement - Time</em>)</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">Trẻ nắm vững c&aacute;c đơn vị đo lường </span></span><span style=\"background-color:#f9f9f9\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#3b3b3b\">- thời gian, độ d&agrave;i, diện t&iacute;ch, thể t&iacute;ch, khối lượng, dung t&iacute;ch v&agrave; tiền tệ.</span></span></span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">, biết &aacute;p dụng v&agrave;o c&aacute;c b&agrave;i to&aacute;n thực tế.<br />\r\n&nbsp;</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">4. H&igrave;nh học (<em>Geometry</em>)</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">Gi&uacute;p trẻ &nbsp;nhận biết kh&aacute;i niệm, thuộc t&iacute;nh, h&igrave;nh th&agrave;nh của mỗi loại h&igrave;nh kh&aacute;c nhau; mối tương quan giữa c&aacute;c h&igrave;nh. Từ đ&oacute;, trẻ &aacute;p dụng t&iacute;nh diện t&iacute;ch, chu vi hay thể t&iacute;ch của c&aacute;c h&igrave;nh kh&aacute;c nhau.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">5. Tư duy tổng hợp (<em>Mathematical Thinking</em>) </span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">Gi&uacute;p trẻ r&egrave;n luyện nhiều kỹ năng tư duy to&aacute;n tổng hợp th&ocirc;ng qua c&aacute;c dạng to&aacute;n đa dạng như c&acirc;n thăng bằng, to&aacute;n quy luật, tư duy nh&oacute;m, tư duy tỷ lệ, to&aacute;n đố, tư duy l&ocirc;gic, lập v&agrave; đọc biểu đồ, to&aacute;n x&aacute;c suất, l&agrave;m tr&ograve;n, ước lượng &hellip; Trẻ được r&egrave;n luyện nhiều kỹ năng như: đọc đề, ph&acirc;n t&iacute;ch đề b&agrave;i, &nbsp;chia nhỏ c&aacute;c vấn đề trong b&agrave;i to&aacute;n, vận dụng khả năng tư duy logic để việc giải to&aacute;n trở n&ecirc;n đơn giản, hiệu quả v&agrave; ch&iacute;nh x&aacute;c.</span></span></span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">&nbsp;6. </span></span><strong><span style=\"background-color:#f9f9f9\"><span style=\"color:black\">Giải to&aacute;n c&oacute; lời văn (<em>Problem solving )</em></span></span></strong><br />\r\n<span style=\"background-color:#f9f9f9\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#3b3b3b\">R&egrave;n luyện c&aacute;c bước gồm: Chia vấn đề th&agrave;nh những phần nhỏ, đơn giản hơn, rồi ph&acirc;n t&iacute;ch, giải to&aacute;n, kiểm tra v&agrave; đ&aacute;nh gi&aacute;.</span></span></span><br />\r\n<span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">&nbsp;</span></span></span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">CHƯƠNG TR&Igrave;NH thcs </span></span></strong></span></span></span></h2>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">avits math (IXL) - junior high school</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">Kết hợp v&agrave; n&acirc;ng cao giữa l&yacute; thuyết v&agrave; c&aacute;c b&agrave;i to&aacute;n ứng dụng, ph&aacute;t triển cho học sinh kỹ năng tư duy, sự hiểu biết v&agrave; th&aacute;i độ tự tin. Nắm chắc kiến thức từ cơ bản đến n&acirc;ng cao.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">-&nbsp;T&iacute;ch cực ho&aacute; hoạt động học tập của học sinh, r&egrave;n luyện khả năng tự học, ph&aacute;t hiện v&agrave; giải quyết vấn đề của HS nhằm h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển ở HS tư duy t&iacute;ch cực, độc lập v&agrave; s&aacute;ng tạo.</span></span></span><br />\r\n<span style=\"font-size:10.5pt\"><span style=\"color:#333333\"><span style=\"background-color:white\">&minus; Chọn lựa sử dụng những phương ph&aacute;p ph&aacute;t huy t&iacute;nh t&iacute;ch cực chủ động của Học Sinh trong học tập v&agrave; ph&aacute;t huy khả năng tự học. Hoạt động ho&aacute; việc học tập của HS bằng những dẫn dắt cho HS tự th&acirc;n trải nghiệm chiếm lĩnh tri thức, chống lối học thụ động.</span><br />\r\n<span style=\"background-color:white\">&minus; Tận dụng ưu thế của từng phương ph&aacute;p dạy học, ch&uacute; trọng sử dụng phương ph&aacute;p dạy học ph&aacute;t hiện v&agrave; giải quyết vấn đề.</span><br />\r\n<span style=\"background-color:white\">&minus; Coi trọng cung cấp kiến thức, r&egrave;n luyện kĩ năng vận dụng kiến thức v&agrave;o thực tiễn.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Nội dung Chương tr&igrave;nh được thiết kế với 4 cấp độ:</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Level 6 &ndash; Level 7 &ndash; Level 8 &ndash; Level 9</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol>\r\n			<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">Số học : T&iacute;nh chia hết, số nguy&ecirc;n tố, số ch&iacute;nh phương, bội v&agrave; ước c&aacute;c số .</span></span></span></span></span></li>\r\n			<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">Đại số : Gi&uacute;p học sinh thực hiện th&agrave;nh thạo tất cả c&aacute;c dạng to&aacute;n đại số nền tảng như tỷ lệ thức, biểu thức, ph&acirc;n thức đến c&aacute;c kh&aacute;i niệm n&acirc;ng cao như biến số, h&agrave;m số hay giải c&aacute;c dạng to&aacute;n đại số đặc trưng như phương tr&igrave;nh, bất phương tr&igrave;nh, hệ phương tr&igrave;nh, căn thức. </span></span></span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:13.5pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">Ngo&agrave;i ra, học sinh được tiếp cận với dạng ph&acirc;n t&iacute;ch dữ liệu từ những thao t&aacute;c đơn giản như đọc dữ liệu, so s&aacute;nh v&agrave; sắp xếp dữ liệu. B&ecirc;n cạnh đ&oacute;, học sinh c&ograve;n biết c&aacute;ch biểu diễn c&aacute;c dữ liệu bằng đồ thị từ đ&oacute; suy luận t&igrave;m ra đ&aacute;p &aacute;n cũng như x&acirc;y dựng c&aacute;c qui luật logic</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:13.5pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em><span style=\"font-size:11.0pt\"><span style=\"color:#4f4f4f\">Cũng cố v&agrave; bổ sung c&aacute;c kiến thức về chương tr&igrave;nh đại số của BGD&amp;ĐT từ lớp 6 đến lớp 9.</span></span></em></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol start=\"3\">\r\n			<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">H&igrave;nh học : Gi&uacute;p học sinh được l&agrave;m quen về tất cả c&aacute;c h&igrave;nh phẳng trong chương tr&igrave;nh phổ th&ocirc;ng v&agrave; c&aacute;c b&agrave;i to&aacute;n li&ecirc;n quan đến h&igrave;nh phẳng như chu vi, diện t&iacute;ch. C&aacute;c h&igrave;nh kh&ocirc;ng gian cũng được đưa v&agrave;o bằng c&aacute;c m&ocirc; h&igrave;nh trực quan th&uacute; vị nhằm gi&uacute;p học sinh ph&acirc;n biệt c&aacute;c h&igrave;nh phẳng, h&igrave;nh khối. Đồng thời c&aacute;c em c&oacute; kh&aacute;i niệm về thể t&iacute;ch, diện t&iacute;ch xung quanh, diện t&iacute;ch to&agrave;n phần. Đ&acirc;y l&agrave; nền tảng để c&aacute;c em học tốt chương tr&igrave;nh phổ th&ocirc;ng v&agrave; &aacute;p dụng kiễn thức v&agrave;o thực tiễn.</span></span></span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:13.5pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">C&aacute;c chủ đề c&aacute;c em đều được l&agrave;m quen với dạng b&agrave;i to&aacute;n giải quyết vấn đề ở những mức độ kh&aacute;c nhau. Từ những b&agrave;i to&aacute;n c&oacute; lời văn đơn giản đến c&aacute;c b&agrave;i to&aacute;n nhiều bước, lập sơ đồ giải to&aacute;n. Giải quyết vấn đề gi&uacute;p ph&aacute;t triển tư duy logic v&agrave; s&aacute;ng tạo để giải quyết c&aacute;c vấn đề trong cuộc sống. </span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:13.5pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em><span style=\"font-size:11.0pt\"><span style=\"color:#4f4f4f\">Cũng cố v&agrave; bổ sung c&aacute;c kiến thức về chương tr&igrave;nh h&igrave;nh học của BGD&amp;ĐT từ lớp 6 đến lớp 9.</span></span></em></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11.0pt\"><span style=\"color:black\">4. Luyện thi theo y&ecirc;u cầu : </span></span></span></span></span></p>\r\n\r\n<h4 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:whitesmoke\"><span style=\"font-family:Cambria,serif\"><span style=\"color:#4f81bd\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#4f4f4f\">Luyện c&aacute;c loại đề thi của BGD&amp;ĐT, c&aacute;c chương tr&igrave;nh thi v&agrave;o c&aacute;c trường chuy&ecirc;n&hellip;..</span></span></em></strong></span></span></span></span></h4>\r\n\r\n<h4 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:whitesmoke\"><span style=\"font-family:Cambria,serif\"><span style=\"color:#4f81bd\"><strong><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#4f4f4f\">Luy&ecirc;n chương tr&igrave;nh của c&aacute;c trường Quốc tế..</span></span></em></strong></span></span></span></span></h4>\r\n', 'banner ixl.jpg', 0, 62, '', '2018-03-30 10:03:00', NULL, 1, '', '', '', '', '', 'Avits Math (IXL)', '', 'avits-math-ixl', '', 1);
INSERT INTO `news` (`news_id`, `news_name`, `news_des`, `news_content`, `news_img`, `news_views`, `newscat_id`, `news_author`, `news_created_date`, `news_update_date`, `state`, `news_sub_info1`, `news_sub_info2`, `news_sub_info3`, `news_sub_info4`, `news_sub_info5`, `title_seo`, `des_seo`, `friendly_url`, `keyword`, `created_id`) VALUES
(103, 'AVITS SMART READING (ĐỌC THÔNG MINH)', 'PHƯƠNG PHÁP ĐỌC THÔNG MINH GIÚP BÉ YÊU PHÁT TRIỂN TRÍ NÃO VÀ KHẢ NĂNG NGÔN NGỮ', '<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Chương tr&igrave;nh&nbsp;</span></span><strong><span style=\"font-size:13.5pt\"><span style=\"color:#548dd4\">Avits </span></span></strong><strong><span style=\"font-size:12.0pt\"><span style=\"color:#548dd4\">Smart Reading</span></span></strong>&nbsp;<span style=\"font-size:13.5pt\"><span style=\"color:#333333\">được ph&aacute;t triển tr&ecirc;n nền tảng phương ph&aacute;p học đọc&nbsp; đa gi&aacute;c quan kết hợp đa cấp độ của Mỹ&nbsp; đ&atilde; được &aacute;p dụng tại c&aacute;c trường học tr&ecirc;n khắp nước&nbsp; Mỹ&nbsp; cũng như tại nhiều quốc gia tr&ecirc;n thế giới. Phương ph&aacute;p n&agrave;y được phụ huynh ưa chuộng v&agrave; lựa chọn l&agrave; phương ph&aacute;p học đọc s&aacute;ng tạo&nbsp; v&agrave; hiệu quả nhất hiện nay.&nbsp;</span></span><strong><em><span style=\"font-size:12.0pt\"><span style=\"color:blue\">Avits Smart Reading được c&ocirc;ng nhận gi&uacute;p trẻ ph&aacute;t triển tr&iacute; n&atilde;o v&agrave; khả năng ng&ocirc;n ngữ.</span></span></em></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Theo nghi&ecirc;n cứu của Trường Đại Học Harvard- Mỹ th&igrave; c&oacute; đến 70% trẻ em dưới 6 tuổi được cha mẹ c&ugrave;ng đọc s&aacute;ch trước khi đi ngủ. C&aacute;c chuy&ecirc;n gia cho biết việc tiếp tục hướng dẫn trẻ đọc s&aacute;ch trong giai đoạn tiểu học l&agrave; rất quan trọng đối với sự ph&aacute;t triển tr&iacute; n&atilde;o v&agrave; ng&ocirc;n ngữ của c&aacute;c em. Tuy nhi&ecirc;n, hầu hết c&aacute;c trẻ em Việt Nam từ 7 tuổi trở l&ecirc;n kh&ocirc;ng được r&egrave;n luyện kỹ năng n&agrave;y ở trường cũng như ở nh&agrave; m&agrave; hầu hết c&aacute;c em đều tự m&igrave;nh đọc một c&aacute;ch tự ph&aacute;t. V&igrave; vậy, n&ecirc;n sau 7 tuổi c&aacute;c em bắt đầu bộc lộ nhiều điểm hạn chế về khả năng đọc, ph&acirc;n t&iacute;ch, diễn đạt bằng ng&ocirc;n ngữ cũng như khả năng tư duy độc lập. Thực tế cho thấy rằng những trẻ em th&iacute;ch đọc s&aacute;ch c&oacute; nhiều khả năng đạt th&agrave;nh t&iacute;ch học tập tốt ở trường v&agrave; th&agrave;nh c&ocirc;ng hơn ở nơi l&agrave;m việc sau n&agrave;y.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">B&iacute; quyết gi&uacute;p c&aacute;c em quan t&acirc;m đến việc đọc l&agrave; giới thiệu với c&aacute;c em nhiều loại s&aacute;ch ph&ugrave; hợp với từng độ tuổi v&agrave; cho con tiếp x&uacute;c với việc học đọc c&agrave;ng sớm c&agrave;ng tốt. Với mỗi chủ đề n&ecirc;n cho trẻ tiếp cận v&agrave; hiểu được từ mới, nội dung cuốn s&aacute;ch v&agrave; c&ugrave;ng thảo luận về c&aacute;c nội dung đ&atilde; đọc để&nbsp; gi&uacute;p trẻ bộc lộ hiểu biết, khả nằn suy luận của m&igrave;nh v&agrave; ph&aacute;t triển tư duy.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Chương tr&igrave;nh&nbsp;</span></span><strong><span style=\"font-size:13.5pt\"><span style=\"color:#548dd4\">Avits </span></span></strong><strong><span style=\"font-size:12.0pt\"><span style=\"color:#548dd4\">Smart Reading</span></span></strong>&nbsp;<span style=\"font-size:13.5pt\"><span style=\"color:#333333\"> đ&atilde; được&nbsp; American Skills triển khai tại Việt Nam nhằm mang đến cho c&aacute;c em những lợi &iacute;ch sau:</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Gi&uacute;p c&aacute;c b&eacute; luyện ph&aacute;t &acirc;m chuẩn.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Gi&uacute;p b&eacute; từ việc biết đ&aacute;nh vần đến biết đọc trơn.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Gi&uacute;p c&aacute;c em th&iacute;ch đọc s&aacute;ch v&agrave; biết c&aacute;ch tự đọc đ&uacute;ng c&aacute;ch.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Gi&uacute;p c&aacute;c em h&igrave;nh th&agrave;nh c&aacute;c kỹ năng tư duy như: khả năng t&oacute;m tắt v&agrave; ghi nhớ th&ocirc;ng tin, khả năng ph&acirc;n t&iacute;ch v&agrave; kh&aacute;i qu&aacute;t một vấn đề, khả năng suy luận logic, khả năng tưởng tượng, li&ecirc;n tưởng v&agrave; s&aacute;ng tạo,&hellip;</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Gi&uacute;p n&atilde;o trẻ hoạt động v&agrave; gi&uacute;p suy nghĩ của trẻ ph&aacute;t triển đa chiều, đồng thời c&ograve;n l&agrave;m tăng sự tập trung của n&atilde;o bộ trong mọi ho&agrave;n cảnh.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Gi&uacute;p tăng cường khả năng giao tiếp hiệu quả v&agrave; gi&uacute;p r&egrave;n luyện năng lực ng&ocirc;n ngữ: đọc s&aacute;ch gi&uacute;p l&agrave;m gi&agrave;u vốn từ, tăng khả năng sử dụng ng&ocirc; ngữ để diễn đạt v&agrave; giao tiếp.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Gi&uacute;p c&aacute;c b&eacute; h&igrave;nh th&agrave;nh nh&acirc;n c&aacute;ch, vốn sống v&agrave; kiến thức x&atilde; hội th&ocirc;ng qua c&aacute;c chủ đề b&agrave;i học phong ph&uacute;, đa dạng.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Gi&uacute;p trẻ cảm thấy phấn chấn hơn, gi&uacute;p b&eacute; giảm bớt những th&oacute;i quen ảnh hưởng đến khả năng tư duy v&agrave; tập trung của b&eacute; như xem tivi, chơi game,&hellip;</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Gi&uacute;p c&aacute;c em giảm bớt &aacute;p lực của cuộc sống v&agrave; lấy lại sự c&acirc;n bằng cần thiết, gi&uacute;p c&aacute;c em sống khoẻ v&agrave; sống tốt hơn.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.5pt\"><span style=\"color:blue\">PHƯƠNG PH&Aacute;P CỦA CHƯƠNG TR&Igrave;NH:</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:blue\">Phương ph&aacute;p học đọc đa cấp độ</span></span></strong><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">: Mỗi buổi học b&eacute; được học một nội dung/ chủ đề hấp dẫn v&agrave; ph&ugrave; hợp với độ tuổi v&agrave; tr&igrave;nh độ của b&eacute;. Đ&acirc;y l&agrave; nội dung được c&aacute;c chuy&ecirc;n gia nước ngo&agrave;i v&agrave; trong nước bi&ecirc;n soạn d&agrave;nh ri&ecirc;ng cho chương tr&igrave;nh&nbsp;</span></span><strong><span style=\"font-size:13.5pt\"><span style=\"color:#548dd4\">Avits </span></span></strong><strong><span style=\"font-size:12.0pt\"><span style=\"color:#548dd4\">Smart Reading</span></span></strong>&nbsp;<span style=\"font-size:13.5pt\"><span style=\"color:#333333\">với c&aacute;c chủ đề rất đa dạng, với nhiều cấp độ kh&aacute;c nhau như: thế giới quanh em, kh&aacute;m ph&aacute; bản th&acirc;n, gi&aacute;o dục t&igrave;nh cảm gia đ&igrave;nh, bạn b&egrave;, gi&aacute;o dục gi&aacute; trị sống, c&aacute;c hiện tượng thi&ecirc;n nhi&ecirc;n v&agrave; c&aacute;c t&aacute;c phẩm văn học nổi tiếng cho mọi lứa tuổi,&hellip;</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Phương ph&aacute;p học đọc đa gi&aacute;c quan</span></span></strong><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">: Với mỗi chủ đề b&eacute; đều được tiếp cận th&ocirc;ng qua c&aacute;c gi&aacute;c quan như: nghe, nh&igrave;n, sờ, tưởng tượng, đọc, nhập vai,&hellip;với c&aacute;c hoạt động phong ph&uacute; như: đố vui, đ&oacute;ng kịch, kể chuyện, xem phim, b&igrave;nh luận, thảo luận nh&oacute;m, chơi tr&ograve; chơi tương t&aacute;c, vận động cơ thể theo ng&ocirc;n ngữ, diễn đạt ng&ocirc;n ngữ bằng c&aacute;c h&agrave;nh động phi ng&ocirc;n ngữ.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Ph&aacute;t triển Kỹ năng tư duy: Mỗi buổi b&eacute; được giới thiệu v&agrave; r&egrave;n luyện một hoặc một số kỹ năng tư duy nhất định. Chẳng hạn như:</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;T&oacute;m tắt th&ocirc;ng tin &ndash; dưới dạng sơ đồ tư duy</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Ghi nhớ v&agrave; diễn đạt</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Kh&aacute;i qu&aacute;t v&agrave; tổng kết</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Kỹ năng suy luận v&agrave; tư duy</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Kỹ năng ph&aacute;n đo&aacute;n</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Kỹ năng ph&acirc;n t&iacute;ch nh&acirc;n vật v&agrave; ph&acirc;n t&iacute;ch bối cảnh</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Đưa ra giải ph&aacute;p cho một vấn đề</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Li&ecirc;n tưởng v&agrave; &aacute;p dụng</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">-&nbsp;&nbsp;Tưởng tượng v&agrave; s&aacute;ng tạo</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Từ vựng: Mỗi buổi sẽ cung cấp cho b&eacute; một lượng từ vựng mới nhất định tuỳ thuộc v&agrave;o từng cấp độ, gi&uacute;p b&eacute; bổ sung vốn từ th&ecirc;m phong ph&uacute;.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">B&aacute;o c&aacute;o h&agrave;ng ng&agrave;y: Sau từng buổi học, phụ huynh đều nhận được th&ocirc;ng tin&nbsp; về những g&igrave; m&agrave; b&eacute; được học trong buổi học đ&oacute;, c&ugrave;ng với giới &yacute; về b&agrave;i tập gi&uacute;p b&eacute; r&egrave;n luyện tại nh&agrave;.</span></span></span></span></span></p>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</h2>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:black\">CHƯƠNG TR&Igrave;NH TIỀN TIỂU HỌC : (D&agrave;nh cho trẻ từ 4-6 tuổi)</span></span></strong></span></span></h2>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#333333\">AVITS&nbsp;</span></span><span style=\"font-size:12.0pt\"><span style=\"color:black\">SMART READING - FOR PRE -SCHOOL</span></span></strong></span></span></h2>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">Chương tr&igrave;nh d&agrave;nh cho c&aacute;c b&eacute; từ 4 tuổi đến 6 tuổi. Ở mỗi cấp độ sẽ c&oacute; nội dung chương tr&igrave;nh học ph&ugrave; hợp với tr&igrave;nh độ của học sinh.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">Khi ghi danh, c&aacute;c em sẽ được l&agrave;m b&agrave;i kiểm đầu v&agrave;o để đ&aacute;nh gi&aacute; tr&igrave;nh độ. Với c&aacute;c b&eacute; từ 4-6 tuổi, đội ngũ chuy&ecirc;n gia ng&ocirc;n của American Skills sẽ ph&acirc;n t&iacute;ch giọng n&oacute;i để c&oacute; biện ph&aacute;p điều chỉnh ph&aacute;t &acirc;m kịp thời cho b&eacute;.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">Smart Reading Skills For Pre -School với 2cấp độ&nbsp;sau:&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"color:#333333\">Cấp độ Starter &ndash; l&agrave;m quen:</span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><br />\r\n<span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">L&agrave; c&aacute;c b&eacute; từ 4-5 tuổi mới l&agrave;m quen với chữ c&aacute;i, b&eacute; n&oacute;i v&agrave; biết diễn đạt bằng ng&ocirc;n ngữ ở mức trung b&igrave;nh.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">Qua chương tr&igrave;nh n&agrave;y b&eacute; sẽ được l&agrave;m quen với c&aacute;c chữ c&aacute;i, biết đọc c&aacute;c từ đơn giản c&oacute; 2 hoặc 3 &acirc;m tiết. Biết sử dụng c&aacute;c từ đ&atilde; học v&agrave;o việc đặt c&acirc;u v&agrave; sử dụng từ trong giao tiếp.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">Ngo&agrave;i ra chương tr&igrave;nh c&ograve;n gi&uacute;p luyện ph&aacute;t &acirc;m chuẩn cho b&eacute; ngay từ đầu v&agrave; bổ sung, l&agrave;m phong ph&uacute; th&ecirc;m kho vốn từ của b&eacute;. B&eacute; được mở rộng c&aacute;c kiến thức về cuộc sống xung quanh, về văn ho&aacute;, x&atilde; hội, gi&aacute; trị sống th&ocirc;ng qua c&aacute;c c&acirc;u chuyện hấp dẫn v&agrave; mang &yacute; nghĩa gi&aacute;o dục cao.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"color:#333333\">Cấp độ </span></strong><strong><span style=\"color:black\">Kindergarten</span><strong><span style=\"color:#333333\"> (K): &ndash; Luyện đọc v&agrave; đọc một c&aacute;ch tr&ocirc;i chảy</span></strong></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">L&agrave; c&aacute;c b&eacute; từ 5-6 tuổi, đ&atilde; trải qua kho&aacute; học Starter hoặc đ&atilde; nhớ bảng chứ c&aacute;i tiếng Việt v&agrave; biết tự đ&aacute;nh vần một số từ đơn giản. Khả năng diễn đạt bằng ng&ocirc;n ngữ của b&eacute; kh&aacute; tốt.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">Qua chương tr&igrave;nh b&eacute; được mở rộng th&ecirc;m vốn từ vựng; từ việc biết c&aacute;ch đ&aacute;nh vần b&eacute; đ&atilde; c&oacute; thể đọc trơn; b&eacute; c&oacute; thể đọc được c&aacute;c mẩu chuyện ngắn v&agrave; tự t&oacute;m tắt được cốt chuyện.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">B&eacute; sẽ được n&acirc;ng cao tr&igrave;nh độ cũng như khả năng ứng dụng ng&ocirc;n ngữ một c&aacute;ch linh hoạt v&agrave; đa dạng.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">B&ecirc;n cạnh đ&oacute; khả năng tư duy, suy luận logic, quan s&aacute;t, ph&aacute;n đo&aacute;n v&agrave; đưa ra quyết định của b&eacute; cũng được cải thiện một c&aacute;ch đ&aacute;ng kể.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">Đ&acirc;y l&agrave; tiền đề gi&uacute;p c&aacute;c b&eacute; tiếp cận được với c&aacute;c m&ocirc;n học kh&aacute;c một c&aacute;ch dễ dạng như: To&aacute;n học, kể chuyện, ca h&aacute;t, kh&aacute;m ph&aacute; khoa học th&ocirc;ng qua c&aacute;c c&acirc;u chuyện l&yacute; th&uacute;. Vốn hiểu biết về x&atilde; hội của b&eacute; c&agrave;ng được mở rộng.</span></span></span></p>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:black\">CHƯƠNG TR&Igrave;NH TIỂU HỌC : </span></span></strong></span></span></h2>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#333333\">AVITS&nbsp;</span></span><span style=\"font-size:12.0pt\"><span style=\"color:black\">SMART READING &nbsp;</span></span><span style=\"font-size:11.0pt\"><span style=\"color:black\">- FOR PRIMARY SCHOOL :</span></span></strong></span></span></h2>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>Cấp độ </strong><strong><span style=\"color:black\">Starter School (SC) </span></strong><strong>- Kho&aacute; học đặc biệt</strong></span></span></p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">D&agrave;nh cho c&aacute;c b&eacute; từ 6-8 tuổi đ&atilde; biết đọc v&agrave; viết nhưng gặp kh&oacute; khăn về ng&ocirc;n ngữ, diễn đạt, ghi nhớ,&hellip;</span></span></p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Chương tr&igrave;nh sẽ gi&uacute;p b&eacute; củng cố lại c&aacute;c kiến thức cơ bản về đọc như: cấu tạo từ, cấu tạo c&acirc;u, đoạn văn,&hellip;</span></span></p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Gi&uacute;p b&eacute; biết &aacute;p dụng tư duy s&aacute;ng tạo v&agrave;o việc đọc v&agrave; ph&aacute; vỡ mọi trở ngại trước đ&acirc;y của b&eacute; về vấn đề ng&ocirc;n ngữ v&agrave; diễn đạt ng&ocirc;n ngữ.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\">Gi&uacute;p b&eacute; th&ecirc;m tự tin trong cuộc sống v&agrave; tiến bộ trong học tập cũng như th&agrave;nh c&ocirc;ng trong tương lai</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>Chương tr&igrave;nh n&acirc;ng cao gồm c&aacute;c cấp độ:</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>Level 1 &ndash; Level 2 &ndash; Level 3 &ndash; Level 4 &ndash; Level 5</strong></span></span></p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">B&eacute; đ&atilde; chuyển từ giai đoạn học đọc sang giai đọan biết sử dụng ng&ocirc;n ngữ như một c&ocirc;ng cụ diễn đạt v&agrave; t&igrave;m kiếm th&ocirc;ng tin.</span></span></p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Ở c&aacute;c cấp độ n&agrave;y c&aacute;c em sẽ được hướng dẫn c&aacute;ch vận dụng tư duy tổng hợp để nắm bắt, ph&acirc;n t&iacute;ch v&agrave; giải quyết c&aacute;c vấn đề ng&ocirc;n ngữ. </span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\">Th&ocirc;ng qua học đọc b&eacute; biết &aacute;p dụng ng&ocirc;n ngữ v&agrave;o c&aacute;c ngữ cảnh kh&aacute;c nhau một c&aacute;ch c&oacute; chủ đ&iacute;ch v&agrave; mang lại hiệu quả cao.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\">Chương tr&igrave;nh c&oacute; phối kết hợp SKG của Bộ gi&aacute;o dục đ&agrave;o tạo Việt nam.</span></span></span></p>\r\n', '1987926.jpg', 0, 62, '', '2018-03-30 10:03:42', '2018-04-12 11:04:13', 1, '', '', '', '', '', 'AVITS SMART READING (ĐỌC THÔNG MINH)', '', 'avits-smart-reading-doc-thong-minh', '', 1);
INSERT INTO `news` (`news_id`, `news_name`, `news_des`, `news_content`, `news_img`, `news_views`, `newscat_id`, `news_author`, `news_created_date`, `news_update_date`, `state`, `news_sub_info1`, `news_sub_info2`, `news_sub_info3`, `news_sub_info4`, `news_sub_info5`, `title_seo`, `des_seo`, `friendly_url`, `keyword`, `created_id`) VALUES
(104, 'AVITS SOCIAL SKILLS', 'RÈN LUYỆN KỸ NĂNG XÃ HỘI GIÚP BÉ HOÀN THIỆN BẢN THÂN, TỰ TIN VÀ THÀNH CÔNG( Dành cho bé từ 4-12 tuổi)', '<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Chương tr&igrave;nh <strong><span style=\"color:#548dd4\">Avits Socail Skills</span></strong> của American Skills l&agrave; <strong><em>Chương tr&igrave;nh đ&agrave;o tạo kỹ năng x&atilde; hội</em></strong> d&agrave;i hạn đầu ti&ecirc;n tại Việt Nam d&agrave;nh cho c&aacute;c b&eacute; Mầm Non v&agrave; Tiểu học.&nbsp; Với gi&aacute;o tr&igrave;nh được đ&aacute;nh gi&aacute; l&agrave; Gi&aacute;o tr&igrave;nh Đ&agrave;o tạo kỹ năng x&atilde; hội ti&ecirc;n tiến nhất của Mỹ v&agrave; đ&atilde; được c&aacute;c chuy&ecirc;n gia về t&acirc;m l&yacute; học trẻ em của Việt Nam điều chỉnh cho ph&ugrave; hợp với trẻ em Việt Nam. American Skills mong muốn <strong><span style=\"color:#548dd4\">Avits Socail Skills</span></strong> sẽ l&agrave; nền m&oacute;ng vững chắc cho c&aacute;c em x&acirc;y dựng ng&ocirc;i nh&agrave; tương lai của m&igrave;nh.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>TẦM QUAN TRỌNG CỦA KỸ NĂNG X&Atilde; HỘI ĐỐI VỚI TRẺ:</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#3b3b3b\">L&agrave; những bậc l&agrave;m cha l&agrave;m mẹ, ai cũng mong mỏi con c&aacute;i m&igrave;nh sẽ được ph&aacute;t triển thật c&acirc;n bằng, h&agrave;i h&ograve;a cả về kiến thức văn ho&aacute; lần x&atilde; hội. Những tưởng khi kinh tế ph&aacute;t triển hơn, x&atilde; hội dư thừa vật chất hơn th&igrave; kh&ocirc;ng kh&oacute; để đạt được điều đ&oacute;; nhưng thực tế lại như đang chứng minh điều ngược lại.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#3b3b3b\">&Aacute;p lực học tập ng&agrave;y c&agrave;ng căng thẳng khiến c&aacute;c em phải d&agrave;nh phần lớn tuổi thơ để vật lộn c&ugrave;ng b&agrave;i vở m&agrave; &iacute;t c&oacute; thời gian để kh&aacute;m ph&aacute; bản th&acirc;n, kh&aacute;m ph&aacute; thế giới xung quanh v&agrave; trải nghiệm cuộc sống như đ&uacute;ng nghĩa của n&oacute;. Trong khi đ&oacute;, nhiều bậc cha mẹ chỉ c&oacute; thể lo cho con được đủ đầy về c&aacute;i ăn c&aacute;i mặc, c&ograve;n những g&igrave; quan trọng hơn như tr&iacute; tuệ, t&acirc;m hồn, t&igrave;nh cảm, kỹ năng x&atilde; hội,&hellip; th&igrave; dẫu rất muốn cũng kh&oacute; l&ograve;ng chăm ch&uacute;t nổi. V&agrave; rồi, kh&ocirc;ng &iacute;t c&aacute;c em lớn l&ecirc;n trong trạng th&aacute;i &ldquo;đỏ vỏ xanh l&ograve;ng&rdquo;: năng lực tr&iacute; tuệ chưa chắc tương đồng với kết quả học tập ở trường; sự th&agrave;nh c&ocirc;ng ở trường lớp chưa chắc đảm bảo cho sự th&agrave;nh c&ocirc;ng khi ra ngo&agrave;i x&atilde; hội; v&agrave; sự th&ocirc;ng minh về tr&iacute; tuệ chưa chắc tương đồng với sự nhạy b&eacute;n về cảm x&uacute;c, t&acirc;m hồn&hellip;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#3b3b3b\">V&igrave; vậy, American Skills đ&atilde; ph&aacute;t triển chương tr&igrave;nh Social Skills nhằm mang đến cho trẻ em sự ph&aacute;t triển to&agrave;n diễn kể cả về thể chất v&agrave; t&acirc;m hồn, cả về kiến thức văn ho&aacute; lẫn x&atilde; hội.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>NHỮNG LỢI &Iacute;CH B&Eacute; ĐẠT ĐƯỢC SAU KHO&Aacute; HỌC/ MỤC TI&Ecirc;U CỦA CHƯƠNG TR&Igrave;NH:</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">- L&agrave;m thế n&agrave;o để trẻ c&oacute; thể t&igrave;m được chỗ đứng cho m&igrave;nh ở tr&ecirc;n lớp cũng như ở ngo&agrave;i x&atilde; hội: khi trẻ bị tẩy chay, hay bị c&ocirc; lập,&hellip;</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">- Cải thiệt những th&oacute;i quen xấu, gi&uacute;p trẻ biết c&aacute;ch cư xử đ&uacute;ng mực với bạn b&egrave;, thầy c&ocirc; v&agrave; những người xung quanh: th&ocirc; lỗ, v&ocirc; lễ, ki&ecirc;u ngạo, kh&ocirc;ng l&agrave;m những h&agrave;nh động kỳ quặc, ngốc nghếch, kh&ocirc;ng ph&ugrave; hợp,&hellip;</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">- Biết t&igrave;m tới sự trợ gi&uacute;p của bạn b&egrave;, thầy c&ocirc;, v&agrave; những người xung quanh khi cần.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">- X&acirc;y dựng t&igrave;nh cảm bạn b&egrave;, người th&acirc;n: biết chia sẻ, cảm th&ocirc;ng, nhận lỗi v&agrave; tha lỗi cho người kh&aacute;c, gi&uacute;p đỡ người kh&aacute;c, động vi&ecirc;n v&agrave; hỗ trợ kịp thời khi bạn cần,&hellip;</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">- Gi&uacute;p c&aacute;c em c&oacute; c&aacute;ch ứng xử b&igrave;nh tĩnh, nh&atilde; nhặn trong những t&igrave;nh hu&ocirc;́ng kh&oacute; chịu. (Chẳng hạn như bị bạn tr&ecirc;u chọc, cảm th&acirc;́y c&ocirc; đơn khi b&ocirc;́ mẹ sinh em b&eacute;, sợ h&atilde;i khi phải đi học...).</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">- Gi&uacute;p c&aacute;c em trở th&agrave;nh người nhạy b&eacute;n, giao ti&ecirc;́p t&ocirc;́t, bi&ecirc;́t quan t&acirc;m, bi&ecirc;́t sẻ chia. Hiểu được cảm x&uacute;c của m&igrave;nh v&agrave; đọc được cảm x&uacute;c của người kh&aacute;c.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">- Gi&uacute;p c&aacute;c em vượt qua được những thời khắc kh&oacute; khăn m&agrave; c&aacute;c em gặp phải như: lỗi thất vọng, sự mất m&aacute;t, cạnh tranh với bạn b&egrave;, kh&aacute;t khao chiến thắng,&hellip;</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">- Gi&uacute;p c&aacute;c em h&ograve;a nhập v&agrave;o một m&ocirc;i trường mới một c&aacute;ch tự tin, h&ograve;a đ&ocirc;̀ng. </span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">- Gi&uacute;p c&aacute;c em học tập t&ocirc;́t hơn v&agrave; h&ograve;a nhập với bạn b&egrave; t&ocirc;́t hơn khi ở trường, x&acirc;y dựng được mối quan hệ tốt với người th&acirc;n v&agrave; th&agrave;nh c&ocirc;ng hơn trong cuộc sống sau n&agrave;y.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">NỘI DUNG CHƯƠNG TR&Igrave;NH:</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Qua những nghi&ecirc;n cứu, c&aacute;c chuy&ecirc;n gia cho rằng bắt đầu từ nhỏ, khi bộ n&atilde;o vẫn c&ograve;n đang trong giai đoạn h&igrave;nh th&agrave;nh, lu&ocirc;n l&agrave; thời điểm tốt nhất để dạy c&aacute;c kỹ năng x&atilde; hội, h&agrave;nh vi v&agrave; cảm x&uacute;c mới cho trẻ. Bắt đầu tại thời điểm n&agrave;y th&igrave; khả năng th&agrave;nh c&ocirc;ng sẽ cao hơn gấp nhiều lần so với c&aacute;c thời kỳ sau đ&oacute;. Độ tuổi 0-6 tuổi được xem l&agrave; thời điểm v&agrave;ng cho sự tạo dựng v&agrave; ph&aacute;t triển kỹ năng x&atilde; hội cần thiết cho trẻ.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Khi c&aacute;c em bắt đầu bước v&agrave;o Tiểu học, như mở ra cho c&aacute;c em m&ocirc;t thế giơi mới rộng lớn v&agrave; nhiều thử th&aacute;ch hơn. Đ&acirc;y l&agrave; thời gian để b&eacute; tạo dựng mối quan hệ bạn b&egrave; thực sự v&agrave; l&agrave; m&ocirc;i trường để trẻ t&igrave;m thấy &ldquo; Người bạn tốt nhất&rdquo; v&agrave; một nh&oacute;m bạn &ldquo; đồng đẳng đồng t&iacute;nh&rdquo; m&agrave; sẽ đi một chặng đường d&agrave;i hướng tới việc th&uacute;c đẩy qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh t&iacute;nh c&aacute;ch ri&ecirc;ng của trẻ. Nếu tạo dựng được mối quan hệ bạn b&egrave; tốt ở trường sẽ g&oacute;p phần gi&uacute;p b&eacute; gặt h&aacute;i được nhiều th&agrave;nh c&ocirc;ng tại m&ocirc;i trường mới n&agrave;y.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">American Skills đ&atilde; x&acirc;y dựng hai chương tr&igrave;nh d&agrave;nh ri&ecirc;ng cho học sinh Mầm non v&agrave; Tiều học với c&aacute;c chủ đề sau:</span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Small Steps, Bigs Skills: L&agrave; chương tr&igrave;nh gi&uacute;p b&eacute; tự ho&agrave;n thiện bản th&acirc;n, biết tự chủ trong mọi t&igrave;nh huống v&agrave; sống tự lập. Gồm c&aacute;c chủ đề nhỏ:</span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Helping in the Kitchen: rửa tay, l&agrave;m b&aacute;nh, dọn b&agrave;n, pha tr&agrave;, r&oacute;t nước, l&agrave;m nước hoa quả,&hellip;</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Getting Ready for the Day: Chuẩn bị t&uacute;i s&aacute;ch đi học, mang giầy, mở cửa, mang thắt lưng,&hellip;</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">My Clean Room: chuẩn bị v&agrave; dọn dẹp giường ngủ, dọn ph&ograve;ng, m&oacute;c quần &aacute;o, gập quần &aacute;o,&hellip;</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Helping at public place: rửa tay, bưng khay, rửa khay, xếp h&agrave;ng,&hellip;</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Time to Relax: tắt bật Tivi, trồng c&acirc;y, chăm s&oacute;c c&acirc;y cảnh, thư cưng,&hellip;</span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Social Skill Development: - nh&oacute;m kỹ năng ph&aacute;t triển kỹ năng x&atilde; hội</span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Frienship- Thiết lập t&igrave;nh bạn</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">T&igrave;nh bạn l&agrave; điều v&ocirc; c&ugrave;ng quan trọng v&agrave; thi&ecirc;ng li&ecirc;ng đối với tuổi thơ của mỗi người. L&agrave;m thế n&agrave;o để nhận ra một người bạn tốt v&agrave; l&agrave;m g&igrave; để trở th&agrave;nh một người bạn tốt lu&ocirc;n l&agrave; vấn đề m&agrave; trẻ gặp phải. Kh&ocirc;ng chỉ trẻ m&agrave; cả phụ huynh cũng mong muốn con c&oacute; &iacute;t nhất l&agrave; một người bạn tốt. Người bạn tốt c&oacute; những ảnh hưởng rất lớn đến trẻ v&agrave; thậm tr&iacute; c&oacute; ảnh hưởng lớn hơn cả bố mẹ của trẻ.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Meeting people and making a Conversation- Gặp gỡ mọi người v&agrave; tiến h&agrave;nh một cuộc hội thoại:</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Tiến h&agrave;nh một cuộc tr&ograve; chuyện giống như một điệu nhảy, đ&ograve;i hỏi một sự nhạy b&eacute;n về thời điểm v&agrave; khả năng dẫn dắt cũng như l&agrave;m theo. Nhiều trẻ em gặp kh&oacute; khăn trong&nbsp; giao tiếp v&agrave; thực hiện c&aacute;c cuộc hội thoại,. Ch&uacute;ng kh&ocirc;ng biết diễn đạt &yacute; của m&igrave;nh bằng lời v&agrave; điều n&agrave;y l&agrave;m ch&uacute;ng ngại giao tiếp hoặc xa l&aacute;nh mọi người xung quanh. Đỉnh điểm của sự ngại giao tiếp l&agrave; biểu hiện của bệnh tự kỷ.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Thanks - Cảm ơn</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Cảm ơn một c&aacute;ch ch&acirc;n th&agrave;nh v&agrave; đ&uacute;ng l&uacute;c l&agrave; một kỹ năng x&atilde; hội quan trong trong suốt cuộc đời của mỗi con người. Cuộc sống sẽ tươi đẹp hơn nếu ta biết tr&acirc;n trọng n&oacute;. Cảm ơn cũng l&agrave; c&aacute;ch thể hiện rằng ta đang tr&acirc;n trọng những điều ch&uacute;ng ta đang c&oacute;.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Apologizing- Xin lỗi</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Ch&uacute;ng ta kh&ocirc;ng phải l&agrave; những người ho&agrave;n hảo. Cũng c&oacute; khi ch&uacute;ng ta mắc một lỗi g&igrave; đ&oacute;. Điều quan trong l&agrave; ta nhận ra được sai lầm v&agrave; t&igrave;m c&aacute;ch sửa chữa những sai lầm đ&oacute;. Xin lỗi cũng l&agrave; c&aacute;ch để trẻ thiết lập c&aacute;c mối quan hệ bền chặt. Nếu trẻ kh&ocirc;ng được học kỹ năng n&agrave;y từ nhỏ, c&oacute; thể sẽ l&agrave;m cho trẻ gặp kh&oacute; khăn trong nhiều mối quan hệ sau n&agrave;y.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Group Skills and Sportmanship- Kỹ năng hoạt động nh&oacute;m v&agrave; tinh thần đồng đội</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Đ&acirc;y l&agrave; nh&oacute;m kỹ năng đ&ograve;i hỏi sự kết hợp của rất nhiều kỹ năng kh&aacute;c nhau. Th&ocirc;ng qua chủ đề n&agrave;y trẻ gi&uacute;p trẻ hiểu được gi&aacute; trị của bản th&acirc;n v&agrave; sức mạnh của tập thể. Điều n&agrave;y hết sức quan trọng trong c&ocirc;ng việc sau n&agrave;y của trẻ.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Using Nice Word, Showing Respect, Being Honest- Sử dụng lời hay &yacute; đẹp, t&ocirc;n trọng v&agrave; th&acirc;n thiện với mọi người.</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Đ&acirc;y l&agrave; những kỹ năng cần thiết gi&uacute;p trẻ tạo dựng được mối quan hệ x&atilde; hội tốt v&agrave; th&agrave;nh c&ocirc;ng trong c&aacute;c mội quan hệ x&atilde; hội.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Self Worth and Confidance ( Everyones is Differnents) - Hiểu gi&aacute; trị của bản th&acirc;n v&agrave; sự tự tin về bản th&acirc;n</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Trẻ nhỏ phải t&ocirc;n trọng bản th&acirc;n m&igrave;nh trước khi c&aacute;c em tiếp x&uacute;c với những người kh&aacute;c, gặp gỡ những người lạ v&agrave; những t&igrave;nh huống mới. Trẻ hiểu được gi&aacute; trị v&agrave; tự tin về bản th&acirc;n sẽ dễ d&agrave;ng hơn khi thiết lập mối quan hệ bạn b&egrave;, xử l&yacute; c&aacute;c xung đột, đối ph&oacute; tốt với c&aacute;c t&igrave;nh huống ti&ecirc;u cực.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Cooperation and Conprimise ( Working Together)- Hợp t&aacute;c v&agrave; thoả hiệp</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Hợp t&aacute;c li&ecirc;n quan đến việc lắng nghe người kh&aacute;c đang n&oacute;i, hiểu được lợi &iacute;ch của việc chia sẻ v&agrave; t&ocirc;n trọng sự lu&acirc;n phi&ecirc;n. Trong nhiều t&igrave;nh huống, để hợp t&aacute;c tốt với nhau trong nh&oacute;m đồng nghĩa với việc đi đến một sự thoả hiệp. Điều n&agrave;y thực sự quan trong trong khi trẻ sống trong m&ocirc;i trường tập thể v&agrave; c&aacute;c c&aacute; nh&acirc;n c&oacute; sự t&aacute;c động qua lại lẫn nhau. Bản th&acirc;n trẻ kh&ocirc;ng tự c&oacute; kỹ năng n&agrave;y m&agrave; kỹ năng n&agrave;y được h&igrave;nh th&agrave;nh trong qu&aacute; tr&igrave;nh trẻ tương t&aacute;c với x&atilde; hội.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Personal Space- Kh&ocirc;ng gian c&aacute; nh&acirc;n</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Bản th&acirc;n trẻ kh&ocirc;ng tự &yacute; thực được kh&ocirc;ng gian c&aacute; nh&acirc;n của m&igrave;nh. Điều n&agrave;y dẫn đến những sự việc rất đau l&ograve;ng l&agrave; ng&agrave;y c&agrave;ng c&oacute; nhiều trẻ em bị lạm dụng t&igrave;nh dục hoặc hiếp d&acirc;m. Đ&ocirc;i khi ch&uacute;ng ta thường bỏ qua việc n&agrave;y một c&aacute;ch v&ocirc; &yacute;. Trẻ em cần được gi&aacute;o dục sớm về điều n&agrave;y để trẻ biết được khoảng c&aacute;ch an to&agrave;n với người xung quanh để trẻ kh&ocirc;ng bị lợi dụng một c&aacute;ch qu&aacute; đ&aacute;ng.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\"><a href=\"http://socialskillscentral.com/social-skills-for-preschoolers/compassion-and-caring-pre-school/\">Compassion, Caring</a> and sharing:</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Trẻ em vốn l&agrave; ngọn nguồn của sự tốt bụng, chu đ&aacute;o v&agrave; chia sẻ với người kh&aacute;c. Nhưng sự tốt bụng v&agrave; đồng cảm sẽ mất dần nếu ch&uacute;ng ta kh&ocirc;ng biết nu&ocirc;i dưỡng n&oacute;. Trẻ c&agrave;ng lớn th&igrave; c&aacute;i t&ocirc;i c&agrave;ng lớn v&agrave; &yacute; thức về lợi &iacute;ch c&aacute; nh&acirc;n c&agrave;ng cao. Ch&uacute;ng ta cần gi&uacute;p trẻ biết c&acirc;n bằng v&agrave; ph&acirc;n biệt giữa c&aacute;i t&ocirc;i c&aacute; nh&acirc;n v&agrave; t&igrave;nh y&ecirc;u thương v&agrave; chu đ&aacute;o với mọi người. Điều n&agrave;y thực sự cần thiết gi&uacute;p h&igrave;nh th&agrave;nh nh&acirc;n c&aacute;ch của trẻ. Khi c&oacute; t&igrave;nh y&ecirc;u thương v&agrave; sự đồng cảm với mọi người th&igrave; việc chia sẻ với người kh&aacute;c sẽ dẽ d&agrave;ng thực hiện hơn.</span></span></p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">3. Fellings- Cảm x&uacute;c- ph&aacute;t triển tr&iacute; th&ocirc;ng minh về cảm x&uacute;c</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Feelings and Thinking Positively- cảm x&uacute;c v&agrave; những suy nghĩ t&iacute;ch cực.</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Nhạy cảm l&agrave; một điều ho&agrave;n to&agrave;n tốt v&agrave; l&agrave; bản năng thi&ecirc;n ph&uacute;. Tuy nhi&ecirc;n trẻ em cần được thấy tầm quan trọng của những suy nghĩ t&iacute;ch cực v&agrave; tại sao cần phải suy nghĩ t&iacute;ch cực. Điều n&agrave;y ảnh hưởng rất lớn đến những quyết định v&agrave; h&agrave;nh động của trẻ với x&atilde; hội.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Dealing with Disappointment, Competition,Winning and Losing</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Bất cứ ở đ&acirc;u cũng tồn tại sự cạnh tranh, chiến thắng, sự mất m&aacute;t v&agrave; lỗi thất vọng. Đ&oacute; l&agrave; sự phong ph&uacute; v&agrave; đa dạng của x&atilde; hội. Trẻ em thường gặp kh&oacute; khăn với cạnh tranh, chiến thắng, mất m&aacute;t v&agrave; thất vọng. Vậy l&agrave;m thế n&agrave;o để trẻ tự tin vượt qua được tất cả điều n&agrave;y?</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Anxiety and Shyness- Lo lắng v&agrave; nh&uacute;t nh&aacute;t</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Lo lắng l&agrave; vấn đề m&agrave; hầu hết c&aacute;c trẻ em đều gặp phải. Nh&uacute;t nh&aacute;t l&agrave; biểu hiện của sự lo lắng ở trẻ. C&aacute;c nh&agrave; t&acirc;m l&yacute; học cho rằng những trẻ em hay lo lắng khi giao tiếp x&atilde; hội thường c&oacute; xu hướng gặp vấn đề khi ch&uacute;ng lớn l&ecirc;n. Điều n&agrave;y c&oacute; thể l&agrave;m cho cuộc sống của trẻ hết sức kh&oacute; khăn. L&agrave;m thế n&agrave;o để trẻ vượt qua được sự lo lắng v&agrave; thấy tự tin trong giao tiếp?</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Felling Angry- Tức giận</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Khi trẻ tức giận trẻ phải l&agrave;m thế n&agrave;o để k&igrave;m chế c&aacute;c cơn tức giận của m&igrave;nh một c&aacute;ch hợp l&yacute; v&agrave; kh&ocirc;n kh&eacute;o. B&ecirc;n cạnh đ&oacute; khi trẻ đối mặt với cơn tức giận của người kh&aacute;c th&igrave; trẻ phải xử l&yacute; thế n&agrave;o để trẻ kh&ocirc;ng bị tổn thương?</span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Daily Routines and Rules: Tu&acirc;n thủ c&aacute;c quy tắc v&agrave; luật lệ </span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Getting ready for School: Gi&uacute;p trẻ chuẩn bị t&acirc;m l&yacute; sẵn s&agrave;ng khi đến trường v&agrave; đến trường với th&aacute;i đội vui vẻ v&agrave; thoải m&aacute;i. Điều n&agrave;y rất quan trọng v&agrave; quyết định đến kết quả học tập ở trường của trẻ.</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Listening and Following Directions: Ở bất cứ đ&acirc;u trẻ cũng đều phải tu&acirc;n thủ sự hướng dẫn. Điều n&agrave;y gi&uacute;p b&eacute; trở th&agrave;nh một c&ocirc;ng d&acirc;n văn minh, lịch sự v&agrave; tr&aacute;nh mọi rủi ro kh&ocirc;ng đ&aacute;ng c&oacute;.</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">My Day at School: Mỗi ng&agrave;y ở trường với bao nhi&ecirc;u hoạt động diễn ra v&agrave; c&aacute;c em phải xử l&yacute; rất nhiều mối quan hệ x&atilde; hội kh&aacute;c nhau. L&agrave;m thế n&agrave;o để trẻ c&oacute; một ng&agrave;y đến trường vui vẻ v&agrave; th&agrave;nh c&ocirc;ng ( học được những kiến thức bổ &iacute;ch, tạo dựng được mối quan hệ tốt với thầy c&ocirc; v&agrave; bạn b&egrave;, an to&agrave;n v&agrave; khoẻ mạnh) đ&oacute; l&agrave; những vấn đề lớn m&agrave; mỗi ch&uacute;ng ta đều quan t&acirc;n.</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">When Things Change: Mọi thứ xung quanh trẻ lu&ocirc;n thay đổi v&agrave; l&agrave;m thế n&agrave;o để trẻ c&oacute; thể th&iacute;ch nghi với những sự thay đổi đ&oacute; một c&aacute;ch t&iacute;ch cực v&agrave; kh&ocirc;ng &aacute;p lực?</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">School Rules: Thậm tr&iacute; ngay trong m&ocirc;i trường Mầm non, trẻ cũng gặp kh&oacute; khăn khi tu&acirc;n theo c&aacute;c quy tắc của lớp học, g&acirc;y rối trong lớp học, c&oacute; những h&agrave;nh vi kh&ocirc;ng đ&uacute;ng đối với bạn b&egrave; v&agrave; thầy c&ocirc; trong lớp. Điều n&agrave;y ảnh hưởng rất lớn đến mối quan hệ của trẻ với bạn b&egrave; v&agrave; thầy c&ocirc; v&agrave; hơn nữa sẽ ảnh hưởng rất lớn đến kết qủa học tập cũng như sự y&ecirc;u th&iacute;ch khi tới lớp của trẻ. Đối với trẻ từ 3-7 tuổi, việc hiểu v&agrave; tu&acirc;n thủ c&aacute;c quy định của trường lớp gi&uacute;p tạo dựng cho trẻ một nề nếp v&agrave; th&oacute;i quen học tập tốt v&agrave; mang lại hiệu quả học tập cao sau n&agrave;y.</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Getting Ready for Bed: Rất nhiều trẻ gặp kh&oacute; khăn đối với việc ngủ tại lớp hay ở nh&agrave;. Điều n&agrave;y ảnh hưởng đến sức khoẻ của trẻ. Gi&uacute;p trẻ c&oacute; một th&oacute;i quen ngủ tốt cũng l&agrave; một điều hết sức quan trọng.</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Making Good Choices: C&oacute; rất nhiều lựa chọn v&agrave; l&agrave;m thể n&agrave;o để trẻ đưa ra được sự lựa chọn đ&uacute;ng đắn v&agrave; hợp l&yacute; ở từng thời điểm?</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">School Breaks: sự đ&aacute;nh lộn, c&atilde;i lộn với bạn b&egrave; tại trường lu&ocirc;n l&agrave; điều kh&ocirc;ng thể tr&aacute;nh khỏi khi trẻ đi học. Nhưng l&agrave;m thế n&agrave;o để trẻ c&oacute; th&aacute;i độ t&iacute;ch cực sau những sự việc như vậy v&agrave; c&oacute; những điều chỉnh h&agrave;nh vi hợp l&yacute;.</span></span></li>\r\n</ul>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Safety- An to&agrave;n</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Đ&acirc;y l&agrave; mối quan t&acirc;m h&agrave;ng đầu của c&aacute;c bậc Phụ huynh khi con trẻ kh&ocirc;ng phải l&uacute;c n&agrave;o cũng ở trong tầm mắt của m&igrave;nh. Vậy ch&uacute;ng ta phải chuẩn bị cho trẻ h&agrave;nh trang như thế n&agrave;o để trẻ lu&ocirc;n an to&agrave;n mọi l&uacute;c mọi nơi. Từ việc đi cầu thang an to&agrave;n, tham gia giao th&ocirc;ng, hay việc bị bắt nạt, hoặc gặp người lạ,&hellip;</span></span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Helth- Sức khoẻ của trẻ l&agrave; điều phụ huynh lu&ocirc;n quan t&acirc;m nhưng lại &iacute;t khi để &yacute; đến việc dạy trẻ tự biết bảo vệ sức khoẻ của m&igrave;nh. Vơi c&aacute;c chủ đề: Taking Care of Me, Trying New Foods, Going the Doctor, Using the Bathroom,&hellip;C&aacute;c chủ đề n&agrave;y sẽ gi&uacute;p trẻ biết c&aacute;ch bảo vệ m&igrave;nh để c&oacute; một sức khoẻ thật tốt.</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">ĐIỂM ĐẶC BIỆT CỦA CHƯƠNG TR&Igrave;NH:</span></span></p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Chương tr&igrave;nh <strong><span style=\"color:#548dd4\">Avits Socail Skills</span></strong> đặc biệt cần sự trợ gi&uacute;p của Phụ huynh trong từng bải giải của gi&aacute;o vi&ecirc;n tr&ecirc;n lớp th&ocirc;ng qua c&aacute;c Bảng c&acirc;u hỏi, Phiếu khảo s&aacute;t phụ huynh/học sinh, v&agrave; những hướng dẫn cụ thể đối với Phụ huynh về việc gi&aacute;m s&aacute;t, hỗ trợ học sinh thực h&agrave;nh c&aacute;c kỹ năng đ&atilde; học.</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">B&aacute;o c&aacute;o kết quả học tập h&agrave;ng ng&agrave;y của học sinh: Việc trao đổi với Phụ huynh được duy tr&igrave; sau từng buổi học qua đ&oacute; Phụ huynh biết được c&aacute;c con được học những g&igrave; v&agrave; việc ứng dụng của c&aacute;c kỹ năng đ&atilde; học trong cuộc sống như thế n&agrave;o. Ch&uacute;ng t&ocirc;i quan niệm, kiến thức c&aacute;c con học được rất quan trọng nhưng quan trọng hơn l&agrave; c&aacute;c con thể hiện n&oacute; như thế n&agrave;o trong c&aacute;c hoạt động x&atilde; hội của m&igrave;nh.</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">CẤP ĐỘ</span></span></p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Chương tr&igrave;nh <strong><span style=\"color:#548dd4\">Avits Socail Skills</span></strong> được ph&acirc;n th&agrave;nh 2 cấp độ</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Cấp độ Mầm Non- <strong><span style=\"color:#548dd4\">Avits Socail Skills -</span></strong> for Kindergarten ( K) d&agrave;nh cho học sinh từ 4-6 tuổi</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:Calibri,sans-serif\">Cấp độ Tiểu học- <strong><span style=\"color:#548dd4\">Avits Socail Skills</span></strong><span style=\"color:#548dd4\"> &nbsp;- </span>for Primary School ( PS) d&agrave;nh cho học sinh từ 6 đến 12 tuổi</span></span></li>\r\n</ul>\r\n', 'image-4-770x550.jpg', 0, 0, '', '2018-03-30 10:03:25', '2018-07-19 16:07:39', 1, '', '', '', '', '', 'AVITS SOCIAL SKILLS', '', 'avits-social-skills', '', 1),
(105, 'Lisp intensive treatment', 'Khóa học  Lisp intensive treatment dành cho các bé có độ tuổi từ 4 đến 10 tuổi. ', '<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:18.0pt\"><span style=\"color:#222222\">N&oacute;i ngọng l&agrave; hiện tượng rối loạn ng&ocirc;n ngữ</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"color:#222222\">Ng&ocirc;n ngữ của trẻ được h&igrave;nh th&agrave;nh tr&ecirc;n cơ sở c&aacute;c phản xạ c&oacute; điều kiện, dựa tr&ecirc;n t&aacute;c động của c&aacute;c yếu tố từ m&ocirc;i trường b&ecirc;n ngo&agrave;i k&iacute;ch th&iacute;ch v&agrave;o trung t&acirc;m nghe.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"color:#222222\">Nếu c&oacute; sự trục trặc n&agrave;o đ&oacute; trong qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh ng&ocirc;n ngữ sẽ xảy ra hiện tượng rối loạn ng&ocirc;n ngữ ở trẻ, điển h&igrave;nh l&agrave; ngọng.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"color:#222222\">C&oacute; 2 dạng n&oacute;i ngọng ở trẻ</span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">N&oacute;i ngọng sinh l&yacute;: cơ quan ph&aacute;t &acirc;m c&oacute; lỗi bẩm sinh như ngắn lưỡi, đầy lưỡi&hellip;</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">N&oacute;i ngọng mang t&iacute;nh x&atilde; hội: ph&aacute;t &acirc;m lệch so với chuẩn.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:18.0pt\"><span style=\"color:#222222\">Nguy&ecirc;n nh&acirc;n khiến cho trẻ n&oacute;i ngọng</span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">L&agrave; do trẻ tự b&oacute;p m&eacute;o &acirc;m thanh để truyền đạt &yacute; m&igrave;nh cho người kh&aacute;c hiểu theo suy nghĩ ri&ecirc;ng của trẻ.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">Cha, mẹ kh&ocirc;ng sửa ngay những từ n&oacute;i sai, khiến trẻ tạo th&agrave;nh th&oacute;i quen m&agrave; lặp lại.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">Cha mẹ v&agrave; những người xung quanh sử dụng sai ng&ocirc;n ngữ khiến con c&aacute;i bắt chước.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">Một số bệnh khi trẻ mắc phải g&acirc;y kh&oacute; thở, ngạt mũi khiến khi n&oacute;i trẻ phải th&egrave; lưỡi ra để ph&aacute;t &acirc;m cũng l&agrave; nguy&ecirc;n nh&acirc;n khiến trẻ n&oacute;i ngọng.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:18.0pt\"><span style=\"color:#222222\">Phương ph&aacute;p sửa n&oacute;i ngọng</span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">Gi&uacute;p b&eacute; thoải m&aacute;i, thả l&ograve;ng người v&agrave; thật b&igrave;nh tĩnh trước khi n&oacute;i.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">Kh&ocirc;ng hỏi dồn khiến b&eacute; l&uacute;ng t&uacute;ng, n&oacute;i lắp, ngọng&hellip;</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">Dạy b&eacute; c&aacute;ch đặt lưỡi thế n&agrave;o, hơi bật ra l&agrave;m sao v&agrave; l&agrave;m mẫu để b&eacute; dễ d&agrave;ng bắt chước v&agrave; học theo.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">N&oacute;i chuyện, h&aacute;t cho b&eacute; nghe: d&ugrave;ng từ ngữ thật chuẩn v&agrave; thường xuy&ecirc;n, b&eacute; sẽ c&oacute; một qu&aacute; tr&igrave;nh để bắt chước theo những b&agrave;i h&aacute;t, c&acirc;u chuyện m&agrave; bạn kể.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">Với những từ b&eacute; bị ngọng ch&uacute;ng ta sẽ kể lại phần đ&oacute; nhiều lần để b&eacute; ghi nhớ v&agrave; l&agrave;m theo.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">Cho b&eacute; tiếp x&uacute;c với m&ocirc;i trường rộng lớn b&ecirc;n ngo&agrave;i. Việc tăng cường những hoạt động giao tiếp, nhất l&agrave; ở chỗ đ&ocirc;ng người sẽ khiến b&eacute; nhanh nhẹn, mau miệng hơn.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">Hạn chế để b&eacute; tiếp x&uacute;c với người hay bị n&oacute;i ngọng.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"color:#222222\">Khi con ngọng, tuyệt đối kh&ocirc;ng nhại lại, điều n&agrave;y khiến b&eacute; sẽ kh&ocirc;ng &yacute; thức được việc ph&aacute;t &acirc;m chuẩn l&agrave; việc n&ecirc;n l&agrave;m.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"color:#1d2129\">MỤC Đ&Iacute;CH SỬA PH&Aacute;T &Acirc;M CHO CON L&Agrave; G&Igrave;?</span></span></span><br />\r\n<span style=\"font-size:10.5pt\"><span style=\"color:#1d2129\"><span style=\"background-color:white\">Ph&aacute;t &acirc;m đ&uacute;ng kh&ocirc;ng phải mục ti&ecirc;u duy nhất v&agrave; nếu đạt được cũng chưa phải l&agrave; kết quả cuối c&ugrave;ng m&agrave; American Skills mong muốn hướng đến.</span><br />\r\n<span style=\"background-color:white\">Hơn thế nữa,</span><br />\r\n<span style=\"background-color:white\">C&aacute;c con c&ograve;n phải n&oacute;i lưu lo&aacute;t, tr&ocirc;i chảy, giao tiếp tốt với người xung quanh.</span><br />\r\n<span style=\"background-color:white\">Ch&iacute;nh v&igrave; vậy, sau khi kết th&uacute;c việc sửa ph&aacute;t &acirc;m, c&aacute;c b&eacute; sẽ tiếp tục trải nghiệm những buổi tập phản xạ để c&oacute; thể n&oacute;i lưu lo&aacute;t hoặc c&oacute; những buổi ngồi tr&ograve; chuyện c&ugrave;ng người lạ để r&egrave;n luyện th&ecirc;m về ng&ocirc;n ngữ.<br />\r\nTất cả đều nằm trong chương tr&igrave;nh r&egrave;n luyện sửa ph&aacute;t &acirc;m cho c&aacute;c con của American Skills.<br />\r\nNội dung Chương tr&igrave;nh :</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:black\">Bước đầu ti&ecirc;n khi dạy, American Skills sẽ đi từ một phụ &acirc;m hay một nguy&ecirc;n &acirc;m chứ kh&ocirc;ng n&ecirc;n đi từ tiếng hoặc từ, trẻ kh&oacute; c&oacute; thể bắt chước được. Sau khi đ&atilde; n&oacute;i tốt &acirc;m đ&oacute;, ch&uacute;ng ta mới gh&eacute;p v&agrave;o c&aacute;c tiếng, c&aacute;c từ, cụm từ, c&aacute;c c&acirc;u v&agrave; cuối c&ugrave;ng l&agrave; hội thoại.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:black\">Người hướng dẫn sẽ l&agrave;m mẫu &acirc;m cần dạy, giới thiệu c&aacute;ch tạo n&ecirc;n &acirc;m đ&oacute; v&agrave; hướng dẫn cho trẻ l&agrave;m. Đặt một c&aacute;i gương trước mặt, người hướng dẫn sẽ l&agrave;m mẫu v&agrave; n&oacute;i về vị tr&iacute; tạo n&ecirc;n &acirc;m đ&oacute; (vị tr&iacute; của lưỡi, h&igrave;nh d&aacute;ng, độ mở của miệng, cử động của v&ograve;m miệng&hellip;), c&aacute;ch tạo n&ecirc;n &acirc;m đ&oacute; (c&aacute;ch lấy hơi v&agrave; đẩy hơi) v&agrave; sự cảm nhận độ rung của d&acirc;y thanh nếu c&oacute;.<br />\r\nSau khi trẻ đ&atilde; n&oacute;i tốt c&aacute;c &acirc;m độc lập, người hướng dẫn chọn từ 15-20 bức tranh c&oacute; &acirc;m đ&oacute; (v&iacute; dụ: nếu ch&uacute;ng ta dạy &acirc;m &ldquo;s&rdquo;, ch&uacute;ng ta sẽ chọn ra những bức tranh c&oacute; phụ &acirc;m đầu l&agrave; &ldquo;s&rdquo; như: &ldquo;s&ocirc;ng&rdquo;, &ldquo;sao&rdquo;, &ldquo;s&aacute;ch&rdquo;&hellip;) để dạy trẻ ph&aacute;t &acirc;m v&agrave;o c&aacute;c tiếng v&agrave; từ.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:black\">Việc sửa c&aacute;c &acirc;m lỗi được coi như l&agrave; ho&agrave;n th&agrave;nh khi trẻ n&oacute;i đ&uacute;ng được c&aacute;c &acirc;m lỗi trong mọi hội thoại, ở mọi ho&agrave;n cảnh kh&aacute;c nhau. Tuy nhi&ecirc;n, việc n&oacute;i tốt c&aacute;c &acirc;m trong từ hoặc c&acirc;u ngắn kh&ocirc;ng đồng nghĩa với việc trẻ sẽ n&oacute;i tốt trong hội thoại. Để l&agrave;m tốt được việc n&agrave;y th&igrave; ngo&agrave;i sự cố gắng của trẻ c&ograve;n cần đến sự tham gia của c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh trẻ v&agrave; những người giao tiếp thường xuy&ecirc;n với trẻ. Sau &acirc;m được chỉnh xong, cứ 3 th&aacute;ng 1 lần, cần thiết phải kiểm tra lại sự ph&aacute;t &acirc;m của trẻ. Nếu trong 1 năm, trong giao tiếp của trẻ kh&ocirc;ng c&ograve;n c&aacute;c &acirc;m sai xuất hiện th&igrave; việc chỉnh &acirc;m kết th&uacute;c.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><a href=\"http://blueoceankids.edu.vn/tieng-anh-tre-em.html\" style=\"color:blue; text-decoration:underline\"><span style=\"font-size:10.5pt\">Kh&oacute;a học </span></a><strong>&nbsp;</strong><strong><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"color:#222222\">Lisp intensive treatment </span></span></span></strong><span style=\"font-size:10.5pt\"><span style=\"color:black\">d&agrave;nh cho c&aacute;c b&eacute; c&oacute; độ tuổi từ 4 đến 10 tuổi. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:10.5pt\"><span style=\"color:black\">Đối tượng:</span></span></strong><span style=\"font-size:10.5pt\"><span style=\"color:black\">&nbsp;d&agrave;nh cho c&aacute;c b&eacute; c&oacute; độ tuổi từ 4 đến 10 tuổi. </span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:10.5pt\"><span style=\"color:black\">Thời gian học:</span></span></strong><span style=\"font-size:10.5pt\"><span style=\"color:black\">&nbsp;24 giờ/ cấp độ, k&eacute;o d&agrave;i khoảng 3 th&aacute;ng</span></span><br />\r\n<strong><span style=\"font-size:10.5pt\"><span style=\"color:black\">Cấp độ:</span></span></strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:black\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.5pt\">&nbsp;Starters </span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:black\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.5pt\">&nbsp; SC</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:10.5pt\"><span style=\"color:black\">Gi&aacute;o tr&igrave;nh</span></span></strong><span style=\"font-size:10.5pt\"><span style=\"color:black\">: Bi&ecirc;n soạn độc quyền của American Skills</span></span><br />\r\n<strong><span style=\"font-size:10.0pt\"><span style=\"color:black\">Nh&oacute;m học</span></span></strong><span style=\"font-size:10.0pt\"><span style=\"color:black\"> : 1 gi&aacute;o vi&ecirc;n hướng dẫn 1-2 học sinh</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:10.5pt\"><span style=\"color:darkorange\">Phương ph&aacute;p giảng dạy khoa học cho từng độ tuổi:</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:10.5pt\"><span style=\"color:black\">Kiểm tra đ&aacute;nh gi&aacute;</span></span></strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:black\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.5pt\">Kiểm tra: C&oacute; hai b&agrave;i kiểm tra giữa v&agrave; cuối kh&oacute;a để gi&uacute;p c&aacute;c gi&aacute;o vi&ecirc;n đ&aacute;nh gi&aacute; khả năng tiến bộ của trẻ trong từng giai đoạn.&nbsp;</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:black\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.5pt\">Kết quả kiểm tra đ&aacute;nh gi&aacute; được th&ocirc;ng b&aacute;o tới Phụ huynh .</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:black\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.5pt\">H&agrave;ng ng&agrave;y Gi&aacute;o vi&ecirc;n sẽ th&ocirc;ng b&aacute;o kết quả r&egrave;n &acirc;m v&agrave; hướng dẫn Phụ huynh c&aacute;ch luyện cho b&eacute; tại nh&agrave; để đạt hiệu quả cao nhất</span></span></span></span></span></li>\r\n</ul>\r\n', 'image-1-770x550.jpg', 0, 0, '', '2018-03-30 10:03:56', '2018-07-19 16:07:59', 1, '', '', '', '', '', 'Lisp intensive treatment', '', 'lisp-intensive-treatment', '', 1);
INSERT INTO `news` (`news_id`, `news_name`, `news_des`, `news_content`, `news_img`, `news_views`, `newscat_id`, `news_author`, `news_created_date`, `news_update_date`, `state`, `news_sub_info1`, `news_sub_info2`, `news_sub_info3`, `news_sub_info4`, `news_sub_info5`, `title_seo`, `des_seo`, `friendly_url`, `keyword`, `created_id`) VALUES
(106, 'Toán mầm non', '', '<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">CHƯƠNG TR&Igrave;NH TIỀN TIỂU HỌC : (D&agrave;nh cho trẻ từ 4-6 tuổi)</span></span></strong></span></span></p>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">avits MATH FOR PRE- SCHOOL: </span></span><span style=\"font-size:11.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">trang bị cho trẻ những kiến thức nền tảng của tư duy to&aacute;n học, tiếp cận v&agrave; ph&aacute;t triển tư duy số, khơi dậy niềm hứng th&uacute; với m&ocirc;n To&aacute;n tr&ecirc;n cơ sở thấu hiểu bản chất của c&aacute;c kh&aacute;i niệm to&aacute;n v&agrave; sự ứng dụng của to&aacute;n để giải quyết vấn đề thế giới thực.</span></span></span><br />\r\n<span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Với 3 cấp độ: Starter =&gt; K =&gt; SC</span></span></strong></span></span></h2>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">GỒM C&Aacute;C CHỦ ĐỀ:</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Color - M&agrave;u sắc</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Ngo&agrave;i &nbsp;việc &nbsp;nhận &nbsp;biết, &nbsp;ph&acirc;n &nbsp;biệt &nbsp;v&agrave; &nbsp;đọc &nbsp;t&ecirc;n &nbsp;m&agrave;u sắc c&aacute;c em c&ograve;n biết th&ocirc;ng qua m&agrave;u sắc ph&acirc;n biệt sự giống nhau v&agrave; kh&aacute;c nhau của sự vật, hiện tượng. T&igrave;m ra quy luật của sự vận động m&agrave;u sắc trong tự nhi&ecirc;n.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Material - Chất liệu</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Chất liệu l&agrave; một thuộc t&iacute;nh quan trọng gi&uacute;p trẻ cảm nhận được c&aacute;c đồ vật, ph&acirc;n biệt đặc t&iacute;nh của c&aacute;c đồ &nbsp;vật. &nbsp;R&egrave;n &nbsp;luyện &nbsp;kỹ &nbsp;năng tư &nbsp;duy &nbsp;của &nbsp;trẻ &nbsp;về &nbsp;sự giống v&agrave; kh&aacute;c nhau của c&aacute;c đồ vật th&ocirc;ng qua chất liệu. Qua đ&oacute; gi&uacute;p c&aacute;c em thấy được sự đa dạng của cuộc sống.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Size - K&iacute;ch thước</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Trẻ bước đầu được l&agrave;m quen với k&iacute;ch cỡ to/ nhỏ, ngắn/d&agrave;i. &nbsp;Biết &nbsp;so &nbsp;s&aacute;nh &nbsp;hơn/nhất. &nbsp;Ban &nbsp;đầu &nbsp;h&igrave;nh th&agrave;nh tư duy ước lượng v&agrave; tỷ lệ.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Shape - H&igrave;nh dạng</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">H&igrave;nh &nbsp;dạng &nbsp;l&agrave; &nbsp;kh&aacute;i &nbsp;niệm &nbsp;ban &nbsp;đầu &nbsp;của &nbsp;tư &nbsp;duy &nbsp;về h&igrave;nh học. Qua chủ để n&agrave;y c&aacute;c em kh&ocirc;ng chỉ dừng lại ở việc nhận biết h&igrave;nh dạng m&agrave; c&ograve;n biết được đặc điểm của c&aacute;c dạng h&igrave;nh, so s&aacute;nh sự giống v&agrave; kh&aacute;c nhau của h&igrave;nh. Gi&uacute;p c&aacute;c em ph&aacute;t triển khả năng quan s&aacute;t, ph&acirc;n t&iacute;ch v&agrave; tư duy về h&igrave;nh.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Classify - Ph&acirc;n loại</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Ph&acirc;n loại l&agrave; chủ đề tư duy tổng hợp của c&aacute;c chủ đề tr&ecirc;n. Qua chủ đề n&agrave;y học sinh được ứng dụng c&aacute;c kiến thức đ&atilde; học v&agrave;o c&aacute;c dạng b&agrave;i tập ph&acirc;n t&iacute;ch v&agrave; tư duy tổng hợp. Gi&uacute;p học sinh r&egrave;n luyện khả năng tư duy về h&igrave;nh v&agrave; ph&aacute;t triển khả năng qua s&aacute;t nhạy b&eacute;n.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Positions - Vị tr&iacute;</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Qua chủ đề n&agrave;y học sinh c&oacute; được những kh&aacute;i niệm ban &nbsp;đầu &nbsp;về &nbsp;kh&ocirc;ng &nbsp;gian &nbsp;v&agrave; &nbsp;l&agrave; &nbsp;tiền &nbsp;đề &nbsp;cho &nbsp;h&igrave;nh &nbsp;học kh&ocirc;ng gian sau n&agrave;y. Tư duy về vị tr&iacute; trong kh&ocirc;ng gian gi&uacute;p &nbsp;trẻ &nbsp;ph&aacute;t &nbsp;triển &nbsp;khả &nbsp;năng &nbsp;tưởng &nbsp;tượng &nbsp;v&agrave; &nbsp;định hướng trong kh&ocirc;ng gian tốt.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Patterns - Quy luật</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">L&agrave; chủ đề điển h&igrave;nh của kỹ năng tư duy của bộ n&atilde;o. Chủ đề n&agrave;y gi&uacute;p r&egrave;n luyện chỉ số IQ của trẻ, gi&uacute;p trẻ ph&aacute;t triển khả năng quan s&aacute;t, ph&acirc;n t&iacute;ch, ph&aacute;n đo&aacute;n, suy luận logic.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Numbers and counting - Tư duy số</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Tư duy số l&agrave; chủ đề kh&ocirc;ng thể thiếu gi&uacute;p trẻ học giỏi To&aacute;n. Ngo&agrave;i việc gi&uacute;p trẻ nhận diện v&agrave; viết số, th&ocirc;ng qua &nbsp;c&aacute;c &nbsp;b&agrave;i &nbsp;tập &nbsp;ứng dụng &nbsp;sẽ &nbsp;gi&uacute;p &nbsp;c&aacute;c &nbsp;em &nbsp;hiểu &nbsp;bản chất của số, vị tr&iacute; của số tr&ecirc;n trục số, khoảng c&aacute;ch của c&aacute;c số. Học đếm sẽ gi&uacute;p trẻ tăng khả năng tập trung, ph&aacute;t triển tr&iacute; n&atilde;o v&agrave; h&igrave;nh th&agrave;nh nếp nhăn của n&atilde;o bộ.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Sorting, ordering, and classifying - Sắp xếp, thứ tự, ph&acirc;n nh&oacute;m</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Khi đ&atilde; hiểu bản chất của c&aacute;c số c&aacute;c em sẽ được r&egrave;n luyện sắp xếp thứ tự c&aacute;c số theo y&ecirc;u cầu. L&agrave;m quen với việc ph&acirc;n nh&oacute;m theo số lượng v&agrave; hiểu được bản chất của nh&oacute;m. Tư duy về nh&oacute;m l&agrave; một dạng tư duy ho&agrave;n to&agrave;n mới m&agrave; từ trước đến nay c&aacute;c học sinh &iacute;t được tiếp cận. Tư duy nh&oacute;m gi&uacute;p c&aacute;c em giải quyết được rất nhiều dạng b&agrave;i tập sau n&agrave;y v&agrave; thậm ch&iacute; cả c&aacute;c b&agrave;i to&aacute;n k&iacute;nh tế, x&atilde; hội cũng như tiếp cận c&aacute;c m&ocirc;n học kh&aacute;c dễ d&agrave;ng hơn.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Fractions - Ph&acirc;n số</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">L&agrave; một chủ đề ho&agrave;n to&agrave;n mới với c&aacute;c em học sinh ở tuổi Mầm mon. Nhưng với c&aacute;ch tiếp cận thực tế v&agrave; trực quan th&ocirc;ng qua việc chia đồ vật, chia h&igrave;nh chương tr&igrave;nh gi&uacute;p c&aacute;c em hiểu bản chất của ph&acirc;n số v&agrave; t&igrave;m được gi&aacute; trị của ph&acirc;n số một c&aacute;ch dễ d&agrave;ng.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Measurement - Đo lường</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Gi&uacute;p c&aacute;c em c&oacute; kh&aacute;i niệm ban đầu về k&iacute;ch thước bằng c&aacute;ch so s&aacute;nh với c&aacute;c vật kh&aacute;c. Gi&uacute;p c&aacute;c em ph&aacute;t triển tư duy về tỷ lệ v&agrave; khả năng ước lượng. Ngo&agrave;i ra, c&aacute;c em c&ograve;n biết so s&aacute;nh c&aacute;c nh&oacute;m vật với nhau như: d&agrave;i hơn/ d&agrave;i nhất, ngắn hơn/ ngắn nhất, thấp hơn/ thấp nhất,&hellip;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Time - Tư duy thời gian</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Thời gian l&agrave; nh&acirc;n tố v&ocirc; c&ugrave;ng quan trọng với cuộc sống của mỗi người. Biết tư duy về thời gian sẽ gi&uacute;p trẻ biết sắp xếp kế hoạch học tập v&agrave; vui chơi ph&ugrave; hợp. Hiểu về sự vận động của thời gian sẽ gi&uacute;p c&aacute;c em c&oacute; &yacute; thức v&agrave; biết c&aacute;ch sử dụng thời gian tốt hơn v&agrave; hiệu quả hơn.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Comparing - So s&aacute;nh</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Qua chủ đề n&agrave;y gi&uacute;p c&aacute;c em hiểu được mối tương quan giữa c&aacute;c sự vật v&agrave; hiện tượng qua đ&oacute; c&aacute;c em thấy được quy luật của To&aacute;n học. Đ&acirc;y l&agrave; một chủ đề tư duy tổng hợp của phần tư duy số. Để giải quyết được c&aacute;c dạng b&agrave;i tập trong chủ đề n&agrave;y c&aacute;c em phải c&oacute; tư duy tốt về lượng, nh&oacute;m, về tương quan tỷ lệ.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Addition - Ph&eacute;p cộng</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Ph&eacute;p cộng l&agrave; chủ đề kh&ocirc;ng thể thiếu trong To&aacute;n học. Math Skills kh&ocirc;ng ch&uacute; trọng đến kết quả của ph&eacute;p t&iacute;nh m&agrave; quan t&acirc;m đến phương ph&aacute;p để đi đến kết quả đ&oacute;. Với phương ph&aacute;p tư duy số kh&ocirc;ng chỉ gi&uacute;p c&aacute;c em t&iacute;nh nhẩm nhanh, nhẩm đ&uacute;ng m&agrave; c&ograve;n gi&uacute;p ph&aacute;t triển tư duy v&agrave; tr&iacute; n&atilde;o.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Subtraction - Ph&eacute;p trừ</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Ph&eacute;p trừ l&agrave; dạng ngược lại của ph&eacute;p cộng. &nbsp;Tư duy ngược đ&ocirc;i khi kh&oacute; hơn tư duy xu&ocirc;i. Với phương ph&aacute;p tư duy Math Skills gi&uacute;p tr&iacute; n&atilde;o của c&aacute;c em hoạt động đa chiều v&agrave; linh hoạt.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:fuchsia\">Proportional thinking - Tư duy tỷ lệ</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Đ&acirc;y l&agrave; đỉnh cao của tư duy số. B&agrave;i tập tư duy tỷ lệ gi&uacute;p r&egrave;n luyện khả năng tư duy tổng hợp, khả năng định lượng v&agrave; tư duy nh&oacute;m.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:black\">1. Cấp độ Starter : </span></strong><span style=\"background-color:white\"><span style=\"color:black\">sẽ bắt đầu được tiếp cận dần với c&aacute;c nội dung To&aacute;n căn bản. Bắt đầu với c&aacute;c nội dung nền tảng: ph&acirc;n biệt h&igrave;nh dạng, m&agrave;u sắc, chất liệu, con số để dần dần y&ecirc;u th&iacute;ch việc tương t&aacute;c với con số. Từ đ&oacute; c&aacute;c b&eacute; sẽ y&ecirc;u th&iacute;ch dần m&ocirc;n To&aacute;n học tưởng chừng như rất kh&ocirc; khan.</span></span><br />\r\n<span style=\"color:black\"><span style=\"background-color:white\">Khi đ&atilde; y&ecirc;u th&iacute;ch m&ocirc;n học, trẻ sẽ tiếp tục được kh&aacute;m ph&aacute; c&aacute;c kiến thức To&aacute;n n&acirc;ng cao hơn: Đếm &ndash; viết, sắp xếp thứ tự c&aacute;c con số. Dần dần từ đ&oacute; mới tiếp cận đến c&aacute;c ph&eacute;p t&iacute;nh cộng v&agrave; trừ. &nbsp;Đ&acirc;y cũng ch&iacute;nh l&agrave; nền tảng để ph&aacute;t triển tư duy v&agrave;o lứa tuổi n&agrave;y cho trẻ.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:black\">2. Cấp độ Kindergarten&nbsp;(K) : </span></strong><span style=\"color:black\">được x&acirc;y dựng đặc th&ugrave; d&agrave;nh cho trẻ từ 5 đến 6 tuổi.<br />\r\n&nbsp;<br />\r\nTrẻ được l&agrave;m quen, kh&aacute;m ph&aacute; c&aacute;c con số th&ocirc;ng qua c&aacute;c nội dung: số liền trước - số liền sau, đếm xu&ocirc;i - đếm ngược, đơn vị&hellip; để hiểu được thứ tự v&agrave; bản chất c&aacute;c con số. Trẻ được t&igrave;m hiểu th&ecirc;m về c&aacute;c h&igrave;nh học: h&igrave;nh oval, h&igrave;nh thoi, h&igrave;nh 5 cạnh đồng thời biết chia h&igrave;nh, gh&eacute;p h&igrave;nh, s&aacute;ng tạo h&igrave;nh để tạo ra c&aacute;c h&igrave;nh mới. Thay v&igrave; trẻ học c&aacute;ch cộng, trừ &ldquo;một c&aacute;ch m&aacute;y m&oacute;c&rdquo; bằng việc nhớ ph&eacute;p t&iacute;nh, trẻ tại IXL được học bản chất của c&aacute;c ph&eacute;p t&iacute;nh qua nhiều c&aacute;ch tiếp cận kh&aacute;c nhau. VD: C&oacute; 2 nh&oacute;m số lượng kh&aacute;c nhau, cần th&ecirc;m v&agrave;o bao nhi&ecirc;u h&igrave;nh để 2 nh&oacute;m bằng nhau. Đ&acirc;y l&agrave; nền tảng gi&uacute;p trẻ biết c&aacute;ch tư duy đ&uacute;ng về To&aacute;n học. V&agrave; khi đ&atilde; hiểu điều nay, trẻ sẽ rất th&iacute;ch học m&ocirc;n To&aacute;n.<br />\r\n&nbsp;<br />\r\n<strong>3. Cấp độ Starter School (SC) : </strong>được x&acirc;y dựng đặc th&ugrave; d&agrave;nh cho trẻ&nbsp;<strong>đang chuẩn bị v&agrave;o lớp 1.</strong><br />\r\nThời điểm n&agrave;y rất quan trọng đối với trẻ v&igrave; đ&acirc;y l&agrave; giai đoạn chuyển tiếp m&ocirc;i trường học tập từ mầm non l&ecirc;n tiểu học.<br />\r\n&nbsp;<br />\r\nTrẻ sẽ được tiếp cận với c&aacute;c kiến thức to&aacute;n một c&aacute;ch logic hơn. Thay v&igrave; học ngay c&aacute;c ph&eacute;p cộng, ph&eacute;p trừ, trẻ vẫn sẽ được tiếp cận c&aacute;c nội dung nền tảng từ quy luật, h&igrave;nh học... V&agrave; dần dần l&agrave;m quen với c&aacute;c kh&aacute;i niệm to&aacute;n học: trục số, đếm c&aacute;ch, t&igrave;m khoảng c&aacute;ch giữa c&aacute;c số.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;<br />\r\n<em>Với chương tr&igrave;nh Tư duy To&aacute;n&nbsp;Mỹ - Avits Math (IXL) - </em></span><span style=\"color:black\">FOR PRE- SCHOOL</span><em><span style=\"color:black\">, trẻ kh&ocirc;ng chỉ thực hiện đ&uacute;ng theo y&ecirc;u cầu, trẻ cần phải giải th&iacute;ch được qu&aacute; tr&igrave;nh đi đến kết quả quả, v&igrave; sao lại l&agrave;m được như vậy để thực h&agrave;nh c&aacute;ch diễn đạt suy nghĩ của m&igrave;nh. C&aacute;c hoạt động thực h&agrave;nh v&agrave; l&agrave;m quen kiến thức được đan xen với nhau trong qu&aacute; tr&igrave;nh học gi&uacute;p trẻ hứng th&uacute; với m&ocirc;n học, tự tin hơn với những b&agrave;i To&aacute;n ở trường. Quan trọng hơn, trẻ được cung cấp c&aacute;c kiến thức rất nền tảng một c&aacute;ch hệ thống v&agrave; hiểu s&acirc;u về bản chất, gi&uacute;p trẻ tư duy v&agrave; ứng dụng v&agrave;o những kiến thức học ở cấp cao hơn.</span></em><br />\r\n<span style=\"color:black\">&nbsp;<br />\r\n<em>Điều đặc biệt của Chương tr&igrave;nh Avits Math (IXL)</em></span><span style=\"color:black\"> FOR PRE- SCHOOL</span><em><span style=\"color:black\"> l&agrave; lu&ocirc;n c&oacute; hoạt động s&aacute;ng tạo mang t&iacute;nh ứng dụng của nội dung vừa học v&agrave;o thực tế. Khi trẻ biết c&aacute;ch vận dụng những điều m&igrave;nh vừa học v&agrave;o cuộc sống, trẻ hiểu bản chất kiến thức v&agrave; sẽ nhớ rất l&acirc;u nội dung học đ&oacute;. Từ đ&oacute; l&agrave;m nền tảng để ph&aacute;t triển tư duy, tr&iacute; th&ocirc;ng minh v&agrave; t&iacute;nh s&aacute;ng tạo của trẻ lứa tuổi n&agrave;y.</span></em></span></span></span></p>\r\n', 'image-2-770x550.jpg', 0, 68, '', '2018-04-02 13:04:52', '2018-07-19 16:07:30', 1, '', '', '', '', '', 'Toán mầm non', '', 'toan-mam-non', '', 1),
(107, 'Toán tiểu học', '', '<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">CHƯƠNG TR&Igrave;NH TIỂU HỌC </span></span></strong></span></span></h2>\r\n\r\n<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">avits math (IXL) - FOR PRIMARY SCHOOL :</span></span></strong></span></span></h2>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">D&agrave;nh cho học sinh tiểu học (6-10 tuổi)&nbsp;được thiết kế nội dung song song với kiến thức to&aacute;n tr&ecirc;n trường của học sinh gi&uacute;p bổ trợ c&aacute;c kiến thức cần thiết cho học sinh. Ngo&agrave;i ra Avits Math c&ograve;n bổ sung một số dạng to&aacute;n quốc tế mang t&iacute;nh ứng dụng cao gi&uacute;p c&aacute;c em ph&aacute;t triển khả năng tư duy, biết ứng dụng to&aacute;n v&agrave;o thực tế.</span></span><br />\r\n<br />\r\n<span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Với phương ph&aacute;p tiệp cận vấn đề To&aacute;n ho&agrave;n to&agrave;n mới v&agrave; khoa học, đi từ dễ đến kh&oacute;, đi từ cơ bản đến phức tạp. Avits Math </span></span><span style=\"font-size:11.0pt\"><span style=\"color:black\">(IXL) </span></span><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">gi&uacute;p học sinh hiểu bản chất vấn đề, c&oacute; kiến thức To&aacute;n vững chắc v&agrave; kh&ocirc;ng c&ograve;n cảm thấy &aacute;p lực với m&ocirc;n To&aacute;n.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><br />\r\n<span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Nội dung Chương tr&igrave;nh được thiết kế với 5 cấp độ:</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Level 1 &ndash; Level 2 &ndash; Level 3 &ndash; Level 4 &ndash; Level 5</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:black\">1. Tư duy số v&agrave; c&aacute;c ph&eacute;p to&aacute;n (<em>Number sense &amp; operations</em>)</span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">Hiểu r&otilde; c&aacute;c kh&aacute;i niệm, bản chất về số tự nhi&ecirc;n, cấu tạo số l&agrave;m tiền đề để r&egrave;n luyện kỹ năng t&iacute;nh to&aacute;n v&agrave; ph&aacute;t triển khả năng t&iacute;nh nhẩm, t&iacute;nh nhanh v&agrave; tư duy logic cho trẻ. Hiểu r&otilde; bản chất của c&aacute;c ph&eacute;p to&aacute;n cộng, trừ, nh&acirc;n, chia để đưa ra phương ph&aacute;p t&iacute;nh&nbsp; to&aacute;n ph&ugrave; hợp v&agrave; cho kết quả nhanh, ch&iacute;nh x&aacute;c nhất.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:black\">2.Ph&acirc;n số (<em>Fractions</em>)</span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">Giới thiệu về ph&acirc;n số cho học sinh hiểu r&otilde; kh&aacute;i niệm &ldquo;ph&acirc;n số l&agrave; g&igrave;?&rdquo; từ những h&igrave;nh ảnh trực quan. Khi nắm chắc được kh&aacute;i niệm, hiểu được bản chất của ph&acirc;n số học sinh sẽ thực hiện ph&eacute;p t&iacute;nh li&ecirc;n quan đến ph&acirc;n số một c&aacute;ch dễ d&agrave;ng v&agrave; ứng dụng th&agrave;nh thạo trong c&aacute;c b&agrave;i to&aacute;n đố li&ecirc;n quan.<br />\r\n&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:black\">3. Đo lường &ndash; Thời gian (<em>Measurement - Time</em>)</span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">Trẻ nắm vững c&aacute;c đơn vị đo lường </span><span style=\"background-color:#f9f9f9\"><span style=\"color:#3b3b3b\">- thời gian, độ d&agrave;i, diện t&iacute;ch, thể t&iacute;ch, khối lượng, dung t&iacute;ch v&agrave; tiền tệ.</span></span><span style=\"color:black\">, biết &aacute;p dụng v&agrave;o c&aacute;c b&agrave;i to&aacute;n thực tế.<br />\r\n&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:black\">4. H&igrave;nh học (<em>Geometry</em>)</span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">Gi&uacute;p trẻ &nbsp;nhận biết kh&aacute;i niệm, thuộc t&iacute;nh, h&igrave;nh th&agrave;nh của mỗi loại h&igrave;nh kh&aacute;c nhau; mối tương quan giữa c&aacute;c h&igrave;nh. Từ đ&oacute;, trẻ &aacute;p dụng t&iacute;nh diện t&iacute;ch, chu vi hay thể t&iacute;ch của c&aacute;c h&igrave;nh kh&aacute;c nhau.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:black\">5. Tư duy tổng hợp (<em>Mathematical Thinking</em>) </span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">Gi&uacute;p trẻ r&egrave;n luyện nhiều kỹ năng tư duy to&aacute;n tổng hợp th&ocirc;ng qua c&aacute;c dạng to&aacute;n đa dạng như c&acirc;n thăng bằng, to&aacute;n quy luật, tư duy nh&oacute;m, tư duy tỷ lệ, to&aacute;n đố, tư duy l&ocirc;gic, lập v&agrave; đọc biểu đồ, to&aacute;n x&aacute;c suất, l&agrave;m tr&ograve;n, ước lượng &hellip; Trẻ được r&egrave;n luyện nhiều kỹ năng như: đọc đề, ph&acirc;n t&iacute;ch đề b&agrave;i, &nbsp;chia nhỏ c&aacute;c vấn đề trong b&agrave;i to&aacute;n, vận dụng khả năng tư duy logic để việc giải to&aacute;n trở n&ecirc;n đơn giản, hiệu quả v&agrave; ch&iacute;nh x&aacute;c.</span></span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11.0pt\"><span style=\"color:black\">&nbsp;6. </span></span><strong><span style=\"font-size:11.0pt\"><span style=\"background-color:#f9f9f9\"><span style=\"color:black\">Giải to&aacute;n c&oacute; lời văn (<em>Problem solving )</em></span></span></span></strong><br />\r\n<span style=\"font-size:11.0pt\"><span style=\"background-color:#f9f9f9\"><span style=\"color:#3b3b3b\">R&egrave;n luyện c&aacute;c bước gồm: Chia vấn đề th&agrave;nh những phần nhỏ, đơn giản hơn, rồi ph&acirc;n t&iacute;ch, giải to&aacute;n, kiểm tra v&agrave; đ&aacute;nh gi&aacute;.</span></span></span><br />\r\n&nbsp;</p>\r\n', 'image-7-770x550.jpg', 0, 68, '', '2018-04-02 13:04:07', '2018-07-19 16:07:21', 1, '', '', '', '', '', 'Toán tiểu học', '', 'toan-tieu-hoc', '', 1),
(108, 'Toán THCS', '', '<h2 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">CHƯƠNG TR&Igrave;NH thcs </span></span></strong></span></span></h2>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:black\">avits math (IXL) - junior high school</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">Kết hợp v&agrave; n&acirc;ng cao giữa l&yacute; thuyết v&agrave; c&aacute;c b&agrave;i to&aacute;n ứng dụng, ph&aacute;t triển cho học sinh kỹ năng tư duy, sự hiểu biết v&agrave; th&aacute;i độ tự tin. Nắm chắc kiến thức từ cơ bản đến n&acirc;ng cao.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">-&nbsp;T&iacute;ch cực ho&aacute; hoạt động học tập của học sinh, r&egrave;n luyện khả năng tự học, ph&aacute;t hiện v&agrave; giải quyết vấn đề của HS nhằm h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển ở HS tư duy t&iacute;ch cực, độc lập v&agrave; s&aacute;ng tạo.</span></span></span><br />\r\n<span style=\"font-size:10.5pt\"><span style=\"color:#333333\"><span style=\"background-color:white\">&minus; Chọn lựa sử dụng những phương ph&aacute;p ph&aacute;t huy t&iacute;nh t&iacute;ch cực chủ động của Học Sinh trong học tập v&agrave; ph&aacute;t huy khả năng tự học. Hoạt động ho&aacute; việc học tập của HS bằng những dẫn dắt cho HS tự th&acirc;n trải nghiệm chiếm lĩnh tri thức, chống lối học thụ động.</span><br />\r\n<span style=\"background-color:white\">&minus; Tận dụng ưu thế của từng phương ph&aacute;p dạy học, ch&uacute; trọng sử dụng phương ph&aacute;p dạy học ph&aacute;t hiện v&agrave; giải quyết vấn đề.</span><br />\r\n<span style=\"background-color:white\">&minus; Coi trọng cung cấp kiến thức, r&egrave;n luyện kĩ năng vận dụng kiến thức v&agrave;o thực tiễn.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Nội dung Chương tr&igrave;nh được thiết kế với 4 cấp độ:</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#333333\">Level 6 &ndash; Level 7 &ndash; Level 8 &ndash; Level 9</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol>\r\n			<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">Số học : T&iacute;nh chia hết, số nguy&ecirc;n tố, số ch&iacute;nh phương, bội v&agrave; ước c&aacute;c số .</span></span></span></span></li>\r\n			<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">Đại số : Gi&uacute;p học sinh thực hiện th&agrave;nh thạo tất cả c&aacute;c dạng to&aacute;n đại số nền tảng như tỷ lệ thức, biểu thức, ph&acirc;n thức đến c&aacute;c kh&aacute;i niệm n&acirc;ng cao như biến số, h&agrave;m số hay giải c&aacute;c dạng to&aacute;n đại số đặc trưng như phương tr&igrave;nh, bất phương tr&igrave;nh, hệ phương tr&igrave;nh, căn thức. </span></span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:13.5pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">Ngo&agrave;i ra, học sinh được tiếp cận với dạng ph&acirc;n t&iacute;ch dữ liệu từ những thao t&aacute;c đơn giản như đọc dữ liệu, so s&aacute;nh v&agrave; sắp xếp dữ liệu. B&ecirc;n cạnh đ&oacute;, học sinh c&ograve;n biết c&aacute;ch biểu diễn c&aacute;c dữ liệu bằng đồ thị từ đ&oacute; suy luận t&igrave;m ra đ&aacute;p &aacute;n cũng như x&acirc;y dựng c&aacute;c qui luật logic</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:13.5pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><em><span style=\"font-size:11.0pt\"><span style=\"color:#4f4f4f\">Cũng cố v&agrave; bổ sung c&aacute;c kiến thức về chương tr&igrave;nh đại số của BGD&amp;ĐT từ lớp 6 đến lớp 9.</span></span></em></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol start=\"3\">\r\n			<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">H&igrave;nh học : Gi&uacute;p học sinh được l&agrave;m quen về tất cả c&aacute;c h&igrave;nh phẳng trong chương tr&igrave;nh phổ th&ocirc;ng v&agrave; c&aacute;c b&agrave;i to&aacute;n li&ecirc;n quan đến h&igrave;nh phẳng như chu vi, diện t&iacute;ch. C&aacute;c h&igrave;nh kh&ocirc;ng gian cũng được đưa v&agrave;o bằng c&aacute;c m&ocirc; h&igrave;nh trực quan th&uacute; vị nhằm gi&uacute;p học sinh ph&acirc;n biệt c&aacute;c h&igrave;nh phẳng, h&igrave;nh khối. Đồng thời c&aacute;c em c&oacute; kh&aacute;i niệm về thể t&iacute;ch, diện t&iacute;ch xung quanh, diện t&iacute;ch to&agrave;n phần. Đ&acirc;y l&agrave; nền tảng để c&aacute;c em học tốt chương tr&igrave;nh phổ th&ocirc;ng v&agrave; &aacute;p dụng kiễn thức v&agrave;o thực tiễn.</span></span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:13.5pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#3b3b3b\">C&aacute;c chủ đề c&aacute;c em đều được l&agrave;m quen với dạng b&agrave;i to&aacute;n giải quyết vấn đề ở những mức độ kh&aacute;c nhau. Từ những b&agrave;i to&aacute;n c&oacute; lời văn đơn giản đến c&aacute;c b&agrave;i to&aacute;n nhiều bước, lập sơ đồ giải to&aacute;n. Giải quyết vấn đề gi&uacute;p ph&aacute;t triển tư duy logic v&agrave; s&aacute;ng tạo để giải quyết c&aacute;c vấn đề trong cuộc sống. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:13.5pt; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><em><span style=\"font-size:11.0pt\"><span style=\"color:#4f4f4f\">Cũng cố v&agrave; bổ sung c&aacute;c kiến thức về chương tr&igrave;nh h&igrave;nh học của BGD&amp;ĐT từ lớp 6 đến lớp 9.</span></span></em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:black\">4. Luyện thi theo y&ecirc;u cầu : </span></span></span></span></p>\r\n\r\n<h4 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:whitesmoke\"><span style=\"font-family:Cambria,serif\"><span style=\"color:#4f81bd\"><strong><em><span style=\"color:#4f4f4f\">Luyện c&aacute;c loại đề thi của BGD&amp;ĐT, c&aacute;c chương tr&igrave;nh thi v&agrave;o c&aacute;c trường chuy&ecirc;n&hellip;..</span></em></strong></span></span></span></span></h4>\r\n\r\n<h4 style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"background-color:whitesmoke\"><span style=\"font-family:Cambria,serif\"><span style=\"color:#4f81bd\"><strong><em><span style=\"color:#4f4f4f\">Luy&ecirc;n chương tr&igrave;nh của c&aacute;c trường Quốc tế..</span></em></strong></span></span></span></span></h4>\r\n', 'image-6-770x550.jpg', 0, 68, '', '2018-04-02 13:04:18', '2018-07-19 16:07:08', 1, '', '', '', '', '', 'Toán THCS', '', 'toan-thcs', '', 1),
(109, 'Lisp Intensive Treatment Phương pháp sửa nói ngọng', 'Nói ngọng là hiện tượng rối loạn ngôn ngữ. Ngôn ngữ của trẻ được hình thành trên cơ sở các phản xạ có điều kiện, dựa trên tác động của các yếu tố từ môi trường bên ngoài kích thích vào trung tâm nghe. Nếu có sự trục trặc nào đó trong quá trình hình thành ngôn ngữ sẽ xảy ra hiện tượng rối loạn ngôn ngữ ở trẻ, điển hình là ngọng.', '', 'image-4-770x550.jpg', 0, 72, '', '2018-04-05 11:04:35', '2018-07-19 16:07:56', 1, '', '', '', '', '', 'Lisp Intensive Treatment Phương pháp sửa nói ngọng', '', 'lisp-intensive-treatment-phuong-phap-sua-noi-ngong', '', 1),
(110, 'Avits English Ngôn ngữ Tiếng Anh: 4-14 tuổi', 'Sự tự tin sử dụng tiếng Anh là lợi thế cho con bạn ngay từ lúc còn ngồi trên ghế nhà trường, để trẻ có thể tự nhiên phát triển kiến thức và nền tảng thành công cho các cơ hội nghề nghiệp trong tương lai. Các khóa học tiếng Anh cho trẻ em tại Hội đồng Anh được thiết kế đặc biệt dựa trên đặc tính của mỗi cá nhân, và được giảng dạy bởi các chuyên gia tiếng Anh toàn cầu luôn tâm huyết với sự nghiệp giáo dục trẻ em. ', '', 'image-3-770x550.jpg', 0, 72, '', '2018-04-05 11:04:16', '2018-07-19 16:07:47', 1, '', '', '', '', '', 'Avits English Ngôn ngữ Tiếng Anh: 4-14 tuổi', '', 'avits-english-ngon-ngu-tieng-anh-4-14-tuoi', '', 1),
(111, 'White Pager', '', '<p style=\"text-align:center\"><img alt=\"le-xuan-quang\" height=\"425\" src=\"/image/images/le-xuan-quang.jpg\" width=\"600\" /></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><strong><em><span style=\"color:#1d2129\">Bạn L&ecirc; Xu&acirc;n Quang &ndash; Sinh năm 2006 - Học sinh xuất sắc ti&ecirc;u biểu của Americanskills Quận 1</span></em></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><br />\r\n<strong><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#1d2129\"><img alt=\"\" height=\"16\" src=\"file:///C:\\Users\\HUYENT~1\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image002.png\" width=\"16\" />- Huy chương bạc trong k&igrave; thi American Mathematics Oplympiad (AMO) - Cuộc thi to&aacute;n quốc tế Hoa Kỳ</span><br />\r\n<span style=\"color:#1d2129\"><img alt=\"\" height=\"16\" src=\"file:///C:\\Users\\HUYENT~1\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image002.png\" width=\"16\" />- Giải Bạc v&ograve;ng chung kết k&igrave; thi To&aacute;n Olympic Titan.</span></span></span></span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#1d2129\"><img alt=\"\" height=\"16\" src=\"file:///C:\\Users\\HUYENT~1\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image001.png\" width=\"16\" />Ch&uacute;c mừng Xu&acirc;n Quang! American Skills v&ocirc; c&ugrave;ng tự h&agrave;o về th&agrave;nh t&iacute;ch tuyệt vời của con. Ch&uacute;c Xu&acirc;n Quang tiếp tục gặt h&aacute;i được nhiều th&agrave;nh c&ocirc;ng tr&ecirc;n con đường học vấn!&nbsp;<br />\r\n<img alt=\"\" height=\"16\" src=\"file:///C:\\Users\\HUYENT~1\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image003.png\" width=\"16\" />V&agrave; phần thưởng tặng B&eacute;: <strong>Học bổng kh&oacute;a To&aacute;n tư duy n&acirc;ng cao 3.000.000 đ&nbsp;</strong><br />\r\nCh&uacute;c cho Xu&acirc;n Quang gặt h&aacute;i được nhiều th&agrave;nh t&iacute;ch hơn nữa!</span></span></span></span></p>\r\n', 'image-2-770x550.jpg', 0, 73, '', '2018-04-21 15:04:58', '2018-07-19 16:07:38', 1, '', '', '', '', '', 'White Pager', '', 'white-pager', 'học viên tiêu biểu', 1),
(112, 'News', ' Đặc biệt môn Toán - con học tại American Skills nổi bật vượt cấp độ, hiện đã học xong chương trình Lớp 4 - con đang được ôn luyện nâng cao và dần bước sang chương trình Lớp 5.', '<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#1d2129\"><strong>- C&ocirc; b&eacute; rất c&aacute; t&iacute;nh th&ocirc;ng minh n&agrave;y học giỏi to&agrave;n diện c&aacute;c m&ocirc;n :</strong> Đặc biệt m&ocirc;n To&aacute;n - con học tại American Skills nổi bật vượt cấp độ, hiện đ&atilde; học xong chương tr&igrave;nh Lớp 4 - con đang được &ocirc;n luyện n&acirc;ng cao v&agrave; dần bước sang chương tr&igrave;nh Lớp 5.<img alt=\"\" height=\"16\" src=\"file:///C:\\Users\\HUYENT~1\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image001.png\" width=\"16\" /></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#1d2129\">- <strong>AMERICAN SKILLS</strong> v&ocirc; c&ugrave;ng tự h&agrave;o đ&atilde; được đồng h&agrave;nh c&ugrave;ng &nbsp;con 4 năm, v&agrave; th&agrave;nh t&iacute;ch h&ocirc;m nay của c&aacute;c con l&agrave; niềm động vi&ecirc;n, kh&iacute;ch lệ, tự tin với hướng giảng dạy đ&uacute;ng, đủ , đảm bảo h&agrave;nh trang cho c&aacute;c con vững v&agrave;ng.</span></span></span></span><br />\r\n<span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:16px\"><span style=\"color:#1d2129\">- Ch&uacute;c mừng Yến Linh nh&eacute; .</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"ngo-hoang-yen-linh\" height=\"400\" src=\"/image/images/ngo-hoang-yen-linh.jpg\" width=\"600\" /></p>\r\n', 'image-1-770x550.jpg', 0, 73, '', '2018-04-21 16:04:44', '2018-07-19 16:07:23', 1, '', '', '', '', '', 'News', '', 'news', 'học viên tiêu biểu', 1);

