INSERT INTO `productcat` (`productcat_id`, `productcat_name`, `productcat_des`, `productcat_content`, `productcat_parent`, `productcat_sort_order`, `productcat_img`, `productcat_created_date`, `productcat_update_date`, `productcat_sub_info1`, `productcat_sub_info2`, `productcat_sub_info3`, `productcat_sub_info4`, `productcat_sub_info5`, `state`, `title_seo`, `des_seo`, `friendly_url`, `keyword`, `created_id`) VALUES
(111, 'Máy cắt ke góc nhôm', '<p>&nbsp; L&agrave; sản phẩm c&oacute; xuất xứ từ Ph&aacute;p, d&agrave;nh ri&ecirc;ng cho kh&ocirc;ng gian nội thất, c&oacute; thể được lắp đặt tr&ecirc;n tất cả c&aacute;c loại m&agrave;nh, r&egrave;m trong, ngo&agrave;i nh&agrave;: r&egrave;m rủ, r&egrave;m roman, m&agrave;nh cầu vồng, m&agrave;nh cuốn... Cho ph&eacute;p bạn xếp h&agrave;ng tất cả c&aacute;c r&egrave;m của bạn ho&agrave;n hảo với 1 điều khiển từ xa.&nbsp;</p>\r\n', '<p>&nbsp; L&agrave; sản phẩm c&oacute; xuất xứ từ Ph&aacute;p, d&agrave;nh ri&ecirc;ng cho kh&ocirc;ng gian nội thất, c&oacute; thể được lắp đặt tr&ecirc;n tất cả c&aacute;c loại m&agrave;nh, r&egrave;m trong, ngo&agrave;i nh&agrave;: r&egrave;m rủ, r&egrave;m roman, m&agrave;nh cầu vồng, m&agrave;nh cuốn... Cho ph&eacute;p bạn xếp h&agrave;ng tất cả c&aacute;c r&egrave;m của bạn ho&agrave;n hảo với 1 điều khiển từ xa.&nbsp;</p>\r\n', 140, 0, 'tyc1355713641.jpg', '2018-07-19 14:07:02', NULL, '', '', '', '', '', 1, 'Máy cắt ke góc nhôm', '  Là sản phẩm có xuất xứ từ Pháp, dành riêng cho không gian nội thất, có thể được lắp đặt trên tất cả các loại mành, rèm trong, ngoài nhà: rèm rủ, rèm roman, mành cầu vồng, mành cuốn... Cho phép bạn xếp hàng tất cả các rèm của bạn hoàn hảo với 1 điều khiển từ xa. ', 'may-cat-ke-goc-nhom', 'Rèm điều khiển tự động', NULL),
(114, 'Máy đục lỗ khóa nhôm', '<p>Chiếc r&egrave;m cửa kh&ocirc;ng chỉ đơn giản l&agrave; vật chắn s&aacute;ng hay tạo khoảng kh&ocirc;ng ri&ecirc;ng tư, n&oacute; c&ograve;n tạo cho mỗi căn ph&ograve;ng một ấn tượng v&agrave; cảm x&uacute;c kh&aacute;c nhau, t&ugrave;y theo c&aacute;ch chọn lựa của chủ nh&acirc;n.</p>\r\n', '<p>Chiếc r&egrave;m cửa kh&ocirc;ng chỉ đơn giản l&agrave; vật chắn s&aacute;ng hay tạo khoảng kh&ocirc;ng ri&ecirc;ng tư, n&oacute; c&ograve;n tạo cho mỗi căn ph&ograve;ng một ấn tượng v&agrave; cảm x&uacute;c kh&aacute;c nhau, t&ugrave;y theo c&aacute;ch chọn lựa của chủ nh&acirc;n.</p>\r\n', 140, 0, '_mg_0400.jpg', '2018-07-19 14:07:32', NULL, '', '', '', '', '', 1, 'Máy đục lỗ khóa nhôm', 'Chiếc rèm cửa không chỉ đơn giản là vật chắn sáng hay tạo khoảng không riêng tư, nó còn tạo cho mỗi căn phòng một ấn tượng và cảm xúc khác nhau, tùy theo cách chọn lựa của chủ nhân.', 'may-duc-lo-khoa-nhom', 'Rèm cổ điển', NULL),
(115, 'Máy phay đầu đố nhôm', '<p>Chiếc r&egrave;m cửa kh&ocirc;ng chỉ đơn giản l&agrave; vật chắn s&aacute;ng hay tạo khoảng kh&ocirc;ng ri&ecirc;ng tư, n&oacute; c&ograve;n tạo cho mỗi căn ph&ograve;ng một ấn tượng v&agrave; cảm x&uacute;c kh&aacute;c nhau, t&ugrave;y theo c&aacute;ch chọn lựa của chủ nh&acirc;n.</p>\r\n', '<p>Chiếc r&egrave;m cửa kh&ocirc;ng chỉ đơn giản l&agrave; vật chắn s&aacute;ng hay tạo khoảng kh&ocirc;ng ri&ecirc;ng tư, n&oacute; c&ograve;n tạo cho mỗi căn ph&ograve;ng một ấn tượng v&agrave; cảm x&uacute;c kh&aacute;c nhau, t&ugrave;y theo c&aacute;ch chọn lựa của chủ nh&acirc;n.</p>\r\n', 140, 0, 'vai_thai.jpg', '2018-07-19 14:07:10', NULL, '', '', '', '', '', 1, 'Máy phay đầu đố nhôm', 'Chiếc rèm cửa không chỉ đơn giản là vật chắn sáng hay tạo khoảng không riêng tư, nó còn tạo cho mỗi căn phòng một ấn tượng và cảm xúc khác nhau, tùy theo cách chọn lựa của chủ nhân.', 'may-phay-dau-do-nhom', 'Vải Thailand', NULL),
(116, 'Máy ghép góc nhôm', '<p>Sunbrella l&agrave; một trong những sản phẩm về vải đứng đầu của thương hiệu sản xuất vải nổi tiếng Sunbrella &ndash; Mỹ. Vải Sunberlla được sản xuất tr&ecirc;n d&acirc;y truyền ti&ecirc;n tiến nhất,dưới đ&ocirc;i b&agrave;n tay của c&aacute;c kỹ sư l&acirc;u năm kinh nghiệm trong ng&agrave;nh sản xuất vải của Mỹ.Vải Sunbrella được l&agrave;m từ 100% chất liệu acrylic, m&agrave;u sắc của vải được nhuộm ngay từ c&ocirc;ng đoạn đầu ti&ecirc;n trong quy tr&igrave;nh sản xuất ngay cả trước khi c&aacute;c sợi vải được h&igrave;nh th&agrave;nh l&ecirc;n.Ch&iacute;nh v&igrave; vậy m&agrave;u sắc của vải Sunbrella chịu được &aacute;nh s&aacute;ng mặt trời v&agrave; c&aacute;c điều kiện khắc nghiệt&nbsp;của thời tiết.&nbsp;</p>\r\n', '<p>Sunbrella l&agrave; một trong những sản phẩm về vải đứng đầu của thương hiệu sản xuất vải nổi tiếng Sunbrella &ndash; Mỹ. Vải Sunberlla được sản xuất tr&ecirc;n d&acirc;y truyền ti&ecirc;n tiến nhất,dưới đ&ocirc;i b&agrave;n tay của c&aacute;c kỹ sư l&acirc;u năm kinh nghiệm trong ng&agrave;nh sản xuất vải của Mỹ.Vải Sunbrella được l&agrave;m từ 100% chất liệu acrylic, m&agrave;u sắc của vải được nhuộm ngay từ c&ocirc;ng đoạn đầu ti&ecirc;n trong quy tr&igrave;nh sản xuất ngay cả trước khi c&aacute;c sợi vải được h&igrave;nh th&agrave;nh l&ecirc;n.Ch&iacute;nh v&igrave; vậy m&agrave;u sắc của vải Sunbrella chịu được &aacute;nh s&aacute;ng mặt trời v&agrave; c&aacute;c điều kiện khắc nghiệt&nbsp;của thời tiết.&nbsp;</p>\r\n', 140, 0, 'vai_my.jpg', '2018-07-19 14:07:30', NULL, '', '', '', '', '', 1, 'Máy ghép góc nhôm', 'Sunbrella là một trong những sản phẩm về vải đứng đầu của thương hiệu sản xuất vải nổi tiếng Sunbrella – Mỹ. Vải Sunberlla được sản xuất trên dây truyền tiên tiến nhất,dưới đôi bàn tay của các kỹ sư lâu năm kinh nghiệm trong ngành sản xuất vải của Mỹ.Vải Sunbrella được làm từ 100% chất liệu acrylic, màu sắc của vải được nhuộm ngay từ công đoạn đầu tiên trong quy trình sản xuất ngay cả trước khi các sợi vải được hình thành lên.Chính vì vậy màu sắc của vải Sunbrella chịu được ánh sáng mặt trời và các điều kiện khắc nghiệt của thời tiết. ', 'may-ghep-goc-nhom', 'Vải Sunbrella', NULL),
(119, 'Máy cắt nhôm', '<p>Chiếc r&egrave;m cửa kh&ocirc;ng chỉ đơn giản l&agrave; vật chắn s&aacute;ng hay tạo khoảng kh&ocirc;ng ri&ecirc;ng tư, n&oacute; c&ograve;n tạo cho mỗi căn ph&ograve;ng một ấn tượng v&agrave; cảm x&uacute;c kh&aacute;c nhau, t&ugrave;y theo c&aacute;ch chọn lựa của chủ nh&acirc;n.</p>\r\n', '<p>Chiếc r&egrave;m cửa kh&ocirc;ng chỉ đơn giản l&agrave; vật chắn s&aacute;ng hay tạo khoảng kh&ocirc;ng ri&ecirc;ng tư, n&oacute; c&ograve;n tạo cho mỗi căn ph&ograve;ng một ấn tượng v&agrave; cảm x&uacute;c kh&aacute;c nhau, t&ugrave;y theo c&aacute;ch chọn lựa của chủ nh&acirc;n.</p>\r\n', 140, 0, '39313_513317235364775_737552300_n.jpg', '2018-07-19 14:07:06', NULL, '', '', '', '', '', 1, 'Máy cắt nhôm', 'Chiếc rèm cửa không chỉ đơn giản là vật chắn sáng hay tạo khoảng không riêng tư, nó còn tạo cho mỗi căn phòng một ấn tượng và cảm xúc khác nhau, tùy theo cách chọn lựa của chủ nhân.', 'may-cat-nhom', 'Vải Bỉ', NULL),
(124, 'Vật tư sản xuất', '<p>&nbsp;So s&aacute;nh tuổi thọ của sơn th&igrave;&nbsp;giấy d&aacute;n tường&nbsp;c&oacute; độ bền kh&ocirc;ng bằng nhưng&nbsp;giấy d&aacute;n tường h&agrave;n quốc&nbsp;lại gi&uacute;p bạn dễ d&agrave;ng tạo được một kh&ocirc;ng gian nhiều m&agrave;u sắc, h&igrave;nh khối như mong muốn, thi c&ocirc;ng nhanh ch&oacute;ng.</p>\r\n', '<p>&nbsp;So s&aacute;nh tuổi thọ của sơn th&igrave;&nbsp;giấy d&aacute;n tường&nbsp;c&oacute; độ bền kh&ocirc;ng bằng nhưng&nbsp;giấy d&aacute;n tường h&agrave;n quốc&nbsp;lại gi&uacute;p bạn dễ d&agrave;ng tạo được một kh&ocirc;ng gian nhiều m&agrave;u sắc, h&igrave;nh khối như mong muốn, thi c&ocirc;ng nhanh ch&oacute;ng.</p>\r\n', 0, 2, 'images_3_0.jpg', '2018-07-19 14:07:46', NULL, '', '', '', '', '', 1, 'Vật tư sản xuất', ' So sánh tuổi thọ của sơn thì giấy dán tường có độ bền không bằng nhưng giấy dán tường hàn quốc lại giúp bạn dễ dàng tạo được một không gian nhiều màu sắc, hình khối như mong muốn, thi công nhanh chóng.', 'vat-tu-san-xuat', 'Giấy Dán Tường', NULL),
